mapper.select:
SELECT  /* TrgtCustMap.onlineId */
        PRMV_ID FROM TB_TRGT_CUST_ALCT A LEFT JOIN TB_TRGT_CUST_DTPT B
    ON A.SUB_TRGT_LIST_ID = B.SUB_TRGT_LIST_ID
   AND A.TRGT_LIST_ID = B.TRGT_LIST_ID
   AND A.TRGT_CUST_DTPT_ID =B.TRGT_CUST_DTPT_ID
 WHERE A.CMPG_ID = #{cmpg_id}

   AND A.CNSR_ID = #{cnsr_id}


   AND A.SUB_TRGT_LIST_ID = #{sub_trgt_list_id}


   AND A.TRGT_CUST_DTPT_ID = #{trgt_cust_dtpt_id}

   AND A.DPMS_STAT_CD != '40' AND A.DPMS_STAT_CD != '30'

SELECT /* TrgtCustMap.getTrgtCustList */ B.* , CASE WHEN EXISTS ( SELECT * FROM TB_TRGT_CUST_ALCT WHERE TRGT_LIST_ID = B.TRGT_LIST_ID AND MEM_NO = B.MEM_NO AND CNTR_CD = B.CNTR_CD) THEN 'Y' ELSE 'N' END AS ALCT_YN FROM (SELECT
ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) PAGE_INDX
ROW_NUMBER() OVER(ORDER BY AMNT_DTTM DESC) PAGE_INDX
, A.* , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.RGSR_ID AND CNTR_CD = A.CNTR_CD) AS RGSR_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.AMND_ID AND CNTR_CD = A.CNTR_CD) AS AMND_NM FROM (SELECT CNTR_CD, TRGT_LIST_ID, SUB_TRGT_LIST_ID , NVL((SELECT 'N' FROM TB_TRGT_CUST_ALCT TA WHERE TA.TRGT_CUST_DTPT_ID = TD.TRGT_CUST_DTPT_ID) , 'Y') AS DELETE_FG , TRGT_CUST_DTPT_ID, CUST_NM, MEM_NO, PRMV_ID , ACCNT_NO, CLPH_TLNO, HOME_TLNO, COMP_TLNO, ETC_TLNO, UNTY_CASE_ADTL_YN , USER_DEFN1_CNTN, USER_DEFN2_CNTN, USER_DEFN3_CNTN, USER_DEFN4_CNTN , USER_DEFN5_CNTN, USER_DEFN6_CNTN, USER_DEFN7_CNTN, USER_DEFN8_CNTN , USER_DEFN9_CNTN, USER_DEFN10_CNTN , USER_DEFN11_CNTN, USER_DEFN12_CNTN, USER_DEFN13_CNTN, USER_DEFN14_CNTN , USER_DEFN15_CNTN, USER_DEFN16_CNTN, USER_DEFN17_CNTN, USER_DEFN18_CNTN , USER_DEFN19_CNTN, USER_DEFN20_CNTN , RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM FROM TB_TRGT_CUST_DTPT TD ) A
) B
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}
ORDER BY ${orderBy} ${orderDirection}

mapper.delete:
DELETE /* TrgtCustMap.deleteTrgtCust */
  FROM TB_TRGT_CUST_DTPT
 WHERE TRGT_LIST_ID = #{trgt_list_id}
   AND CNTR_CD =  #{cntr_cd}
   AND SUB_TRGT_LIST_ID = #{sub_trgt_list_id}
   AND TRGT_CUST_DTPT_ID = #{trgt_cust_dtpt_id}

mapper.update:
UPDATE /* TrgtCustMap.updateTrgtCustByCustId */
       TB_TRGT_CUST_DTPT
   SET AMNT_DTTM = TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')
        , AMND_ID = #{amnd_id}
        , MEM_NO = #{mem_no}
 WHERE TRGT_LIST_ID = #{trgt_list_id}
   AND TRGT_CUST_ID = #{trgt_cust_id}

where.if:
AND A.CMPG_PRFM_TEAM_CD = #{cmpg_prfm_team_cd}
AND B.CMPG_PRFM_TEAM_CD = #{cmpg_prfm_team_cd}
AND C.CMPG_PRFM_TEAM_CD = #{cmpg_prfm_team_cd}

mapper.select:
SELECT /* TrgtCustMap.getTrgtCustHstrList */ B.* FROM (SELECT
ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) AS PAGE_INDX
ROW_NUMBER() OVER(ORDER BY CUST_NM DESC) AS PAGE_INDX
, A.* , FN_GETHRCHCDNM('CAM990', A.CMPG_DIV_CD, A.CNTR_CD) AS CMPG_DIV_NM , GETCODENM('CAM050', A.CMPG_PRGR_STAT_CD, A.CNTR_CD) AS CMPG_PRGR_STAT_NM , GETCODENM('CAM090', A.DPMS_RESL_CD, A.CNTR_CD) AS DPMS_RESL_NM , (SELECT B.TEAM_NM FROM TB_CONS_TEAM B WHERE B.TEAM_CD = A.CMPG_PRFM_TEAM_CD) AS CMPG_PRFM_TEAM_NM FROM (SELECT A.CMPG_ID, A.CMPG_DIV_CD, A.CMPG_NM, A.CMPG_STT_DT, A.CMPG_END_DT , A.CMPG_PRGR_STAT_CD, A.TRGT_LIST_ID, A.CNTR_CD , B.TRGT_LIST_NM, C.SUB_TRGT_LIST_ID, C.SUB_TRGT_LIST_NM, C.CMPG_PRFM_TEAM_CD , D.TRGT_CUST_DTPT_ID, D.CUST_NM, D.MEM_NO, D.ACCNT_NO , D.CLPH_TLNO, D.HOME_TLNO, D.COMP_TLNO, D.ETC_TLNO , E.CNSR_ID, E.DPMS_RESL_CD, E.DPMS_RESL_CNTN, TO_CHAR(E.DPMS_DTTM, 'YYYYMMDDHH24MISS') AS DPMS_DTTM, E.PROD_JOIN_YN , (SELECT F.USER_NM FROM TB_USER F WHERE F.USER_ID = E.CNSR_ID) AS CNSR_NM FROM TB_CMPG_BASI A JOIN TB_TRGT_LIST_DTL B ON A.TRGT_LIST_ID = B.TRGT_LIST_ID JOIN TB_SUB_TRGT_LIST_DTL C ON B.TRGT_LIST_ID = C.TRGT_LIST_ID JOIN TB_TRGT_CUST_DTPT D ON C.TRGT_LIST_ID = D.TRGT_LIST_ID AND C.SUB_TRGT_LIST_ID = D.SUB_TRGT_LIST_ID LEFT OUTER JOIN TB_TRGT_CUST_ALCT E ON D.TRGT_LIST_ID = E.TRGT_LIST_ID AND D.SUB_TRGT_LIST_ID = E.SUB_TRGT_LIST_ID AND D.TRGT_CUST_DTPT_ID = E.TRGT_CUST_DTPT_ID
) A ) B
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}
ORDER BY ${orderBy} ${orderDirection}
