mapper.select:
SELECT /* TrgtListMap.getTrgtListList */ C.* , (SELECT USER_NM FROM TB_USER WHERE USER_ID = C.RGSR_ID AND CNTR_CD = C.CNTR_CD) AS RGSR_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = C.AMND_ID AND CNTR_CD = C.CNTR_CD) AS AMND_NM , (SELECT TEAM_NM FROM TB_CONS_TEAM WHERE TEAM_CD = C.CMPG_PRFM_TEAM_CD) AS CMPG_PRFM_TEAM_NM , (SELECT TEAM_NM FROM TB_CONS_TEAM WHERE TEAM_CD = C.HGRN_TEAM_CD) AS HGRN_TEAM_NM , GETCODENM('CAM060', C.CMPG_INVT_MTHD_CD, C.CNTR_CD) AS CMPG_INVT_MTHD_NM , GETCODENM('CAM120', C.CMPG_PRAN_CHNL_CD, C.CNTR_CD) AS CMPG_PRAN_CHNL_NM , FN_GETHRCHCDNM('CAM990', C.CMPG_TYP_CD, C.CNTR_CD) AS CMPG_TYP_NM , FN_GETHRCHCDNM('CAM990', C.HGRN_HRCH_CD, C.CNTR_CD) AS HGRN_HRCH_NM FROM (SELECT
ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) PAGE_INDX
ROW_NUMBER() OVER(ORDER BY AMNT_DTTM DESC) PAGE_INDX
, TRGT_LIST_ID, TRGT_LIST_NM, TRGT_LIST_EXPI, CMPG_INVT_MTHD_CD ,CMPG_PRAN_CHNL_CD , CMPG_TYP_CD, ALCT_YN, CUST_DATA_EXTC_CNT, RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM , CNTR_CD, HGRN_HRCH_CD, HRCH_ADTL_CD ,HRCH_ADTL_CD_NM , CMPG_PRFM_TEAM_CD , (SELECT HGRN_TEAM_CD FROM TB_CONS_TEAM WHERE TEAM_CD = B.CMPG_PRFM_TEAM_CD) AS HGRN_TEAM_CD FROM (SELECT A.TRGT_LIST_ID, A.TRGT_LIST_NM, A.TRGT_LIST_EXPI , A.CMPG_INVT_MTHD_CD, A.CMPG_PRAN_CHNL_CD , A.CMPG_TYP_CD, A.ALCT_YN, A.CUST_DATA_EXTC_CNT , A.RGSR_ID, A.RGSN_DTTM, A.AMND_ID, A.AMNT_DTTM , A.CNTR_CD, HC.HGRN_HRCH_CD, HC.HRCH_ADTL_CD, HC.HRCH_ADTL_CD_NM , A.CMPG_PRFM_TEAM_CD FROM TB_TRGT_LIST_DTL A LEFT JOIN TB_HRCH_CD HC ON A.CMPG_TYP_CD = HC.HRCH_CD ) B
) C
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}
ORDER BY ${orderBy} ${orderDirection}

mapper.update:
UPDATE /* TrgtListMap.updateTrgtList */
       TB_TRGT_LIST_DTL
   SET


       ALCT_YN         = NVL2((SELECT MIN(CMPG_ID) KEEP(DENSE_RANK FIRST ORDER BY CMPG_NM)
                                  FROM TB_CMPG_BASI B
                                 WHERE TRGT_LIST_ID=#{trgt_list_id}), 'Y','N')


       CUST_DATA_EXTC_CNT       = (SELECT COUNT(*) FROM TB_TRGT_CUST_DTPT B WHERE B.TRGT_LIST_ID = #{trgt_list_id} AND B.CNTR_CD = #{cntr_cd})


       TRGT_LIST_NM     = #{trgt_list_nm}
     , TRGT_LIST_EXPI   = #{trgt_list_expi}
     , CNTR_CD         = #{cntr_cd}
     , AMND_ID        = #{amnd_id}
     , AMNT_DTTM            = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')


 WHERE TRGT_LIST_ID		= #{trgt_list_id}
   AND CNTR_CD			= #{cntr_cd}

mapper.delete:
DELETE /* TrgtListMap.deleteTrgtList */
  FROM TB_TRGT_LIST_DTL
 WHERE CNTR_CD = #{cntr_cd}
   AND TRGT_LIST_ID = #{trgt_list_id}
