mapper.select:
SELECT /* RecrncResrDtlMap.getRecrncResrDtlList */ C.* , GETCODENM('CNS030', RECRNC_STAT_CD, CNTR_CD) AS RECRNC_STAT_NM , (TO_DATE(RECRNC_DTTM, 'YYYYMMDDHH24MISS') - SYSDATE) * 24 * 60 AS LEFT_TM , (CASE WHEN TO_DATE(RECRNC_DTTM, 'YYYYMMDDHH24MISS') > SYSDATE THEN TRUNC(TO_DATE(RECRNC_DTTM, 'YYYYMMDDHH24MISS') - SYSDATE) || '일 ' || LPAD(TRUNC(MOD((TO_DATE(RECRNC_DTTM, 'YYYYMMDDHH24MISS') - SYSDATE), 1) * 24), 2, 0) || '시간 ' || LPAD(TRUNC(MOD((TO_DATE(RECRNC_DTTM, 'YYYYMMDDHH24MISS') - SYSDATE) * 24, 1) * 60), 2, 0) || '분 ' || LPAD(TRUNC(ROUND(MOD((TO_DATE(RECRNC_DTTM, 'YYYYMMDDHH24MISS') - SYSDATE) * 24 * 60, 1) * 60)), 2, 0) || '초' ELSE TRUNC(SYSDATE - TO_DATE(RECRNC_DTTM, 'YYYYMMDDHH24MISS')) || '일 ' || LPAD(TRUNC(MOD((SYSDATE - TO_DATE(RECRNC_DTTM, 'YYYYMMDDHH24MISS')), 1) * 24), 2, 0) || '시간 ' || LPAD(TRUNC(MOD((SYSDATE - TO_DATE(RECRNC_DTTM, 'YYYYMMDDHH24MISS')) * 24, 1) * 60), 2, 0) || '분 ' || LPAD(TRUNC(ROUND(MOD((SYSDATE - TO_DATE(RECRNC_DTTM, 'YYYYMMDDHH24MISS')) * 24 * 60, 1) * 60)), 2, 0) || '초' END) AS TERM , (SELECT D.TEAM_NM FROM TB_CONS_TEAM D WHERE D.TEAM_CD = C.TEAM1_CD AND D.CNTR_CD = C.CNTR_CD) AS TEAM1_NM , (SELECT D.TEAM_NM FROM TB_CONS_TEAM D WHERE D.TEAM_CD = C.TEAM2_CD AND D.CNTR_CD = C.CNTR_CD) AS TEAM2_NM FROM (SELECT ROWNUM AS PAGE_INDX, B.* , (SELECT C.HGRN_TEAM_CD FROM TB_CONS_TEAM C WHERE C.TEAM_CD = B.TEAM2_CD AND C.CNTR_CD = B.CNTR_CD) AS TEAM1_CD FROM (SELECT CONSNO, RECRNC_CNTC_TLNO, RECRNC_DT, RECRNC_HRMN, RECRNC_STAT_CD , CNSR_ID, SIDO_TMCNT, RECRNC_SIDO_DTTM, RECRNC_DTTM , CNTR_CD, RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM , (SELECT B.USER_NM FROM TB_USER B WHERE B.USER_ID = A.CNSR_ID AND B.CNTR_CD = A.CNTR_CD) AS CNSR_NM , (SELECT B.USER_NM FROM TB_USER B WHERE B.USER_ID = A.RGSR_ID AND B.CNTR_CD = A.CNTR_CD) AS RGSR_NM , (SELECT B.USER_NM FROM TB_USER B WHERE B.USER_ID = A.AMND_ID AND B.CNTR_CD = A.CNTR_CD) AS AMND_NM , (SELECT B.TEAM_CD FROM TB_USER B WHERE B.USER_ID = A.CNSR_ID AND B.CNTR_CD = A.CNTR_CD) AS TEAM2_CD FROM (SELECT CONSNO, RECRNC_CNTC_TLNO, RECRNC_DT, RECRNC_HRMN, RECRNC_STAT_CD , CNSR_ID, SIDO_TMCNT, RECRNC_SIDO_DTTM, RECRNC_DT || RECRNC_HRMN || '00' AS RECRNC_DTTM , CNTR_CD, RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM FROM TB_RECRNC_RESR_DTL A
) A
ORDER BY ${orderBy} ${orderDirection}
) B ) C
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

mapper.update:
UPDATE /* RecrncResrDtlMap.updateRecrncResrDtl */ TB_RECRNC_RESR_DTL SET AMND_ID = #{amnd_id} , AMNT_DTTM = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
, CNSR_ID = #{cnsr_id}
, RECRNC_STAT_CD = '30'
, SIDO_TMCNT = SIDO_TMCNT + 1 , RECRNC_SIDO_DTTM = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') , RECRNC_STAT_CD = '20'
, RECRNC_STAT_CD = '10' , RECRNC_CNTC_TLNO = REPLACE(#{recrnc_cntc_tlno}, '-', '') , RECRNC_DT = REPLACE(#{recrnc_dt}, '-', '') , RECRNC_HRMN = REPLACE(#{recrnc_hrmn}, ':', '') , CNSR_ID = #{amnd_id}
WHERE CONSNO = #{consno}

mapper.select:
SELECT /* RecrncResrDtlMap.getTodoRecrncResrDtlList */ C.* , DECODE(SUBSTR(ACPG_DTTM, 1, 8), TO_CHAR(SYSDATE, 'YYYYMMDD'), 'Y', 'N') AS TODAYFG , GETCODENM('CNS010', ACPG_CHNL_CD, CNTR_CD) AS ACPG_CHNL_NM , GETCODENM('CNS020', CONS_DSPSMDDL_DTPT_CD, CNTR_CD) AS CONS_DSPSMDDL_DTPT_NM , GETCODENM('CNS030', CONS_STAT_CD, CNTR_CD) AS CONS_STAT_NM FROM (SELECT ROWNUM AS PAGE_INDX, B.*
FROM (SELECT A.CONSNO, A.CONSNO_GROP_NO, A.CONSNO_SQNO, A.ARS_SRVC_ADDR, A.INCO_TLNO , A.CALL_ID, A.MEM_NO, A.ACPG_CHNL_CD, A.ACPG_DTTM, A.ACTP_ID , A.TOTL_CONS_HR, A.CLNT_NM, A.CONS_TYP1_CD, A.CONS_TYP2_CD, A.CONS_TYP3_CD , A.CONS_MEMO_CNTN, A.CONS_STAT_CD, A.CONS_DSPSMDDL_DTPT_CD, A.CONS_DSPS_DTTM, A.RSPS_DEPT_CD , A.CHPR_ID, A.ADMR_AMNT_YN, A.ADMR_ID, A.ADMR_AMNT_DTTM, A.CNTR_CD , A.RGSR_ID, A.RGSN_DTTM, A.AMND_ID, A.AMNT_DTTM , MEM.MEM_NM, A.SEX, A.BRTH_MON_DAY, MEM.CI_VAL, A.IDN_NO , MEM.HOME_TEL, MEM.CELL, MEM.WRPL_TEL, MEM.HOME_ZIP, MEM.HOME_ADDR , MEM.HOME_ADDR2, A.CREDIT_RATING, A.CRDT_MNG_NO, MEM.EMAIL , C.RECRNC_CNTC_TLNO, C.RECRNC_DT, C.RECRNC_HRMN, C.SIDO_TMCNT, C.RECRNC_DTTM , (SELECT D.CONS_TYP_NM FROM TB_CONS_TYP_CD D WHERE D.CONS_TYP_CD = A.CONS_TYP3_CD AND D.CNTR_CD = A.CNTR_CD) AS CONS_TYP3_NM FROM TB_CONS A LEFT OUTER JOIN LF_DMUSER.MEMBER MEM ON A.MEM_NO = MEM.MEM_NO , (SELECT CONSNO, RECRNC_CNTC_TLNO, RECRNC_DT, RECRNC_HRMN, RECRNC_STAT_CD , CNSR_ID, SIDO_TMCNT, RECRNC_DT || RECRNC_HRMN || '00' AS RECRNC_DTTM FROM TB_RECRNC_RESR_DTL WHERE RECRNC_STAT_CD IN ('10','20')) C WHERE A.CONSNO = C.CONSNO
AND C.CNSR_ID = #{user_id}
AND A.CONSNO = #{consno}
ORDER BY ${orderBy} ${orderDirection}
) B ) C
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}
