mapper.select:
SELECT /* TrgtExcpMap.getTrgtExcpList */ A.* FROM (SELECT
ROW_NUMBER() OVER (ORDER BY ${orderBy} ${orderDirection}) AS PAGE_INDX
ROW_NUMBER() OVER (ORDER BY AMNT_DTTM DESC) AS PAGE_INDX
, A.* FROM (SELECT A.* , GETCODENM('CUS180', A.EXCP_GBN_CD, A.CNTR_CD) AS EXCP_GBN_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.RGSR_ID AND CNTR_CD = A.CNTR_CD) AS RGSR_NM , (SELECT USER_NM FROM TB_USER C WHERE USER_ID = A.AMND_ID AND CNTR_CD = A.CNTR_CD) AS AMND_NM FROM (SELECT TRGT_EXCP_ID, EXCP_GBN_CD, EXCP_ID, EXCP_NM, EXCP_EXPI, USE_YN , CNTR_CD, RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM, USER_DEFN1_CNTN , DECODE(USER_DEFN2_CNTN,'01','01','02') AS USER_DEFN2_CNTN /* 01:대상제외, 02:제외 */ , USER_DEFN3_CNTN, USER_DEFN4_CNTN, USER_DEFN5_CNTN FROM TB_TRGT_EXCP A
) A ) A ) A
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

mapper.update:
UPDATE /* TrgtExcpMap.updateTrgtExcp */
       TB_TRGT_EXCP
   SET AMNT_DTTM	= TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
     , AMND_ID		= #{amnd_id}
     , EXCP_EXPI	= #{excp_expi}
     , EXCP_ID		= #{excp_id}
     , USE_YN		= #{use_yn}

     , USER_DEFN1_CNTN = #{user_defn1_cntn}


     , USER_DEFN2_CNTN = #{user_defn2_cntn}


     , USER_DEFN3_CNTN = #{user_defn3_cntn}



 WHERE TRGT_EXCP_ID	= #{trgt_excp_id}

UPDATE /* TrgtExcpMap.deleteTrgtExcp */
       TB_TRGT_EXCP
   SET AMNT_DTTM	= TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
     , AMND_ID		= #{amnd_id}
     , USE_YN		= #{use_yn}
 WHERE TRGT_EXCP_ID	= #{trgt_excp_id}
