AND (A.ACCNT_NO LIKE '%' || #{srch_vl} || '%' OR A.MEM_NO LIKE '%' || #{srch_vl} || '%' OR B.MEM_NM LIKE '%' || #{srch_vl} || '%' OR A.BIRTH LIKE '%' || #{srch_vl} || '%' OR A.BANK_ACCNT_NO LIKE '%' || #{srch_vl} || '%')
