SELECT /* DlwCmsMap.getDlwWdrwTranDtlCount */ COUNT(*) FROM WDRW_REQ_CALL_CENTER REQ INNER JOIN MEM_PROD_ACCNT ACCNT ON ACCNT.ACCNT_NO = REQ.ACCNT_NO AND ACCNT.DEL_FG = 'N' INNER JOIN PRODUCT PROD ON PROD.PROD_CD = ACCNT.PROD_CD AND PROD.DEL_FG = 'N' INNER JOIN MEMBER MEM ON MEM.MEM_NO = ACCNT.MEM_NO AND MEM.DEL_FG = 'N' LEFT OUTER JOIN CMS_MEM CMEM ON CMEM.ACCNT_NO = ACCNT.ACCNT_NO AND CMEM.DEL_FG = 'N' LEFT OUTER JOIN CARD_MEM CARDMEM ON CARDMEM.ACCNT_NO = ACCNT.ACCNT_NO AND CARDMEM.DEL_FG = 'N' WHERE 1=1
AND CMEM.ACCNT_NO IS NOT NULL
AND CARDMEM.ACCNT_NO IS NOT NULL
AND REQ.ACCNT_NO = #{accntno}
AND REQ.SEQ = (SELECT MAX (SEQ) FROM WDRW_REQ_CALL_CENTER WHERE ACCNT_NO = #{accntno} AND DEL_FG IS NULL)
AND REQ.SEQ = #{seq}
AND REQ.INV_DT BETWEEN #{stt_dt} AND #{end_dt}
AND REQ.REG_DM BETWEEN #{stt_dt} AND #{end_dt}
AND REQ.WDRW_REQ_YN = #{wdrwreqappyn}
AND REQ.DEL_FG = #{del_fg}
AND REQ.DEL_FG IS NULL
AND REQ.GUBUN = #{gubun}

SELECT /* DlwCmsMap.getDlwWdrwTranDtlList */ * FROM (SELECT ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) PAGE_INDX,
'' AS PAGE_INDX,
X.* FROM (SELECT REQ.SEQ , REQ.ACCNT_NO , REQ.PAYR_NO , MEM.MEM_NM , CASE WHEN REQ.GUBUN = 'CMS' THEN CMEM.ICHAE_DT ELSE CARDMEM.PAY_DT END AS ICHAE_DT , CMEM.ICHAE_LIMIT_GUNSU , PROD.MON_PAY_AMT , NVL(CASE WHEN NVL(PROD.PROD_CL, '00') = '03' THEN CASE WHEN FN_ACCNT_STAT(ACCNT.ACCNT_NO) = 'Y' THEN CASE WHEN ACCNT.KSTBIT = '09' THEN (SELECT SUM( END_NO - ST_NO + 1 ) FROM PRODUCT_DTL WHERE PROD_CD = PROD.PROD_CD AND ALLT_AMT>0) ELSE CASE WHEN ACCNT.KSTBIT = '09' THEN (PROD.EXPR_NO -ACCNT.NEW_CHAN_GUNSU) ELSE (PROD.EXPR_NO -ACCNT.NEW_CHAN_GUNSU) END END ELSE CASE WHEN ACCNT.KSTBIT = '09' THEN (SELECT SUM( END_NO - ST_NO + 1 ) FROM PRODUCT_DTL WHERE PROD_CD = PROD.PROD_CD AND ADD_AMT>0) ELSE CASE WHEN ACCNT.KSTBIT = '09' THEN (SELECT SUM( END_NO - ST_NO + 1 ) FROM PRODUCT_DTL WHERE PROD_CD = PROD.PROD_CD AND ALLT_AMT>0) ELSE (SELECT SUM( END_NO - ST_NO + 1 ) FROM PRODUCT_DTL WHERE PROD_CD = PROD.PROD_CD AND ALLT_AMT>0) END END END ELSE (PROD.EXPR_NO -ACCNT.NEW_CHAN_GUNSU) END, 0) AS EXPR_NO , REQ.INV_GUNSU , REQ.WDRW_REQ_AMT , REQ.BANK_CD , CASE WHEN REQ.GUBUN = 'CMS' THEN FN_COM_NM ('63', REQ.BANK_CD) ELSE FN_COM_NM ('0120', REQ.BANK_CD) END AS BANK_NM , REQ.BANK_ACCNT_NO AS BANK_ACCNT_NO , REQ.BIRTH IDN_NO , CASE WHEN NVL(PROD.PROD_CL, '00') = '03' THEN CASE WHEN FN_ACCNT_STAT(ACCNT.ACCNT_NO) = 'Y' THEN CASE WHEN ACCNT.KSTBIT = '09' THEN FN_YEN_CHE_COMB(ACCNT.ACCNT_NO) ELSE CASE WHEN ACCNT.KSTBIT = '09' THEN FN_YEN_CHE(ACCNT.ACCNT_NO) ELSE FN_YEN_CHE(ACCNT.ACCNT_NO) END END ELSE CASE WHEN ACCNT.KSTBIT = '09' THEN FN_YEN_CHE_COMB_ADD(ACCNT.ACCNT_NO) ELSE CASE WHEN ACCNT.KSTBIT = '09' THEN FN_YEN_CHE_COMB(ACCNT.ACCNT_NO) ELSE FN_YEN_CHE_COMB(ACCNT.ACCNT_NO) END END END ELSE FN_YEN_CHE(ACCNT.ACCNT_NO) END AS DEL_STAT , REQ.BANK_FILIN_BRKDN , REQ.INV_NO , ACCNT.OLD_MID , CMEM.SYNC_NO , FN_COM_NM ('67', REQ.CL) AS CL , REQ.NOTE , REQ.WDRW_REQ_YN , FN_EMPLE_NM(REQ.REG_MAN) AS REG_MAN , TO_CHAR(REQ.REG_DM, 'YYYY-MM-DD HH24:MI:SS') AS REG_DM , REQ.INV_DT AS INV_DT , CASE WHEN REQ.GUBUN = 'CARD' THEN NVL((SELECT /*+INDEX(CARD_WDRW_REQ INDEX_CARD_WDRW_REQ_IDNTT) */ MAX(INV_DT) FROM CARD_WDRW_REQ WHERE IDNTT = REQ.IDNTT), '') ELSE NVL((SELECT /*+INDEX(WDRW_REQ INDEX_WDRW_REQ_IDNTT) */ MAX(INV_DT) FROM WDRW_REQ WHERE IDNTT = REQ.IDNTT), '') END AS WDRW_REQ_DT , CASE WHEN REQ.GUBUN = 'CARD' THEN NVL((SELECT /*+INDEX(CARD_WDRW_REQ INDEX_CARD_WDRW_REQ_IDNTT) */ MAX(FN_COM_NM('16', STAT)) FROM CARD_WDRW_REQ WHERE IDNTT = REQ.IDNTT), '') ELSE NVL((SELECT /*+INDEX(WDRW_REQ INDEX_WDRW_REQ_IDNTT) */ MAX(FN_COM_NM('16', STAT)) FROM WDRW_REQ WHERE IDNTT = REQ.IDNTT), '') END AS WDRW_REQ_STAT , REQ.GUBUN , CASE WHEN REQ.GUBUN = 'CMS' THEN CASE WHEN NVL((SELECT /*+INDEX(WDRW_REQ IDX_WDRW_REQ_ACCNT_STAT) */ COUNT(SEQ) FROM WDRW_REQ WHERE ACCNT_NO = REQ.ACCNT_NO AND INV_NO = REQ.INV_NO AND STAT = '02'), 0)>0 THEN '신청 중' WHEN NVL((SELECT /*+INDEX(WDRW_REQ IDX_WDRW_REQ_ACCNT_STAT) */ COUNT(SEQ) FROM WDRW_REQ WHERE ACCNT_NO = REQ.ACCNT_NO AND INV_NO = REQ.INV_NO AND STAT = '04'), 0)>0 THEN '완료' ELSE '' END WHEN REQ.GUBUN = 'CARD' THEN CASE WHEN NVL((SELECT COUNT(SEQ) FROM CARD_WDRW_REQ WHERE ACCNT_NO = REQ.ACCNT_NO AND INV_NO = REQ.INV_NO AND STAT = '02'), 0)>0 THEN '신청 중' WHEN NVL((SELECT COUNT(SEQ) FROM CARD_WDRW_REQ WHERE ACCNT_NO = REQ.ACCNT_NO AND INV_NO = REQ.INV_NO AND STAT = '04'), 0)>0 THEN '완료' ELSE '' END ELSE '' END AS REAL_WDRW_REQ_STAT , CASE WHEN REQ.GUBUN = 'CMS' THEN CASE WHEN NVL(#{mode}, '') != 'popup' THEN 'Y'
WHEN NVL((SELECT /*+INDEX(WDRW_REQ IDX_WDRW_REQ_ACCNT_STAT) */ COUNT(SEQ) FROM WDRW_REQ WHERE ACCNT_NO = REQ.ACCNT_NO AND INV_NO = REQ.INV_NO AND STAT = '02' AND NVL(IDNTT, 0) = 0), 0)>0 THEN 'N' WHEN NVL((SELECT /*+INDEX(WDRW_REQ IDX_WDRW_REQ_ACCNT_STAT) */ COUNT(SEQ) FROM WDRW_REQ WHERE ACCNT_NO = REQ.ACCNT_NO AND INV_NO = REQ.INV_NO AND STAT = '04' AND NVL(IDNTT, 0) = 0), 0)>0 THEN 'N' ELSE 'Y' END WHEN REQ.GUBUN = 'CARD' THEN CASE WHEN NVL(#{mode}, '') != 'popup' THEN 'Y'
WHEN NVL((SELECT COUNT(SEQ) FROM CARD_WDRW_REQ WHERE ACCNT_NO = REQ.ACCNT_NO AND INV_NO = REQ.INV_NO AND STAT = '02' AND NVL(IDNTT, 0) = 0), 0)>0 THEN 'N' WHEN NVL((SELECT COUNT(SEQ) FROM CARD_WDRW_REQ WHERE ACCNT_NO = REQ.ACCNT_NO AND INV_NO = REQ.INV_NO AND STAT = '04' AND NVL(IDNTT, 0) = 0), 0)>0 THEN 'N' ELSE 'Y' END ELSE 'Y' END AS APP_YN FROM WDRW_REQ_CALL_CENTER REQ INNER JOIN MEM_PROD_ACCNT ACCNT ON ACCNT.ACCNT_NO = REQ.ACCNT_NO AND ACCNT.DEL_FG = 'N' INNER JOIN PRODUCT PROD ON PROD.PROD_CD = ACCNT.PROD_CD AND PROD.DEL_FG = 'N' INNER JOIN MEMBER MEM ON MEM.MEM_NO = ACCNT.MEM_NO AND MEM.DEL_FG = 'N' LEFT OUTER JOIN CMS_MEM CMEM ON CMEM.ACCNT_NO = ACCNT.ACCNT_NO AND CMEM.DEL_FG = 'N' LEFT OUTER JOIN CARD_MEM CARDMEM ON CARDMEM.ACCNT_NO = ACCNT.ACCNT_NO AND CARDMEM.DEL_FG = 'N' WHERE 1=1
AND CMEM.ACCNT_NO IS NOT NULL
AND CARDMEM.ACCNT_NO IS NOT NULL
AND REQ.ACCNT_NO = #{accnt_no}
AND REQ.SEQ = (SELECT MAX (SEQ) FROM WDRW_REQ_CALL_CENTER WHERE ACCNT_NO = #{accnt_no} AND DEL_FG IS NULL)
AND REQ.SEQ = #{seq}
AND REQ.INV_DT BETWEEN #{stt_dt} AND #{end_dt}
AND TO_CHAR(REQ.REG_DM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt}
AND REQ.WDRW_REQ_YN = #{wdrwreqappyn}
AND REQ.DEL_FG = #{del_fg}
AND REQ.DEL_FG IS NULL
AND REQ.GUBUN = #{gubun}
) X ) Z
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

