, REQ.BANK_FILIN_BRKDN , REQ.INV_NO , ACCNT.OLD_MID , CMEM.SYNC_NO , FN_COM_NM ('67', REQ.CL) CL FROM WDRW_REQ REQ INNER JOIN MEM_PROD_ACCNT ACCNT ON ACCNT.ACCNT_NO = REQ.ACCNT_NO AND ACCNT.DEL_FG = 'N' INNER JOIN PRODUCT PROD ON PROD.PROD_CD = ACCNT.PROD_CD AND PROD.DEL_FG = 'N' LEFT OUTER JOIN CMS_MEM CMEM ON CMEM.ACCNT_NO = ACCNT.ACCNT_NO AND CMEM.DEL_FG = 'N' INNER JOIN MEMBER MEM ON MEM.MEM_NO = ACCNT.MEM_NO AND MEM.DEL_FG = 'N' WHERE REQ.STAT = '01'
