SELECT /* DlwCmsMap.getWdrwHstrSttc */ dept_nm as dept_nm , count(accnt_no) cnt
, count(accnt_no) * NVL((select WDRW_IMPS_COM from cms_com), 0) wdrw_Charge
, '-' wdrw_Charge
, sum(wdrw_req_amt) wdrw_Amt , sum(newAmt)+sum(yujiAmt) as real_Amt , sum(newCnt) new_Cnt , sum(yujiCnt) yuji_Cnt , sum(newAmt) new_Amt , sum(yujiAmt) yuji_Amt
, (sum(newCnt)+sum(yujiCnt)) * NVL((select WDRW_COM from cms_com), 0) pay_Charge , (sum(newCnt)+sum(yujiCnt)) * NVL((select WDRW_COM from cms_com), 0) + count(accnt_no) * NVL((select WDRW_IMPS_COM from cms_com), 0) charge
, sum(pay_charge) pay_Charge , sum(pay_charge) charge
FROM (SELECT dep.lvl_dept_cd , dn.dept_nm , wr.stat , wr.accnt_no , TO_NUMBER(wr.wdrw_req_amt) wdrw_req_amt , case when stat='04' and inv_no =1 then 1 else 0 end newCnt , case when stat='04' and inv_no> 1 then 1 else 0 end yujiCnt , TO_NUMBER( case when stat='04' and inv_no = 1 then wr.wdrw_req_amt else 0 end) newAmt , TO_NUMBER( case when stat='04' and inv_no> 1 then wr.wdrw_req_amt else 0 end) yujiAmt
, TO_NUMBER(case when stat='04' then convert(int, convert(numeric, wdrw_req_amt)*(0.018)) else 0 end) pay_charge
FROM
WDRW_REQ wr
CARD_WDRW_REQ wr
JOIN MEM_PROD_ACCNT mpa on wr.ACCNT_NO = mpa.ACCNT_NO INNER JOIN PRODUCT P ON mpa.PROD_CD = P.PROD_CD JOIN EMPLOYEE e on e.emple_no=mpa.emple_no left JOIN (select dept_cd , lvl , case when lvl=1 then dept_cd when lvl=2 then dept_cd when lvl=3 then high_dept_cd when lvl=4 then (SELECT high_dept_Cd FROM department WHERE dept_cd=dp.high_dept_cd) when lvl=5 then (SELECT high_dept_cd FROM department WHERE dept_cd = (SELECT high_dept_Cd FROM department WHERE dept_cd=dp.high_dept_cd)) when lvl=6 then (SELECT high_dept_cd FROM department WHERE dept_cd =(SELECT high_dept_cd FROM department WHERE dept_cd = (SELECT high_dept_Cd FROM department WHERE dept_cd=dp.high_dept_cd))) else dept_cd end as lvl_dept_cd FROM department dp )dep on e.dept_cd=dep.dept_cd JOIN department dn on dn.dept_cd=dep.lvl_dept_cd WHERE 1=1 AND P.PROD_CL != '03' AND pay_app_dt between #{inv_stt_dt} and #{inv_end_dt}
AND pay_app_dt < #{inv_end_dt}
AND pay_app_dt between #{inv_stt_dt} and #{inv_end_dt}
AND pay_app_dt > #{inv_stt_dt}
)tmp group by lvl_dept_cd, dept_Nm ORDER BY lvl_dept_cd

