/* DlwCmsMap.getWdrwHstrStatsSumByInvDt*/ '건수' as gbn , count(accnt_no) wdrw , sum(pay) pay , sum(wdrwErr) wdrw_err , sum(newTot) new_tot , sum(new) new_amt , cast((CAST(sum(new) as float)/CAST(sum(newTot) as float))*100 as int) new_per , sum(yujiTot) yuji_tot , sum(yuji) yuji , cast((CAST(sum(yuji) as float)/CAST(sum(yujiTot) as float))*100 as int) yuji_per , count(accnt_no) charge FROM (SELECT wr.accnt_no , case when stat='04' then 1 else 0 end pay , case when stat='04' then 0 else 1 end wdrwErr , case when inv_no =1 then 1 else 0 end newTot , case when stat='04' and inv_no =1 then 1 else 0 end new , case when inv_no> 1 then 1 else 0 end yujiTot , case when stat='04' and inv_no> 1 then 1 else 0 end yuji FROM
WDRW_REQ wr
CARD_WDRW_REQ wr
JOIN MEM_PROD_ACCNT mpa on wr.ACCNT_NO = mpa.ACCNT_NO inner join employee emp on mpa.emple_no = emp.emple_no WHERE 1=1
${dataAthrQury}
