/* 납입회차 */ SELECT '납입회차' AS SECTION , NVL((SELECT COUNT(NO) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY , NVL((SELECT COUNT(NO) FROM PAY_MNG_DTL WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL , NVL((SELECT COUNT(NO) FROM PAY_MNG_DTL1 WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL1 , 0 TOTAL , 0 READY_CASH , 0 ALL_TOTAL FROM MEM_PROD_ACCNT MPA WHERE MPA.ACCNT_NO = #{accnt_no}
UNION ALL
/* 잔여회차 */
SELECT SECTION , (TOTAL_PAY_NO - TOTAL_PAY) AS REST_NO_PAY , (TOTAL_REL_NO - TOTAL_PAY_DTL) AS REST_NO_REL , (TOTAL_ADD_NO - TOTAL_PAY_DTL1) AS REST_NO_ADD , 0 TOTAL , 0 READY_CASH , 0 ALL_TOTAL
FROM ( SELECT '잔여회차' AS SECTION , NVL((SELECT COUNT(NO) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY , NVL((SELECT COUNT(NO) FROM PAY_MNG_DTL WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL , NVL((SELECT COUNT(NO) FROM PAY_MNG_DTL1 WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL1 , NVL((SELECT COUNT(NO) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND AMT > 0 ), 0) TOTAL_PAY_NO , NVL((SELECT COUNT(NO) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND REL_AMT > 0 ), 0) TOTAL_REL_NO , NVL((SELECT COUNT(NO) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND ADD_AMT > 0 ), 0) TOTAL_ADD_NO , 0 TOTAL , 0 READY_CASH , 0 ALL_TOTAL FROM MEM_PROD_ACCNT MPA WHERE MPA.ACCNT_NO = #{accnt_no} )
UNION ALL
/* 납입금액 */ SELECT SECTION , TOTAL_PAY , TOTAL_PAY_DTL , TOTAL_PAY_DTL1 , (TOTAL_PAY + TOTAL_PAY_DTL + TOTAL_PAY_DTL1) AS TOTAL , 0 AS READY_CASH , (TOTAL_PAY + TOTAL_PAY_DTL + TOTAL_PAY_DTL1) ALL_TOTAL FROM ( SELECT '납입금액' AS SECTION , NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY , NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG_DTL WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL , NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG_DTL1 WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL1 FROM MEM_PROD_ACCNT MPA WHERE MPA.ACCNT_NO = #{accnt_no} )
UNION ALL
/* 할인금액 */ SELECT SECTION , ((PAY_AMT_BY1+PAY_AMT_BY2) * NEW_CHAN_GUNSU) TOTAL_PAY , TOTAL_PAY_DTL , TOTAL_PAY_DTL1 , ((PAY_AMT_BY1+PAY_AMT_BY2) * NEW_CHAN_GUNSU) TOTAL , READY_CASH , ((PAY_AMT_BY1+PAY_AMT_BY2) * NEW_CHAN_GUNSU) ALL_TOTAL FROM ( SELECT '할인금액' AS SECTION , NVL((SELECT AMT FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND NO = 1), 0) PAY_AMT_BY1 , NVL((SELECT REL_AMT FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND NO = 1), 0) PAY_AMT_BY2 , MPA.NEW_CHAN_GUNSU , 0 TOTAL_PAY_DTL , 0 TOTAL_PAY_DTL1 , 0 TOTAL , 0 READY_CASH , 0 ALL_TOTAL FROM MEM_PROD_ACCNT MPA WHERE MPA.ACCNT_NO = #{accnt_no} )
UNION ALL
/* 잔여금액 */ SELECT SECTION , ((TOTAL_PAY_NO - TOTAL_PAY) - ((PAY_AMT_BY1+PAY_AMT_BY2) * NEW_CHAN_GUNSU)) AS REST_NO_PAY , (TOTAL_REL_NO - TOTAL_PAY_DTL) AS REST_NO_REL , (TOTAL_ADD_NO - TOTAL_PAY_DTL1) AS REST_NO_ADD , ((TOTAL_PAY_NO - TOTAL_PAY) + (TOTAL_REL_NO - TOTAL_PAY_DTL) + (TOTAL_ADD_NO - TOTAL_PAY_DTL1) - ((PAY_AMT_BY1+PAY_AMT_BY2) * NEW_CHAN_GUNSU)) TOTAL , NVL(READY_CASH,0) READY_CASH , ((TOTAL_PAY_NO - TOTAL_PAY) + (TOTAL_REL_NO - TOTAL_PAY_DTL) + (TOTAL_ADD_NO - TOTAL_PAY_DTL1) + NVL(READY_CASH,0) -((PAY_AMT_BY1+PAY_AMT_BY2) * NEW_CHAN_GUNSU)) ALL_TOTAL FROM ( SELECT '잔여금액' AS SECTION , MPA.NEW_CHAN_GUNSU , NVL((SELECT AMT FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND NO = 1), 0) PAY_AMT_BY1 , NVL((SELECT REL_AMT FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND NO = 1), 0) PAY_AMT_BY2 , NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY , NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG_DTL WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL , NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG_DTL1 WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL1 , NVL((SELECT SUM(AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND AMT > 0 ), 0) TOTAL_PAY_NO , NVL((SELECT SUM(REL_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND REL_AMT > 0 ), 0) TOTAL_REL_NO , NVL((SELECT SUM(ADD_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND ADD_AMT > 0 ), 0) TOTAL_ADD_NO ,#{ready_cash} AS READY_CASH
FROM MEM_PROD_ACCNT MPA WHERE MPA.ACCNT_NO = #{accnt_no} )
UNION ALL
/* 합계금액 */ SELECT SECTION , TOTAL_PAY_NO , TOTAL_REL_NO , TOTAL_ADD_NO , (TOTAL_PAY_NO + TOTAL_REL_NO + TOTAL_ADD_NO) TOTAL , NVL(READY_CASH,0) READY_CASH , ((TOTAL_PAY_NO + TOTAL_REL_NO + TOTAL_ADD_NO) + NVL(READY_CASH,0)) ALL_TOTAL FROM ( SELECT '합계금액' AS SECTION , NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY , NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG_DTL WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL , NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG_DTL1 WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL1 , NVL((SELECT SUM(AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND AMT > 0 ), 0) TOTAL_PAY_NO , NVL((SELECT SUM(REL_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND REL_AMT > 0 ), 0) TOTAL_REL_NO , NVL((SELECT SUM(ADD_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND ADD_AMT > 0 ), 0) TOTAL_ADD_NO ,#{ready_cash} AS READY_CASH
FROM MEM_PROD_ACCNT MPA WHERE MPA.ACCNT_NO = #{accnt_no} )

