/* DlwCmsMap.getPayTotalList */
SELECT '총납입회차' AS SECTION , NVL((SELECT COUNT(NO) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY , NVL((SELECT COUNT(NO) FROM PAY_MNG_DTL WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL , NVL((SELECT COUNT(NO) FROM PAY_MNG_DTL1 WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL1 , 0 TOTAL FROM MEM_PROD_ACCNT MPA INNER JOIN PRODUCT P ON MPA.PROD_CD = P.PROD_CD WHERE MPA.ACCNT_NO = #{accnt_no}
UNION ALL
SELECT '잔여회차' AS SECTION , NVL(P.EXPR_NO,0) - NVL(MPA.NEW_CHAN_GUNSU,0) - NVL((SELECT COUNT(NO) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY , NVL((SELECT SUM(END_NO - ST_NO + 1) FROM PRODUCT_DTL WHERE PROD_CD = P.PROD_CD AND ALLT_AMT > 0), 0) - NVL((SELECT COUNT(NO) FROM PAY_MNG_DTL WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL , NVL((SELECT SUM(END_NO - ST_NO + 1) FROM PRODUCT_DTL WHERE PROD_CD = P.PROD_CD AND ADD_AMT > 0), 0) - NVL((SELECT COUNT(NO) FROM PAY_MNG_DTL1 WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL , 0 TOTAL FROM MEM_PROD_ACCNT MPA INNER JOIN PRODUCT P ON MPA.PROD_CD = P.PROD_CD WHERE MPA.ACCNT_NO = #{accnt_no}
UNION ALL
SELECT '총납입금액' AS SECTION , NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY , NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG_DTL WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL , NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG_DTL1 WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL_PAY_DTL1 , NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) + NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG_DTL WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) + NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG_DTL1 WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) TOTAL FROM MEM_PROD_ACCNT MPA INNER JOIN PRODUCT P ON MPA.PROD_CD = P.PROD_CD WHERE MPA.ACCNT_NO = #{accnt_no}
UNION ALL
SELECT '잔여금액' AS SECTION , CASE WHEN P.PAY_TYPE = '001' THEN (P.EXPR_NO - MPA.NEW_CHAN_GUNSU - NVL((SELECT COUNT(NO) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0)) * P.MON_PAY_AMT ELSE NVL((SELECT SUM((END_NO - ST_NO + 1) * MONTH_PAY_AMT) FROM PRODUCT_DTL WHERE PROD_CD = P.PROD_CD), 0) - NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) END TOTAL_PAY , CASE WHEN P.PAY_TYPE = '001' THEN 0 ELSE (SELECT SUM((END_NO - ST_NO + 1) * (ALLT_AMT)) FROM PRODUCT_DTL WHERE PROD_CD = P.PROD_CD) - NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG_DTL WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) END TOTAL_PAY_DTL , CASE WHEN P.PAY_TYPE = '001' THEN 0 ELSE (SELECT SUM((END_NO - ST_NO + 1) * (ADD_AMT)) FROM PRODUCT_DTL WHERE PROD_CD = P.PROD_CD) - NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG_DTL1 WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) END TOTAL_PAY_DTL1 , CASE WHEN P.PAY_TYPE = '001' THEN (P.EXPR_NO - MPA.NEW_CHAN_GUNSU - NVL((SELECT COUNT(NO) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0))*P.MON_PAY_AMT ELSE NVL((SELECT SUM((END_NO - ST_NO + 1) * MONTH_PAY_AMT) FROM PRODUCT_DTL WHERE PROD_CD = P.PROD_CD), 0) - NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) END + CASE WHEN P.PAY_TYPE = '001' THEN 0 ELSE (SELECT SUM((END_NO - ST_NO + 1) * (ALLT_AMT + ADD_AMT)) FROM PRODUCT_DTL WHERE PROD_CD = P.PROD_CD) - NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG_DTL WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) - NVL((SELECT SUM(PAY_AMT) FROM PAY_MNG_DTL1 WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'), 0) END TOTAL FROM MEM_PROD_ACCNT MPA INNER JOIN PRODUCT P ON MPA.PROD_CD = P.PROD_CD WHERE MPA.ACCNT_NO = #{accnt_no}
UNION ALL
SELECT '합계' AS SECTION , CASE WHEN P.PAY_TYPE = '001' THEN (P.EXPR_NO - MPA.NEW_CHAN_GUNSU) * P.MON_PAY_AMT ELSE NVL((SELECT SUM((END_NO - ST_NO + 1) * MONTH_PAY_AMT) FROM PRODUCT_DTL WHERE PROD_CD = P.PROD_CD), 0) END TOTAL_PAY , CASE WHEN P.PAY_TYPE = '001' THEN 0 ELSE (SELECT SUM((END_NO - ST_NO + 1) * (ALLT_AMT)) FROM PRODUCT_DTL WHERE PROD_CD = P.PROD_CD) END TOTAL_PAY_DTL , CASE WHEN P.PAY_TYPE = '001' THEN 0 ELSE (SELECT SUM((END_NO - ST_NO + 1) * (ADD_AMT)) FROM PRODUCT_DTL WHERE PROD_CD = P.PROD_CD) END TOTAL_PAY_DTL1 , CASE WHEN P.PAY_TYPE = '001' THEN (P.EXPR_NO - MPA.NEW_CHAN_GUNSU) * P.MON_PAY_AMT ELSE NVL((SELECT SUM((END_NO - ST_NO + 1) * MONTH_PAY_AMT) FROM PRODUCT_DTL WHERE PROD_CD = P.PROD_CD), 0) END + CASE WHEN P.PAY_TYPE = '001' THEN 0 ELSE (SELECT SUM((END_NO - ST_NO + 1) * (ALLT_AMT + ADD_AMT)) FROM PRODUCT_DTL WHERE PROD_CD = P.PROD_CD) END TOTAL FROM MEM_PROD_ACCNT MPA INNER JOIN PRODUCT P ON MPA.PROD_CD = P.PROD_CD
WHERE MPA.ACCNT_NO = #{accnt_no}

mapper.insert:

INSERT /* DlwCmsMap.insertGasuDtl */
  INTO REFUND
     ( MST_SEQ
     , SEQ
     , REFUND_DAY
     , REFUND_AMT
     , BANK_CD
     , BANK_ACCNT_NO
     , DEPR
     , REG_DM
     , REG_MAN
     , UPD_DM
     , UPD_MAN
     , DEL_FG
     , NOTE
     )
SELECT #{gasu_seq}
     , A.SEQ
     , REPLACE(#{refund_day},'/','')
     , CAST(#{refund_amt} AS NUMERIC)
     , #{bank_cd}
     , #{bank_accnt_no}
     , #{depr}
     , SYSDATE
     , #{reg_man}
     , SYSDATE
     , #{upd_man}
     , 'N'
     , #{note}
  FROM (SELECT NVL(MAX(SEQ) + 1, '1') AS SEQ
          FROM REFUND WHERE MST_SEQ = #{gasu_seq}
       ) A

mapper.update:

UPDATE /* DlwCmsMap.updateGasuDtl */
       REFUND
   SET REFUND_DAY    = REPLACE(#{refund_day},'/','')
     , REFUND_AMT    = CAST(#{refund_amt} AS NUMERIC)
     , BANK_CD       = #{bank_cd}
     , BANK_ACCNT_NO = #{bank_accnt_no}
     , DEPR          = #{depr}
     , UPD_DM        = SYSDATE
     , UPD_MAN       = #{upd_man}
     , NOTE          = #{note}
 WHERE MST_SEQ       = #{gasu_seq}
   AND SEQ           = #{seq}


UPDATE /* DlwCmsMap.deleteGasuDtl */
       REFUND
   SET DEL_Fg = 'Y'
     , UPD_DM  = SYSDATE
     , UPD_MAN = #{upd_man}
WHERE MST_SEQ = #{gasu_seq}

   AND SEQ = #{seq}


UPDATE /* DlwCmsMap.updateGasuMng */
       GASU_AMT_REG
   SET GASU_AMT      = CAST(#{gasu_amt} AS INT)
     , GASU_ALLT_AMT = CAST(#{gasu_allt_amt} AS INT)
     , GASU_ADD_AMT  = CAST(#{gasu_add_amt} AS INT)
     , UPD_DM        = SYSDATE
     , UPD_MAN       = #{upd_man}
     , PAY_DAY       = #{pay_day}
     , NOTE          = #{note}
     , PAY_NO        = CAST(#{pay_no} AS INT)
WHERE seq = #{gasu_seq}


UPDATE /* DlwCmsMap.deleteGasuMng */
       GASU_AMT_REG
   SET DEL_FG  = 'Y'
     , UPD_DM  = SYSDATE
     , UPD_MAN = #{upd_man}
 WHERE SEQ = #{gasu_seq}

