SELECT /*DlwCmsMap.getDlwWdrwHstr_Basic*/ REQ.ACCNT_NO , REQ.INV_DT , MEM.MEM_NM , CMEM.ICHAE_DT , CMEM.ICHAE_LIMIT_GUNSU , PROD.MON_PAY_AMT , CASE WHEN NVL(prod.PROD_CL, '00') = '03' THEN CASE WHEN ACCNT.KSTBIT NOT IN ( '03','04') THEN CASE WHEN ACCNT.KSTBIT = '09' THEN (SELECT SUM( END_NO - ST_NO + 1 ) FROM PRODUCT_DTL WHERE PROD_CD = PROD.PROD_CD AND ALLT_AMT> 0) ELSE CASE WHEN ACCNT.KSTBIT = '09' THEN (PROD.EXPR_NO -ACCNT.NEW_CHAN_GUNSU) ELSE (PROD.EXPR_NO -ACCNT.NEW_CHAN_GUNSU) END END ELSE CASE WHEN ACCNT.KSTBIT = '09' THEN (SELECT SUM( END_NO - ST_NO + 1 ) FROM PRODUCT_DTL WHERE PROD_CD = PROD.PROD_CD AND ADD_AMT> 0) ELSE CASE WHEN ACCNT.KSTBIT = '09' THEN (SELECT SUM( END_NO - ST_NO + 1 ) FROM PRODUCT_DTL WHERE PROD_CD = PROD.PROD_CD AND ALLT_AMT> 0) ELSE (SELECT SUM( END_NO - ST_NO + 1 ) FROM PRODUCT_DTL WHERE PROD_CD = PROD.PROD_CD AND ALLT_AMT> 0) END END END ELSE (PROD.EXPR_NO -ACCNT.NEW_CHAN_GUNSU) END EXPR_NO , FN_COM_NM ('S020', REQ.CBIT) CBIT_NM , REQ.CBIT , REQ.PAY_AMT , REQ.PAY_DTL_AMT , REQ.PAY_DTL1_AMT , REQ.PAY_CNT , REQ.PAY_DTL_CNT , REQ.PAY_DTL1_CNT , REQ.BANK_CD , FN_COM_NM ('63', REQ.BANK_CD) BANK_NM ,REQ.BANK_ACCNT_NO AS BANK_ACCNT_NO , req.birth idn_no , REQ.INV_NO , ACCNT.OLD_MID , CMEM.SYNC_NO , FN_COM_NM ('67', REQ.CL) CL , ACCNT.PROD_CD , PROD.PROD_CL FROM WDRW_CMS_BASIC REQ INNER JOIN MEM_PROD_ACCNT ACCNT ON ACCNT.ACCNT_NO = REQ.ACCNT_NO AND ACCNT.DEL_FG = 'N' INNER JOIN PRODUCT PROD ON PROD.PROD_CD = ACCNT.PROD_CD AND PROD.DEL_FG = 'N' LEFT OUTER JOIN CMS_MEM CMEM ON CMEM.ACCNT_NO = ACCNT.ACCNT_NO AND CMEM.DEL_FG = 'N' INNER JOIN MEMBER MEM ON MEM.MEM_NO = ACCNT.MEM_NO AND MEM.DEL_FG = 'N' WHERE REQ.CBIT = '01' AND REQ.DEL_FG = 'N'
AND REQ.ACCNT_NO = #{accnt_no}
ORDER BY MEM.MEM_NM

