mapper.select:
SELECT MST_SERIAL_NUM /* 등록일련번호 */ ,STA_RECORD_GB /* RECORD구분(STARTLINE) */ ,STA_MAKE_DTTM /* 파일작성일(STARTLINE) */ ,STA_SEND_ID /* 전송대상ID(STARTLINE) */ ,STA_RECV_DTTM /* 매입결과수신일(STARTLINE) */ ,STA_FILLER /* FILLER(STARTLINE) */ ,TOT_RECORD_GB /* RECORD구분(TOTALLINE) */ ,TOT_RECV_DTTM /* 매입결과수신일(TOTALLINE) */ ,TOT_SEND_ID /* 전송대상ID(TOTALLINE) */ ,TO_NUMBER(TOT_SEND_NUM) AS TOT_SEND_NUM /* 전송건수(TOTALLINE) */ ,TO_NUMBER(TOT_APPY_NUM) AS TOT_APPY_NUM /* 승인건수(TOTALLINE) */ ,TO_NUMBER(TOT_APPY_PAY) AS TOT_APPY_PAY /* 승인금액(TOTALLINE) */ ,TO_NUMBER(TOT_CNCL_NUM) AS TOT_CNCL_NUM /* 취소건수(TOTALLINE) */ ,TO_NUMBER(TOT_CNCL_PAY) AS TOT_CNCL_PAY /* 취소금액(TOTALLINE) */ ,TOT_FILLER /* FILLER(TOTALLINE) */ ,FILE_NAME /* 파일명 */ ,FILE_PATH /* 파일경로 */ ,CALC_YN /* 산출여부 */ ,RGSR_ID /* 등록자ID */ ,RGSR_DTTM /* 등록일시 */ ,DEL_FG /* 삭제여부 */ FROM LF_DMUSER.TB_ACQU_RSLT_MST A WHERE 1=1 AND DEL_FG = 'N' AND STA_RECORD_GB = 'H'
AND FILE_NAME = #{file_name}
AND STA_MAKE_DTTM = #{sta_make_dttm}

SELECT  /*DlwCmsMap.getAcquResultDataListTotalCnt*/
	COUNT(*)
  FROM ( SELECT *
           FROM LF_DMUSER.TB_ACQU_RSLT_MST A
           INNER JOIN LF_DMUSER.TB_ACQU_RSLT_DTL B
                   ON A.MST_SERIAL_NUM = B.MST_SERIAL_NUM
                 AND A.DEL_FG = 'N'
                 AND B.DEL_FG = 'N'
          WHERE 1=1
          AND A.STA_RECORD_GB = 'H'
          AND B.DATA_GB = 'D'

            AND B.DATA_TRAD_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


            AND B.DATA_PURC_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


            AND A.STA_RECV_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


            AND A.STA_MAKE_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


            AND B.DATA_AMOT_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


            AND B.DATA_TRAD_GB = #{data_trad_gb} 


            AND TRIM(B.DATA_APPY_NUM) = #{data_appy_num}


            AND TRIM(B.DATA_PURC_CRCO) = #{data_purc_crco}

       ) TBL
  WHERE 1=1

SELECT /*DlwCmsMap.getAcquResultDataList*/ TBL.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY B.DATA_TID ASC) AS PAGE_INDX ,TRIM(A.MST_SERIAL_NUM) AS M_MST_SERIAL_NUM /* 등록일련번호 */ ,TRIM(A.STA_RECORD_GB) AS M_STA_RECORD_GB /* RECORD구분(STARTLINE) */ ,TRIM(A.STA_MAKE_DTTM) AS M_STA_MAKE_DTTM /* 파일작성일(STARTLINE) */ ,TRIM(A.STA_SEND_ID) AS M_STA_SEND_ID /* 전송대상ID(STARTLINE) */ ,TRIM(A.STA_RECV_DTTM) AS M_STA_RECV_DTTM /* 매입결과수신일(STARTLINE) */ ,TRIM(A.STA_FILLER) AS M_STA_FILLER /* FILLER(STARTLINE) */ ,TRIM(A.TOT_RECORD_GB) AS M_TOT_RECORD_GB /* RECORD구분(TOTALLINE) */ ,TRIM(A.TOT_RECV_DTTM) AS M_TOT_RECV_DTTM /* 매입결과수신일(TOTALLINE) */ ,TRIM(A.TOT_SEND_ID) AS M_TOT_SEND_ID /* 전송대상ID(TOTALLINE) */ ,TO_NUMBER(A.TOT_SEND_NUM) AS M_TOT_SEND_NUM /* 전송건수(TOTALLINE) */ ,TO_NUMBER(A.TOT_APPY_NUM) AS M_TOT_APPY_NUM /* 승인건수(TOTALLINE) */ ,TO_NUMBER(A.TOT_APPY_PAY) AS M_TOT_APPY_PAY /* 승인금액(TOTALLINE) */ ,TO_NUMBER(A.TOT_CNCL_NUM) AS M_TOT_CNCL_NUM /* 취소건수(TOTALLINE) */ ,TO_NUMBER(A.TOT_CNCL_PAY) AS M_TOT_CNCL_PAY /* 취소금액(TOTALLINE) */ ,TRIM(A.TOT_FILLER) AS M_TOT_FILLER /* FILLER(TOTALLINE) */ ,TRIM(A.FILE_NAME) AS M_FILE_NAME /* 파일명 */ ,TRIM(A.FILE_PATH) AS M_FILE_PATH /* 파일경로 */ ,TRIM(A.CALC_YN) AS M_CALC_YN /* 산출여부 */ ,TRIM(A.RGSR_ID) AS M_RGSR_ID /* 등록자ID */ ,TRIM(A.RGSR_DTTM) AS M_RGSR_DTTM /* 등록일시 */ ,TRIM(A.DEL_FG) AS M_DEL_FG /* 삭제여부 */ ,TRIM(B.MST_SERIAL_NUM) AS D_MST_SERIAL_NUM /* 등록일련번호MST */ ,TRIM(B.DATA_GB) AS D_DATA_GB /* RECORD구분(DATALINE) */ ,TRIM(B.DATA_TID) AS D_DATA_TID /* TID(DATALINE) */ ,TRIM(B.DATA_FRAN_ID) AS D_DATA_FRAN_ID /* 가맹점ID(DATALINE) */ ,TRIM(B.DATA_TRAD_GB) AS D_DATA_TRAD_GB /* 거래구분(DATALINE) */ ,TRIM(B.DATA_TRAD_DTTM) AS D_DATA_TRAD_DTTM /* 거래일자(DATALINE) */ ,TRIM(B.DATA_PURC_DTTM) AS D_DATA_PURC_DTTM /* 매입일자(DATALINE) */ ,TRIM(B.DATA_APPY_NUM) AS D_DATA_APPY_NUM /* 승인번호(DATALINE) */ ,TRIM(B.DATA_PURC_CRCO) AS D_DATA_PURC_CRCO /* 매입카드사코드(DATALINE) */ ,TRIM(B.DATA_CARD_GB) AS D_DATA_CARD_GB /* 카드구분(DATALINE) */ ,TRIM(B.DATA_CARD_NUM) AS D_DATA_CARD_NUM /* 카드번호(DATALINE) */ ,TO_NUMBER(B.DATA_PURC_PAY) AS D_DATA_PURC_PAY /* 매입금액(DATALINE) */ ,TO_NUMBER(B.DATA_FRAN_FEE) AS D_DATA_FRAN_FEE /* 가맹점수수료(DATALINE) */ ,TO_NUMBER(B.DATA_ETC_FEE) AS D_DATA_ETC_FEE /* 기타수수료(DATALINE) */ ,TO_NUMBER(B.DATA_AMOT_PAID) AS D_DATA_AMOT_PAID /* 지급금액(DATALINE) */ ,TRIM(B.DATA_AMOT_DTTM) AS D_DATA_AMOT_DTTM /* 지급예정일(DATALINE) */ ,TRIM(B.DATA_TRAD_BLGB) AS D_DATA_TRAD_BLGB /* 거래구분(DATALINE) */ ,TRIM(B.DATA_FILLER) AS D_DATA_FILLER /* FILLER(DATALINE) */ ,TRIM(B.RGSR_ID) AS D_RGSR_ID /* 등록자ID */ ,TRIM(B.RGSR_DTTM) AS D_RGSR_DTTM /* 등록일시 */ ,TRIM(B.DEL_FG) AS D_DEL_FG /* 삭제여부 */ FROM LF_DMUSER.TB_ACQU_RSLT_MST A INNER JOIN LF_DMUSER.TB_ACQU_RSLT_DTL B ON A.MST_SERIAL_NUM = B.MST_SERIAL_NUM AND A.DEL_FG = 'N' AND B.DEL_FG = 'N' WHERE 1=1 AND A.STA_RECORD_GB = 'H' AND B.DATA_GB = 'D'
AND B.DATA_TRAD_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND B.DATA_PURC_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND A.STA_RECV_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND A.STA_MAKE_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND B.DATA_AMOT_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND B.DATA_TRAD_GB = #{data_trad_gb}
AND TRIM(B.DATA_APPY_NUM) = #{data_appy_num}
AND TRIM(B.DATA_PURC_CRCO) = #{data_purc_crco}
) TBL WHERE 1=1
AND PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT COUNT(*)                 AS M_TOT_SEND_NUM
      ,SUM(TBL.DATA_PURC_PAY)   AS D_DATA_PURC_PAY_SUM
      ,SUM(TBL.DATA_FRAN_FEE)   AS D_DATA_FRAN_FEE_SUM
      ,SUM(TBL.DATA_ETC_FEE)    AS D_DATA_ETC_FEE_SUM
      ,SUM(TBL.DATA_AMOT_PAID)  AS D_DATA_AMOT_PAID_SUM
  FROM ( SELECT *
           FROM LF_DMUSER.TB_ACQU_RSLT_MST A
           INNER JOIN LF_DMUSER.TB_ACQU_RSLT_DTL B
                   ON A.MST_SERIAL_NUM = B.MST_SERIAL_NUM
                 AND A.DEL_FG = 'N'
                 AND B.DEL_FG = 'N'
          WHERE 1=1
          AND A.STA_RECORD_GB = 'H'
          AND B.DATA_GB = 'D'

            AND B.DATA_TRAD_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


            AND B.DATA_PURC_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


            AND A.STA_RECV_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


            AND A.STA_MAKE_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


            AND B.DATA_AMOT_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


            AND B.DATA_TRAD_GB = #{data_trad_gb} 


            AND TRIM(B.DATA_APPY_NUM) = #{data_appy_num} 

       ) TBL
 WHERE 1=1

WITH CALC_RSLT AS ( SELECT DATA_TRAD_GB ,COUNT(*) AS M_TOT_SEND_NUM ,SUM(B.DATA_PURC_PAY) AS D_DATA_PURC_PAY_SUM ,SUM(B.DATA_FRAN_FEE) AS D_DATA_FRAN_FEE_SUM ,SUM(B.DATA_ETC_FEE) AS D_DATA_ETC_FEE_SUM ,SUM(B.DATA_AMOT_PAID) AS D_DATA_AMOT_PAID_SUM FROM LF_DMUSER.TB_ACQU_RSLT_MST A INNER JOIN LF_DMUSER.TB_ACQU_RSLT_DTL B ON A.MST_SERIAL_NUM = B.MST_SERIAL_NUM AND A.DEL_FG = 'N' AND B.DEL_FG = 'N' WHERE 1=1 AND A.STA_RECORD_GB = 'H' AND B.DATA_GB = 'D'
AND B.DATA_TRAD_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND B.DATA_PURC_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND A.STA_RECV_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND A.STA_MAKE_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND B.DATA_AMOT_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND TRIM(B.DATA_APPY_NUM) = #{data_appy_num}
GROUP BY DATA_TRAD_GB )
SELECT NVL((SELECT M_TOT_SEND_NUM FROM CALC_RSLT WHERE 1=1 AND DATA_TRAD_GB = '0'), 0) + NVL((SELECT M_TOT_SEND_NUM FROM CALC_RSLT WHERE 1=1 AND DATA_TRAD_GB = '2'), 0) AS M_TOT_SEND_NUM ,NVL((SELECT D_DATA_PURC_PAY_SUM FROM CALC_RSLT WHERE 1=1 AND DATA_TRAD_GB = '0'), 0) - NVL((SELECT D_DATA_PURC_PAY_SUM FROM CALC_RSLT WHERE 1=1 AND DATA_TRAD_GB = '2'), 0) AS D_DATA_PURC_PAY_SUM ,NVL((SELECT D_DATA_FRAN_FEE_SUM FROM CALC_RSLT WHERE 1=1 AND DATA_TRAD_GB = '0'), 0) - NVL((SELECT D_DATA_FRAN_FEE_SUM FROM CALC_RSLT WHERE 1=1 AND DATA_TRAD_GB = '2'), 0) AS D_DATA_FRAN_FEE_SUM ,NVL((SELECT D_DATA_ETC_FEE_SUM FROM CALC_RSLT WHERE 1=1 AND DATA_TRAD_GB = '0'), 0) - NVL((SELECT D_DATA_ETC_FEE_SUM FROM CALC_RSLT WHERE 1=1 AND DATA_TRAD_GB = '2'), 0) AS D_DATA_ETC_FEE_SUM ,NVL((SELECT D_DATA_AMOT_PAID_SUM FROM CALC_RSLT WHERE 1=1 AND DATA_TRAD_GB = '0'), 0) - NVL((SELECT D_DATA_AMOT_PAID_SUM FROM CALC_RSLT WHERE 1=1 AND DATA_TRAD_GB = '2'), 0) AS D_DATA_AMOT_PAID_SUM FROM DUAL

SELECT TBL.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY B.DATA_TID ASC) AS PAGE_INDX ,TRIM(A.MST_SERIAL_NUM) AS M_MST_SERIAL_NUM /* 등록일련번호 */ ,TRIM(A.STA_RECORD_GB) AS M_STA_RECORD_GB /* RECORD구분(STARTLINE) */ ,TRIM(A.STA_MAKE_DTTM) AS M_STA_MAKE_DTTM /* 파일작성일(STARTLINE) */ ,TRIM(A.STA_SEND_ID) AS M_STA_SEND_ID /* 전송대상ID(STARTLINE) */ ,TRIM(A.STA_RECV_DTTM) AS M_STA_RECV_DTTM /* 매입결과수신일(STARTLINE) */ ,TRIM(A.STA_FILLER) AS M_STA_FILLER /* FILLER(STARTLINE) */ ,TRIM(A.TOT_RECORD_GB) AS M_TOT_RECORD_GB /* RECORD구분(TOTALLINE) */ ,TRIM(A.TOT_RECV_DTTM) AS M_TOT_RECV_DTTM /* 매입결과수신일(TOTALLINE) */ ,TRIM(A.TOT_SEND_ID) AS M_TOT_SEND_ID /* 전송대상ID(TOTALLINE) */ ,TO_NUMBER(A.TOT_SEND_NUM) AS M_TOT_SEND_NUM /* 전송건수(TOTALLINE) */ ,TO_NUMBER(A.TOT_APPY_NUM) AS M_TOT_APPY_NUM /* 승인건수(TOTALLINE) */ ,TO_NUMBER(A.TOT_APPY_PAY) AS M_TOT_APPY_PAY /* 승인금액(TOTALLINE) */ ,TO_NUMBER(A.TOT_CNCL_NUM) AS M_TOT_CNCL_NUM /* 취소건수(TOTALLINE) */ ,TO_NUMBER(A.TOT_CNCL_PAY) AS M_TOT_CNCL_PAY /* 취소금액(TOTALLINE) */ ,TRIM(A.TOT_FILLER) AS M_TOT_FILLER /* FILLER(TOTALLINE) */ ,TRIM(A.FILE_NAME) AS M_FILE_NAME /* 파일명 */ ,TRIM(A.FILE_PATH) AS M_FILE_PATH /* 파일경로 */ ,TRIM(A.CALC_YN) AS M_CALC_YN /* 산출여부 */ ,TRIM(A.RGSR_ID) AS M_RGSR_ID /* 등록자ID */ ,TRIM(A.RGSR_DTTM) AS M_RGSR_DTTM /* 등록일시 */ ,TRIM(A.DEL_FG) AS M_DEL_FG /* 삭제여부 */ ,TRIM(B.MST_SERIAL_NUM) AS D_MST_SERIAL_NUM /* 등록일련번호MST */ ,TRIM(B.DATA_GB) AS D_DATA_GB /* RECORD구분(DATALINE) */ ,TRIM(B.DATA_TID) AS D_DATA_TID /* TID(DATALINE) */ ,TRIM(B.DATA_FRAN_ID) AS D_DATA_FRAN_ID /* 가맹점ID(DATALINE) */ ,TRIM(B.DATA_TRAD_GB) AS D_DATA_TRAD_GB /* 거래구분(DATALINE) */ ,TRIM(B.DATA_TRAD_DTTM) AS D_DATA_TRAD_DTTM /* 거래일자(DATALINE) */ ,TRIM(B.DATA_PURC_DTTM) AS D_DATA_PURC_DTTM /* 매입일자(DATALINE) */ ,TRIM(B.DATA_APPY_NUM) AS D_DATA_APPY_NUM /* 승인번호(DATALINE) */ ,TRIM(B.DATA_PURC_CRCO) AS D_DATA_PURC_CRCO /* 매입카드사코드(DATALINE) */ ,TRIM(B.DATA_CARD_GB) AS D_DATA_CARD_GB /* 카드구분(DATALINE) */ ,TRIM(B.DATA_CARD_NUM) AS D_DATA_CARD_NUM /* 카드번호(DATALINE) */ ,TO_NUMBER(B.DATA_PURC_PAY) AS D_DATA_PURC_PAY /* 매입금액(DATALINE) */ ,TO_NUMBER(B.DATA_FRAN_FEE) AS D_DATA_FRAN_FEE /* 가맹점수수료(DATALINE) */ ,TO_NUMBER(B.DATA_ETC_FEE) AS D_DATA_ETC_FEE /* 기타수수료(DATALINE) */ ,TO_NUMBER(B.DATA_AMOT_PAID) AS D_DATA_AMOT_PAID /* 지급금액(DATALINE) */ ,TRIM(B.DATA_AMOT_DTTM) AS D_DATA_AMOT_DTTM /* 지급예정일(DATALINE) */ ,TRIM(B.DATA_TRAD_BLGB) AS D_DATA_TRAD_BLGB /* 거래구분(DATALINE) */ ,TRIM(B.DATA_FILLER) AS D_DATA_FILLER /* FILLER(DATALINE) */ ,TRIM(B.RGSR_ID) AS D_RGSR_ID /* 등록자ID */ ,TRIM(B.RGSR_DTTM) AS D_RGSR_DTTM /* 등록일시 */ ,TRIM(B.DEL_FG) AS D_DEL_FG /* 삭제여부 */ FROM LF_DMUSER.TB_ACQU_RSLT_MST A INNER JOIN LF_DMUSER.TB_ACQU_RSLT_DTL B ON A.MST_SERIAL_NUM = B.MST_SERIAL_NUM AND A.DEL_FG = 'N' AND B.DEL_FG = 'N' WHERE 1=1 AND A.STA_RECORD_GB = 'H' AND B.DATA_GB = 'D'
AND B.DATA_TRAD_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND B.DATA_PURC_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND A.STA_RECV_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND A.STA_MAKE_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND B.DATA_AMOT_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND B.DATA_TRAD_GB = #{data_trad_gb}
AND TRIM(B.DATA_APPY_NUM) = #{data_appy_num}
) TBL WHERE 1=1

SELECT /* DlwCmsMap.getNotAuthResultDataList */ PAGE_INDX, ACCNT_NO, MEM_NM, REQ_DAY, FILE_MAKE_DAY, '' AS PAYMENT_DAY, CASE WHEN CANCEL_YN = 'Y' THEN CNCL_REQ_NO ELSE REQ_NO END AS REQ_NO, PAY_KIND, RESULT_KEY, RESULT_CD, RESULT_MSG, PAY_MTHD, AUTH_DT, AUTH_CD, '' AS ICHAE_CD, '' AS ICHAE_NO, '' AS ETC, CANCEL_DT, AUTH_YN, CASE WHEN CANCEL_YN = 'Y' THEN (MON_PAY_AMT * -1) ELSE MON_PAY_AMT END AS MON_PAY_AMT, CASE WHEN CANCEL_YN = 'Y' THEN (BAS_CMIS_AMT * -1) ELSE BAS_CMIS_AMT END AS BAS_CMIS_AMT, CASE WHEN CANCEL_YN = 'Y' THEN (FRC_CMIS_AMT * -1) ELSE FRC_CMIS_AMT END AS FRC_CMIS_AMT, CASE WHEN CANCEL_YN = 'Y' THEN (ETC_CMIS_AMT * -1) ELSE ETC_CMIS_AMT END AS ETC_CMIS_AMT, CASE WHEN CANCEL_YN = 'Y' THEN (CUR_PAY_AMT * -1) ELSE CUR_PAY_AMT END AS CUR_PAY_AMT, CANCEL_YN FROM ( SELECT ROWNUM AS PAGE_INDX, RST.ACCNT_NO, LF_DMUSER.FN_MEM_NM(MPA.MEM_NO) AS MEM_NM, RST.REQ_DAY, TO_CHAR(TO_DATE(RST.REQ_DAY, 'YYYYMMDD') + 1, 'YYYYMMDD') AS FILE_MAKE_DAY, REQ.REQ_PAY_NO AS REQ_NO, RST.PAY_KIND, RST.RESULT_KEY, RST.RESULT_CD, RST.RESULT_MSG, RST.PAY_MTHD, RST.AUTH_DT, RST.AUTH_CD, CNCL.REQ_DAY AS CANCEL_DT, RST.AUTH_YN, PI.PROD_AMT AS MON_PAY_AMT, RST.BAS_CMIS_AMT, PI.PROD_AMT - RST.CUR_PAY_AMT - RST.ETC_CMIS_AMT AS FRC_CMIS_AMT, RST.ETC_CMIS_AMT, RST.CUR_PAY_AMT, NVL(RST.CANCEL_YN,'N') AS CANCEL_YN, CNCL.REQ_PAY_NO AS CNCL_REQ_NO FROM LF_DMUSER.TB_MEMBER_WDRW_RESULT RST LEFT OUTER JOIN LF_DMUSER.TB_MEMBER_WDRW_REQ REQ ON REQ.ACCNT_NO = RST.ACCNT_NO AND REQ.REQ_DAY = RST.REQ_DAY AND REQ.RESULT_KEY = RST.RESULT_KEY LEFT OUTER JOIN LF_DMUSER.TB_MEMBER_REQ_NAUTH_CNCL CNCL ON RST.ACCNT_NO = CNCL.ACCNT_NO AND RST.REQ_DAY = CNCL.REQ_DAY AND RST.RESULT_KEY = CNCL.CC_RESULT_KEY AND CNCL.DEL_FG ='X' INNER JOIN MEM_PROD_ACCNT MPA ON RST.ACCNT_NO = MPA.ACCNT_NO AND MPA.DEL_FG = 'N' INNER JOIN PRODUCT PD ON MPA.PROD_CD = PD.PROD_CD INNER JOIN ( SELECT PROD_CD, NO, SUM(AMT) + SUM(REL_AMT) + SUM(ADD_AMT) AS PROD_AMT FROM PRODUCT_NO_DATA GROUP BY PROD_CD, NO ) PI ON REQ.PROD_CD = PI.PROD_CD AND REQ.REQ_PAY_NO = PI.NO WHERE 1=1 AND RST.REQ_DAY BETWEEN #{sta_dttm} AND #{end_dttm} AND RST.AUTH_YN = 'N'
AND NVL(RST.CANCEL_YN,'N') = 'N'
AND NVL(RST.CANCEL_YN,'N') = 'Y'
AND RST.AUTH_CD = #{data_appy_num}
) MAIN_TBL WHERE 1=1 AND RESULT_CD = '00'
AND PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT COUNT(*) AS ICNT, SUM(CASE WHEN CANCEL_YN = 'Y' THEN (NVL(MON_PAY_AMT,0) * -1) ELSE NVL(MON_PAY_AMT,0) END) AS TOT_MON_PAY_AMT, SUM(CASE WHEN CANCEL_YN = 'Y' THEN (NVL(BAS_CMIS_AMT,0) * -1) ELSE NVL(BAS_CMIS_AMT,0) END) AS TOT_BAS_CMIS_AMT, SUM(CASE WHEN CANCEL_YN = 'Y' THEN (NVL(FRC_CMIS_AMT,0) * -1) ELSE NVL(FRC_CMIS_AMT,0) END) AS TOT_FRC_CMIS_AMT, SUM(CASE WHEN CANCEL_YN = 'Y' THEN (NVL(ETC_CMIS_AMT,0) * -1) ELSE NVL(ETC_CMIS_AMT,0) END) AS TOT_ETC_CMIS_AMT, SUM(CASE WHEN CANCEL_YN = 'Y' THEN (NVL(CUR_PAY_AMT,0) * -1) ELSE NVL(CUR_PAY_AMT,0) END) AS TOT_CUR_PAY_AMT FROM ( SELECT PD.MON_PAY_AMT, RST.BAS_CMIS_AMT, PD.MON_PAY_AMT - RST.CUR_PAY_AMT - RST.ETC_CMIS_AMT AS FRC_CMIS_AMT, RST.ETC_CMIS_AMT, RST.CUR_PAY_AMT, NVL(RST.CANCEL_YN,'N') AS CANCEL_YN FROM LF_DMUSER.TB_MEMBER_WDRW_RESULT RST LEFT OUTER JOIN LF_DMUSER.TB_MEMBER_WDRW_REQ REQ ON REQ.ACCNT_NO = RST.ACCNT_NO AND REQ.REQ_DAY = RST.REQ_DAY AND REQ.RESULT_KEY = RST.RESULT_KEY LEFT OUTER JOIN LF_DMUSER.TB_MEMBER_REQ_NAUTH_CNCL CNCL ON RST.ACCNT_NO = CNCL.ACCNT_NO AND RST.REQ_DAY = CNCL.REQ_DAY AND RST.RESULT_KEY = CNCL.CC_RESULT_KEY AND CNCL.DEL_FG ='X' INNER JOIN MEM_PROD_ACCNT MPA ON RST.ACCNT_NO = MPA.ACCNT_NO AND MPA.DEL_FG = 'N' INNER JOIN PRODUCT PD ON MPA.PROD_CD = PD.PROD_CD WHERE 1=1 AND RST.REQ_DAY BETWEEN #{sta_dttm} AND #{end_dttm} AND RST.AUTH_YN = 'N' AND REQ.DEL_FG = 'C'
AND NVL(RST.CANCEL_YN,'N') = 'N'
AND NVL(RST.CANCEL_YN,'N') = 'Y'
AND RST.AUTH_CD = #{data_appy_num}
) MAIN_TBL

SELECT /* DlwCmsMap.getNotAuthResultDataCancelList */ PAGE_INDX, ACCNT_NO, MEM_NM, REQ_DAY, FILE_MAKE_DAY, '' AS PAYMENT_DAY, CASE WHEN CANCEL_YN = 'Y' THEN CNCL_REQ_NO ELSE REQ_NO END AS REQ_NO, PAY_KIND, RESULT_KEY, RESULT_CD, RESULT_MSG, PAY_MTHD, AUTH_DT, AUTH_CD, '' AS ICHAE_CD, '' AS ICHAE_NO, '' AS ETC, CANCEL_DT, AUTH_YN, MON_PAY_AMT, BAS_CMIS_AMT, FRC_CMIS_AMT, ETC_CMIS_AMT, CUR_PAY_AMT, CANCEL_YN FROM ( SELECT ROWNUM AS PAGE_INDX, CNC.ACCNT_NO, LF_DMUSER.FN_MEM_NM(MPA.MEM_NO) AS MEM_NM, CNC.REQ_DAY, TO_CHAR(TO_DATE(CNC.REQ_DAY, 'YYYYMMDD') + 1, 'YYYYMMDD') AS FILE_MAKE_DAY, CNC.REQ_PAY_NO AS REQ_NO, RST.PAY_KIND, RST.RESULT_KEY, RST.RESULT_CD, RST.RESULT_MSG, RST.PAY_MTHD, RST.AUTH_DT, RST.AUTH_CD, CNC.REQ_DAY AS CANCEL_DT, RST.AUTH_YN, PI.PROD_AMT AS MON_PAY_AMT, RST.BAS_CMIS_AMT, PI.PROD_AMT - RST.CUR_PAY_AMT - RST.ETC_CMIS_AMT AS FRC_CMIS_AMT, RST.ETC_CMIS_AMT, RST.CUR_PAY_AMT, NVL(RST.CANCEL_YN,'N') AS CANCEL_YN, CNC.REQ_PAY_NO AS CNCL_REQ_NO FROM TB_MEMBER_REQ_NAUTH_CNCL CNC INNER JOIN TB_MEMBER_WDRW_RESULT RST ON CNC.CC_RESULT_KEY = RST.RESULT_KEY INNER JOIN MEM_PROD_ACCNT MPA ON CNC.ACCNT_NO = MPA.ACCNT_NO AND MPA.DEL_FG = 'N' INNER JOIN PRODUCT PD ON MPA.PROD_CD = PD.PROD_CD INNER JOIN ( SELECT PROD_CD, NO, SUM(AMT) + SUM(REL_AMT) + SUM(ADD_AMT) AS PROD_AMT FROM PRODUCT_NO_DATA GROUP BY PROD_CD, NO ) PI ON MPA.PROD_CD = PI.PROD_CD AND CNC.REQ_PAY_NO = PI.NO WHERE 1=1 AND CNC.DEL_FG = 'X' AND CNC.REQ_DAY BETWEEN #{sta_dttm} AND #{end_dttm} AND RST.AUTH_YN = 'N'
AND NVL(RST.CANCEL_YN,'N') = 'Y'
AND RST.AUTH_CD = #{data_appy_num}
) MAIN_TBL WHERE 1=1 AND RESULT_CD = '00'
AND PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT COUNT(*) AS ICNT, SUM(CASE WHEN CANCEL_YN = 'Y' THEN (NVL(MON_PAY_AMT,0) * -1) ELSE NVL(MON_PAY_AMT,0) END) AS TOT_MON_PAY_AMT, SUM(CASE WHEN CANCEL_YN = 'Y' THEN (NVL(BAS_CMIS_AMT,0) * -1) ELSE NVL(BAS_CMIS_AMT,0) END) AS TOT_BAS_CMIS_AMT, SUM(CASE WHEN CANCEL_YN = 'Y' THEN (NVL(FRC_CMIS_AMT,0) * -1) ELSE NVL(FRC_CMIS_AMT,0) END) AS TOT_FRC_CMIS_AMT, SUM(CASE WHEN CANCEL_YN = 'Y' THEN (NVL(ETC_CMIS_AMT,0) * -1) ELSE NVL(ETC_CMIS_AMT,0) END) AS TOT_ETC_CMIS_AMT, SUM(CASE WHEN CANCEL_YN = 'Y' THEN (NVL(CUR_PAY_AMT,0) * -1) ELSE NVL(CUR_PAY_AMT,0) END) AS TOT_CUR_PAY_AMT FROM ( SELECT PI.PROD_AMT AS MON_PAY_AMT, RST.BAS_CMIS_AMT, PI.PROD_AMT - RST.CUR_PAY_AMT - RST.ETC_CMIS_AMT AS FRC_CMIS_AMT, RST.ETC_CMIS_AMT, RST.CUR_PAY_AMT, NVL(RST.CANCEL_YN,'N') AS CANCEL_YN, CNC.REQ_PAY_NO AS CNCL_REQ_NO FROM TB_MEMBER_REQ_NAUTH_CNCL CNC INNER JOIN TB_MEMBER_WDRW_RESULT RST ON CNC.CC_RESULT_KEY = RST.RESULT_KEY INNER JOIN MEM_PROD_ACCNT MPA ON CNC.ACCNT_NO = MPA.ACCNT_NO AND MPA.DEL_FG = 'N' INNER JOIN PRODUCT PD ON MPA.PROD_CD = PD.PROD_CD INNER JOIN ( SELECT PROD_CD, NO, SUM(AMT) + SUM(REL_AMT) + SUM(ADD_AMT) AS PROD_AMT FROM PRODUCT_NO_DATA GROUP BY PROD_CD, NO ) PI ON MPA.PROD_CD = PI.PROD_CD AND CNC.REQ_PAY_NO = PI.NO WHERE 1=1 AND CNC.DEL_FG = 'X' AND CNC.REQ_DAY BETWEEN #{sta_dttm} AND #{end_dttm} AND RST.AUTH_YN = 'N'
AND NVL(RST.CANCEL_YN,'N') = 'Y'
AND RST.AUTH_CD = #{data_appy_num}
) MAIN_TBL

/* DlwCmsMap.getNotAuthResultDataListTotalSummary2 */ WITH CALC_RSLT AS ( SELECT * FROM ( SELECT RST.CANCEL_YN, COUNT(*) AS ICNT, NVL(SUM(PD.MON_PAY_AMT), 0) AS TOT_MON_PAY_AMT, NVL(SUM(RST.BAS_CMIS_AMT), 0) AS TOT_BAS_CMIS_AMT, NVL(SUM(PD.MON_PAY_AMT), 0) - NVL(SUM(RST.CUR_PAY_AMT), 0) - NVL(SUM(RST.ETC_CMIS_AMT), 0) AS TOT_FRC_CMIS_AMT, NVL(SUM(RST.ETC_CMIS_AMT), 0) AS TOT_ETC_CMIS_AMT, NVL(SUM(RST.CUR_PAY_AMT), 0) AS TOT_CUR_PAY_AMT FROM LF_DMUSER.TB_MEMBER_WDRW_REQ REQ INNER JOIN LF_DMUSER.PRODUCT PD ON REQ.PROD_CD = PD.PROD_CD LEFT OUTER JOIN LF_DMUSER.TB_MEMBER_WDRW_RESULT RST ON REQ.ACCNT_NO = RST.ACCNT_NO AND REQ.PAY_MTHD = RST.PAY_MTHD AND REQ.REQ_DAY = RST.REQ_DAY AND REQ.RESULT_KEY = RST.RESULT_KEY WHERE 1=1 AND RST.AUTH_YN = 'N' AND RST.RESULT_CD = '00'
AND RST.REQ_DAY BETWEEN #{sta_dttm} AND #{end_dttm}
AND TO_CHAR(TO_DATE(RST.REQ_DAY, 'YYYYMMDD') + 1, 'YYYYMMDD') BETWEEN #{sta_dttm} AND #{end_dttm}
AND TO_CHAR(TO_DATE(RST.REQ_DAY, 'YYYYMMDD') + 4, 'YYYYMMDD') BETWEEN #{sta_dttm} AND #{end_dttm}
AND RST.CANCEL_YN = 'N'
AND RST.CANCEL_YN = 'Y'
AND RST.AUTH_CD = #{data_appy_num}
AND RST.ICHAE_CD = #{data_purc_crco}
GROUP BY RST.CANCEL_YN ) WHERE 1=1 )
SELECT NVL((SELECT ICNT FROM CALC_RSLT WHERE 1=1 AND CANCEL_YN = 'N'), 0) + NVL((SELECT ICNT FROM CALC_RSLT WHERE 1=1 AND CANCEL_YN = 'Y'), 0) AS ICNT, NVL((SELECT TOT_MON_PAY_AMT FROM CALC_RSLT WHERE 1=1 AND CANCEL_YN = 'N'), 0) - NVL((SELECT TOT_MON_PAY_AMT FROM CALC_RSLT WHERE 1=1 AND CANCEL_YN = 'Y'), 0) AS TOT_MON_PAY_AMT, NVL((SELECT TOT_BAS_CMIS_AMT FROM CALC_RSLT WHERE 1=1 AND CANCEL_YN = 'N'), 0) - NVL((SELECT TOT_BAS_CMIS_AMT FROM CALC_RSLT WHERE 1=1 AND CANCEL_YN = 'Y'), 0) AS TOT_BAS_CMIS_AMT, NVL((SELECT TOT_FRC_CMIS_AMT FROM CALC_RSLT WHERE 1=1 AND CANCEL_YN = 'N'), 0) - NVL((SELECT TOT_FRC_CMIS_AMT FROM CALC_RSLT WHERE 1=1 AND CANCEL_YN = 'Y'), 0) AS TOT_FRC_CMIS_AMT, NVL((SELECT TOT_ETC_CMIS_AMT FROM CALC_RSLT WHERE 1=1 AND CANCEL_YN = 'N'), 0) - NVL((SELECT TOT_ETC_CMIS_AMT FROM CALC_RSLT WHERE 1=1 AND CANCEL_YN = 'Y'), 0) AS TOT_ETC_CMIS_AMT, NVL((SELECT TOT_CUR_PAY_AMT FROM CALC_RSLT WHERE 1=1 AND CANCEL_YN = 'N'), 0) - NVL((SELECT TOT_CUR_PAY_AMT FROM CALC_RSLT WHERE 1=1 AND CANCEL_YN = 'Y'), 0) AS TOT_CUR_PAY_AMT FROM DUAL WHERE 1=1

SELECT  /*DlwCmsMap.getAcquInicisResultDataListTotalCnt*/
	COUNT(*)
  FROM ( SELECT *
           FROM LF_DMUSER.TB_ACQU_INICIS_LIST AIL INNER JOIN 
                MEM_PROD_ACCNT MPA ON AIL.ACCNT_NO = MPA.ACCNT_NO INNER JOIN 
                PRODUCT PD ON MPA.PROD_CD = PD.PROD_CD
          WHERE 1=1

            AND AIL.AUTH_DT BETWEEN #{sta_dttm} AND #{end_dttm}


            AND AIL.ACQU_DT BETWEEN #{sta_dttm} AND #{end_dttm}


            AND TRIM(AIL.CARD_CD) = #{data_purc_crco}

       ) TBL
  WHERE 1=1

SELECT /*DlwCmsMap.getAcquInicisResultDataList*/ TBL.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY AIL.TID ASC) AS PAGE_INDX ,AIL.AUTH_DT ,AIL.AUTH_CD ,AIL.ACQU_DT ,AIL.ACCNT_NO ,AIL.TID ,AIL.CARD_CD ,LF_DMUSER.FN_COM_NM('0196', AIL.CARD_CD) AS CARD_NM ,AIL.DATA_PUR_PAY ,AIL.DATA_ETC_FEE ,AIL.DATA_AMOT_PAID ,AIL.CANCEL_DT ,AIL.RAW_DATA ,AIL.REG_DM ,AIL.REG_MAN ,PD.PROD_NM ,CASE WHEN SUBSTR(AIL.TID, 11, 10) IN ('daemyunhn1', 'daemyunhn2') THEN '장기할부' ELSE '' END AS LONGTERM_YN FROM LF_DMUSER.TB_ACQU_INICIS_LIST AIL INNER JOIN MEM_PROD_ACCNT MPA ON AIL.ACCNT_NO = MPA.ACCNT_NO INNER JOIN PRODUCT PD ON MPA.PROD_CD = PD.PROD_CD WHERE 1=1
AND AIL.AUTH_DT BETWEEN #{sta_dttm} AND #{end_dttm}
AND AIL.ACQU_DT BETWEEN #{sta_dttm} AND #{end_dttm}
AND TRIM(AIL.CARD_CD) = #{data_purc_crco}
) TBL WHERE 1=1
AND PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT COUNT(*)                 AS M_TOT_SEND_NUM
      ,SUM(TBL.DATA_PUR_PAY)    AS D_DATA_PUR_PAY_SUM
      ,SUM(TBL.DATA_ETC_FEE)    AS D_DATA_ETC_FEE_SUM
      ,SUM(TBL.DATA_AMOT_PAID)  AS D_DATA_AMOT_PAID_SUM
  FROM ( SELECT *
           FROM LF_DMUSER.TB_ACQU_INICIS_LIST AIL
          WHERE 1=1

            AND AIL.AUTH_DT BETWEEN #{sta_dttm} AND #{end_dttm}


            AND AIL.ACQU_DT BETWEEN #{sta_dttm} AND #{end_dttm}


            AND TRIM(AIL.CARD_CD) = #{data_purc_crco}

       ) TBL
 WHERE 1=1

        SELECT /* DlwCmsMap.getLoanPurchResultDataTotalCnt */
            COUNT(*) AS ICNT
        FROM LF_DMUSER.TB_ACQU_RSLT_DTL ARD
        WHERE 1=1
        AND ARD.DATA_FRAN_ID = 'purch000m'


            AND ARD.DATA_TRAD_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


            AND ARD.DATA_PURC_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


            AND ARD.DATA_AMOT_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


            AND ARD.DATA_TRAD_GB = #{data_trad_gb} 


            AND ARD.DATA_APPY_NUM = #{data_appy_num}


	            AND NVL(DATA_TID,'NOT CONNECT') = 'NOT CONNECT'

SELECT /* DlwCmsMap.getLoanPurchResultDataList */ MAIN_TBL.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY ARD.ACCNT_NO ASC) AS PAGE_INDX, ARD.ACCNT_NO, ARD.DATA_TRAD_DTTM, ARD.DATA_PURC_DTTM, ARD.DATA_APPY_NUM, ARD.DATA_PURC_CRCO, ARD.DATA_CARD_GB, ARD.DATA_CARD_NUM, ARD.DATA_PURC_PAY, ARD.DATA_FRAN_FEE, ARD.DATA_ETC_FEE, ARD.PURCH_CMIS_TOT, ARD.PURCH_CMIS_POINT, ARD.DATA_AMOT_DTTM, ARD.DATA_TRAD_GB, ARD.DATA_TID, ARD.PRE_TRAD_DTTM, ARD.RGSR_ID, ARD.RGSR_DTTM, ARD.DEL_FG FROM LF_DMUSER.TB_ACQU_RSLT_DTL ARD WHERE 1=1 AND ARD.DATA_FRAN_ID = 'purch000m'
AND ARD.DATA_TRAD_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND ARD.DATA_PURC_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND ARD.DATA_AMOT_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}
AND ARD.DATA_TRAD_GB = #{data_trad_gb}
AND ARD.DATA_APPY_NUM = #{data_appy_num}
AND NVL(DATA_TID,'NOT CONNECT') = 'NOT CONNECT'
) MAIN_TBL WHERE 1=1
AND PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT /* DlwCmsMap.getLoanPurchResultDataListTotalSummary */
    COUNT(*) AS ICNT,
    SUM(ARD.DATA_PURC_PAY) AS TOT_MON_PURCH_AMT
FROM LF_DMUSER.TB_ACQU_RSLT_DTL ARD
WHERE 1=1
AND ARD.DATA_FRAN_ID = 'purch000m'

    AND ARD.DATA_TRAD_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


    AND ARD.DATA_PURC_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


    AND ARD.DATA_AMOT_DTTM BETWEEN #{sta_dttm} AND #{end_dttm}


    AND ARD.DATA_TRAD_GB = #{data_trad_gb} 


    AND ARD.DATA_APPY_NUM = #{data_appy_num}

mapper.update:
/* DlwCmsMap.updateLoanPurchResultAccntNoRealTID */ BEGIN /* 1. 승인일자 + 승인번호 + 카드번호로 회원 연결 */ MERGE INTO TB_ACQU_RSLT_DTL AA USING ( SELECT AAA.DATA_TRAD_DTTM, AAA.DATA_PURC_DTTM, AAA.DATA_APPY_NUM, CARD.CARD_NO, CARD.TID, CARD.ACCNT_NO FROM TB_ACQU_RSLT_DTL AAA LEFT OUTER JOIN ( SELECT RST.ACCNT_NO, RST.TID, RST.REQ_DAY, RST.AUTH_CD, SUBSTR(CM.CARD_NO,0,6) AS CARD_NO FROM TB_MEMBER_WDRW_RESULT RST INNER JOIN CARD_MEM CM ON RST.ACCNT_NO = CM.ACCNT_NO AND CM.DEL_FG = 'N' WHERE 1=1 AND NVL(RST.INI_YN,'N') = 'N' ) CARD ON AAA.DATA_TRAD_DTTM = CARD.REQ_DAY AND AAA.DATA_APPY_NUM = CARD.AUTH_CD AND SUBSTR(AAA.DATA_CARD_NUM,0,6) = CARD.CARD_NO WHERE 1=1 AND AAA.DATA_TRAD_DTTM = #{req_day} AND AAA.DATA_TRAD_GB = 0 AND AAA.DATA_FRAN_ID = 'purch000m' AND LENGTH(NVL(CARD.TID,'N')) < 40 ) BB ON (AA.DATA_FRAN_ID = 'purch000m' AND AA.DATA_TRAD_DTTM = BB.DATA_TRAD_DTTM AND DATA_TRAD_GB = 0 AND AA.DATA_APPY_NUM = BB.DATA_APPY_NUM AND SUBSTR(AA.DATA_CARD_NUM,0,6) = BB.CARD_NO) WHEN MATCHED THEN UPDATE SET AA.DATA_TID = BB.TID, AA.ACCNT_NO = BB.ACCNT_NO;
COMMIT;
/* 2. 승인일자 + 승인번호로 회원 연결 */ MERGE INTO TB_ACQU_RSLT_DTL AA USING ( SELECT DATA_TRAD_DTTM, DATA_APPY_NUM, TID, ACCNT_NO FROM ( SELECT DATA_TRAD_DTTM, DATA_APPY_NUM, MAX(RST.TID) AS TID, MAX(RST.ACCNT_NO) AS ACCNT_NO FROM TB_ACQU_RSLT_DTL AAA INNER JOIN TB_MEMBER_WDRW_RESULT RST ON AAA.DATA_TRAD_DTTM = RST.REQ_DAY AND AAA.DATA_APPY_NUM = RST.AUTH_CD WHERE 1=1 AND AAA.DATA_TRAD_DTTM = #{req_day} AND AAA.DATA_TRAD_GB = 0 AND AAA.DATA_FRAN_ID = 'purch000m' AND NVL(DATA_TID,'NOT CONNECT') = 'NOT CONNECT' AND NVL(RST.INI_YN,'N') = 'N' GROUP BY DATA_TRAD_DTTM, DATA_APPY_NUM HAVING COUNT(*) = 1 ) ) BB ON (AA.DATA_FRAN_ID = 'purch000m' AND AA.DATA_TRAD_DTTM = BB.DATA_TRAD_DTTM AND DATA_TRAD_GB = 0 AND AA.DATA_TRAD_DTTM = BB.DATA_TRAD_DTTM AND AA.DATA_APPY_NUM = BB.DATA_APPY_NUM) WHEN MATCHED THEN UPDATE SET AA.DATA_TID = BB.TID, AA.ACCNT_NO = BB.ACCNT_NO;
COMMIT;
/* 3. 승인 취소 승인일자 + 승인번호로 회원 연결 */ MERGE INTO TB_ACQU_RSLT_DTL AA USING ( SELECT BBB.ACCNT_NO, BBB.TID, AAA.DATA_TRAD_DTTM, BBB.CARD_NO, AAA.DATA_APPY_NUM, BBB.REQ_DAY FROM TB_ACQU_RSLT_DTL AAA INNER JOIN ( SELECT TO_CHAR(RFD.CANCEL_DT,'YYYYMMDD') AS CANCEL_DT, SUBSTR(CARD_NO, 0,6) AS CARD_NO, RST.REQ_DAY, RST.AUTH_CD, RST.ACCNT_NO, RST.TID FROM TB_MEMBER_REQ_REFUND RFD INNER JOIN TB_MEMBER_WDRW_RESULT RST ON RFD.ACCNT_NO = RST.ACCNT_NO AND RFD.REQ_DAY = RST.REQ_DAY AND RFD.RESULT_KEY = RFD.RESULT_KEY INNER JOIN CARD_MEM CM ON RST.ACCNT_NO = CM.ACCNT_NO AND CM.DEL_FG = 'N' WHERE 1=1 AND RFD.PAY_MTHD = '06'
) BBB ON AAA.DATA_TRAD_DTTM = BBB.CANCEL_DT AND AAA.DATA_APPY_NUM = BBB.AUTH_CD WHERE 1=1 AND DATA_TRAD_DTTM = #{req_day} AND DATA_FRAN_ID = 'purch000m' AND DATA_TRAD_GB = 2 AND NVL(DATA_TID,'NOT CONNECT') = 'NOT CONNECT' ) BB ON (AA.DATA_TRAD_DTTM = BB.DATA_TRAD_DTTM AND AA.DATA_APPY_NUM = BB.DATA_APPY_NUM AND DATA_TRAD_GB = 2) WHEN MATCHED THEN UPDATE SET AA.DATA_TID = BB.TID, AA.ACCNT_NO = BB.ACCNT_NO ;
COMMIT; END;

/* DlwCmsMap.updateLoanPurchMemberWdrwResultAuth */
UPDATE LF_DMUSER.TB_ACQU_RSLT_DTL
SET ACCNT_NO = #{accnt_no},
	DATA_TID = #{tid}
WHERE 1=1
AND DATA_FRAN_ID = 'purch000m'
AND DATA_TID = 'NOT CONNECT'
AND DATA_TRAD_GB = #{data_trad_gb}
AND DATA_TRAD_DTTM = #{req_day}
AND DATA_APPY_NUM = #{auth_cd}
AND SUBSTR(DATA_CARD_NUM, 0, 6) = SUBSTR(#{card_no}, 0, 6)

/* DlwCmsMap.updateLoanPurchMemberWdrwResultCancel */
UPDATE LF_DMUSER.TB_ACQU_RSLT_DTL
SET ACCNT_NO = #{accnt_no},
	DATA_TID = #{tid}
WHERE 1=1
AND DATA_FRAN_ID = 'purch000m'
AND DATA_TID = 'NOT CONNECT'
AND DATA_TRAD_GB = #{data_trad_gb}
AND DATA_TRAD_DTTM = #{cancel_dt}
AND DATA_APPY_NUM = #{auth_cd}
AND PRE_TRAD_DTTM = #{req_day}
AND DATA_PURC_PAY = #{pay_amt}
AND SUBSTR(DATA_CARD_NUM, 0, 6) = SUBSTR(#{card_no}, 0, 6)

mapper.insert:
BEGIN
INSERT INTO NEW_CANCL_APP ( SEQ, ACCNT_NO, BANK_ACCNT_NO, APP_DT, PAYR_NO, BANK_CD, DEPR, INV_TG_DT, APP_CL, ACPT_MTHD, RELTN, STAT, REG_DM, REG_MAN, UPD_DM, UPD_MAN, BIRTH, DEPR_CELL ) SELECT (SELECT MAX(SEQ) FROM NEW_CANCL_APP) + ROWNUM AS SEQ, EB.ACCNT_NO, EB.BANK_ACCNT_NO, EB.EXT_DT AS APP_DT, MPA.MEM_NO AS PAYR_NO, EB.BANK_CD, CM.DEPR, CM.ICHAE_DT AS INV_TG_DT, EB.APP_CL, CM.ACPT_MTHD, CM.RELTN, '04' AS STAT, SYSDATE AS REG_DM, 'EB11_AUTO' AS REG_MAN, SYSDATE AS UPD_DM, '' AS UPD_MAN, EB.IDN_NO AS BIRTH, '' AS DEPR_CELL FROM TB_CMS_EB11_LIST EB INNER JOIN MEM_PROD_ACCNT MPA ON EB.ACCNT_NO = MPA.ACCNT_NO AND MPA.DEL_FG = 'N' LEFT OUTER JOIN CMS_MEM CM ON EB.ACCNT_NO = CM.ACCNT_NO AND CM.DEL_FG = 'N' WHERE EXT_DT = #{ext_dt} AND MPA.PAY_MTHD = '04' AND EB.APP_CL IN (3,7);
INSERT INTO NEW_CANCL_APP ( SEQ, ACCNT_NO, BANK_ACCNT_NO, APP_DT, PAYR_NO, BANK_CD, DEPR, INV_TG_DT, APP_CL, ACPT_MTHD, RELTN, STAT, REG_DM, REG_MAN, UPD_DM, UPD_MAN, BIRTH, DEPR_CELL ) SELECT (SELECT MAX(SEQ) FROM NEW_CANCL_APP) + ROWNUM AS SEQ, EB.ACCNT_NO, EB.BANK_ACCNT_NO, EB.EXT_DT AS APP_DT, MPA.MEM_NO AS PAYR_NO, EB.BANK_CD, CM.DEPR, CM.ICHAE_DT AS INV_TG_DT, EB.APP_CL, CM.ACPT_MTHD, CM.RELTN, '04' AS STAT, SYSDATE AS REG_DM, 'EB11_AUTO' AS REG_MAN, SYSDATE AS UPD_DM, '' AS UPD_MAN, EB.IDN_NO AS BIRTH, '' AS DEPR_CELL FROM TB_CMS_EB11_LIST EB INNER JOIN MEM_PROD_ACCNT MPA ON EB.ACCNT_NO = MPA.ACCNT_NO AND MPA.DEL_FG = 'N' LEFT OUTER JOIN CMS_MEM CM ON EB.ACCNT_NO = CM.ACCNT_NO AND CM.DEL_FG = 'N' WHERE EXT_DT = #{ext_dt} AND MPA.PAY_MTHD = '04' AND EB.APP_CL = 1;
COMMIT;
END;

mapper.update:
BEGIN
DELETE FROM CMS_MEM_DAMO WHERE ACCNT_NO IN ( SELECT EB.ACCNT_NO FROM TB_CMS_EB11_LIST EB INNER JOIN MEM_PROD_ACCNT MPA ON EB.ACCNT_NO = MPA.ACCNT_NO AND MPA.DEL_FG = 'N' WHERE EXT_DT = #{ext_dt} AND MPA.PAY_MTHD = '04' AND EB.APP_CL IN (3,7) ) AND ACCNT_NO NOT IN ( SELECT ACCNT_NO FROM TB_CMS_EB11_LIST WHERE EXT_DT = #{ext_dt} GROUP BY ACCNT_NO HAVING COUNT(*) > 1 );
MERGE INTO CMS_MEM_DAMO CM USING( SELECT EXT_DT, ACCNT_NO, BANK_CD, BANK_ACCNT_NO, IDN_NO FROM TB_CMS_EB11_LIST WHERE 1=1 AND APP_CL = 1 AND EXT_DT = #{ext_dt} AND ACCNT_NO IN ( SELECT ACCNT_NO FROM TB_CMS_EB11_LIST WHERE EXT_DT = #{ext_dt} GROUP BY ACCNT_NO HAVING COUNT(*) > 1 ) ) TBL ON (CM.ACCNT_NO = TBL.ACCNT_NO AND CM.DEL_FG = 'N') WHEN MATCHED THEN UPDATE SET CM.BANK_CD = TBL.BANK_CD, CM.SEC_BANK_ACCNT_NO = DAMO.ENC_VARCHAR_B64('LF_DMUSER','CMS_MEM','BANK_ACCNT_NO',TBL.BANK_ACCNT_NO), CM.SEC_IDN_NO = DAMO.ENC_VARCHAR_B64('LF_DMUSER','CMS_MEM','IDN_NO',TBL.IDN_NO), CM.UPD_MAN = 'EB11_AUTO', CM.UPD_DM = SYSDATE ;
UPDATE TB_CMS_EB11_LIST SET BANK_ACCNT_NO = '****-****-' || SUBSTR(TRIM(BANK_ACCNT_NO),-4), IDN_NO = '***' || SUBSTR(TRIM(IDN_NO),-3) WHERE EXT_DT = #{ext_dt};
COMMIT;
END;
