mapper.insert:
BEGIN
/* DlwDeductionMap.insertGongjeDayExt */
INSERT INTO LF_DMUSER.TB_GONGJE_DAY_EXT /* [001] [공제 전송 기초 대상 데이터] 신규 회원 등록 */ SELECT GDATE, ACCNT_NO, CL, GBIT, WORK_DM, PAY_NO, SUBSTR(('0000000' || ROWNUM),-8) AS RESULT_KEY, EXT_INFO, EXT_INFO2 FROM ( SELECT TO_CHAR(SYSDATE, 'YYYYMMDD') AS GDATE, MBID.ACCNT_NO, 'D' AS CL, '00' AS GBIT, MBID.JOIN_DT AS WORK_DM, MBID.TRUE_CNT AS PAY_NO, MBID.PAY_MTHD AS EXT_INFO, '' AS EXT_INFO2 FROM LF_DMUSER.TB_MEMBER_BASIC_INFO_DAY MBID WHERE 1=1 AND KSTBIT = '02' AND STAT IN ('01','11','011') AND NOT EXISTS (SELECT ACCNT_NO FROM LF_DMUSER.TB_GONGJE_ASIS_DATA WHERE ACCNT_NO = MBID.ACCNT_NO)
UNION ALL
/* [001-1] [공제 전송 기초 대상 데이터] 신규 회원 등록 (장례이행보증제도) */ SELECT TO_CHAR(SYSDATE, 'YYYYMMDD') AS GDATE, MBID.ACCNT_NO, 'D' AS CL, '00' AS GBIT, MBID.JOIN_DT AS WORK_DM, MBID.TRUE_CNT AS PAY_NO, MBID.PAY_MTHD AS EXT_INFO, TO_CHAR((SELECT PERF_AMT FROM TB_PERF_MAIN WHERE ACCNT_NO = MBID.ACCNT_NO AND DEL_FG = 'N')) AS EXT_INFO2 FROM LF_DMUSER.TB_MEMBER_BASIC_INFO_DAY MBID WHERE 1=1 AND KSTBIT = '02' AND STAT IN ('23') AND NOT EXISTS (SELECT ACCNT_NO FROM LF_DMUSER.TB_GONGJE_ASIS_DATA WHERE ACCNT_NO = MBID.ACCNT_NO)
UNION ALL
/* [002] [공제 전송 기초 대상 데이터] 회원 정보 변경 등록 20190313 양도양수의 경우 회원의 정보가 넘어가기 때문에 별도로 정보변경 하지 않는다 넘길시 에러 */ SELECT TO_CHAR(SYSDATE, 'YYYYMMDD') AS GDATE, TBL.ACCNT_NO, 'U' AS CL, '00' AS GBIT, TBL.WORK_DM, 0 AS PAY_NO, '' AS EXT_INFO, '' AS EXT_INFO2
FROM ( SELECT ACCNT_NO, MAX(WORK_DM) AS WORK_DM FROM REG_UPD_DEL_INF RUDI WHERE 1=1 AND NEW_YN = 'Y' AND CL1 = 'U' AND EXISTS (SELECT ACCNT_NO FROM LF_DMUSER.TB_GONGJE_ASIS_DATA WHERE ACCNT_NO = RUDI.ACCNT_NO AND KSTBIT = '정상') AND WORK_DM != TO_CHAR(SYSDATE,'YYYYMMDD') AND DAT1 IN('자택우편번호','자택주소','생년월일','성명','휴대폰','주민번호','이메일') GROUP BY ACCNT_NO ) TBL WHERE 1=1 AND NOT EXISTS ( SELECT ACCNT_NO FROM TRANSFER_BRKDN TR WHERE 1=1 AND ACCNT_NO = TBL.ACCNT_NO AND TR.NEW_YN = 'Y' AND HANOV_DT != TO_CHAR(SYSDATE,'YYYYMMDD') AND EXISTS (SELECT ACCNT_NO FROM LF_DMUSER.TB_GONGJE_ASIS_DATA WHERE ACCNT_NO = TR.ACCNT_NO AND KSTBIT = '정상') )
UNION ALL
/* [003] [공제 전송 기초 대상 데이터] 해약 회원 등록 */ SELECT TO_CHAR(SYSDATE, 'YYYYMMDD') AS GDATE, RS.ACCNT_NO, 'R' AS CL, '00' AS GBIT, --NVL(D.PAY_AMT,0) PAY_AMT, RESN_PROC_DAY AS WORK_DM, 0 AS PAY_NO, '' AS EXT_INFO, '' AS EXT_INFO2 FROM RESCISSION RS WHERE 1=1 AND EXISTS (SELECT ACCNT_NO FROM LF_DMUSER.TB_GONGJE_ASIS_DATA WHERE ACCNT_NO = RS.ACCNT_NO AND KSTBIT = '정상') AND DEL_FG = 'N' AND (RESN_CL = '02' OR (RESN_CL != '02' AND NVL(RESN_PROC_YN,'N') = 'Y'))
UNION ALL
/* [004] [공제 전송 기초 대상 데이터] 양도양수 회원 등록 */ SELECT DISTINCT TO_CHAR(SYSDATE, 'YYYYMMDD') AS GDATE, TR.ACCNT_NO, 'T' AS CL, '00' AS GBIT, HANOV_DT AS WORK_DM, 0 AS PAY_NO, '' AS EXT_INFO, '' AS EXT_INFO2 FROM TRANSFER_BRKDN TR WHERE 1=1 AND TR.NEW_YN = 'Y' AND HANOV_DT != TO_CHAR(SYSDATE,'YYYYMMDD') AND EXISTS (SELECT ACCNT_NO FROM LF_DMUSER.TB_GONGJE_ASIS_DATA WHERE ACCNT_NO = TR.ACCNT_NO AND KSTBIT = '정상')
UNION ALL
/* [005] [공제 전송 기초 대상 데이터] 행사 회원 등록 */ SELECT TO_CHAR(SYSDATE, 'YYYYMMDD') AS GDATE, EV.ACCNT_NO, 'E' AS CL, '00' AS GBIT, EV.EVENT_PROC_DAY AS WORK_DM, 0 AS PAY_NO, '' AS EXT_INFO, '' AS EXT_INFO2 FROM EVENT EV WHERE 1=1 AND EV.DEL_FG = 'N' AND EXISTS (SELECT ACCNT_NO FROM LF_DMUSER.TB_GONGJE_ASIS_DATA WHERE ACCNT_NO = EV.ACCNT_NO AND KSTBIT = '정상')
UNION ALL
/* [006] [공제 전송 기초 대상 데이터] 입금정보등록 */
SELECT TO_CHAR(SYSDATE,'YYYYMMDD') AS GDATE, MBID.ACCNT_NO, 'P' AS CL, '00' AS GBIT, MBID.TRUE_DAY AS WORK_DM, (NVL(GONGJE_CNT,0) + LVL.ROW_CNT) AS PAY_NO, MBID.TRUE_MTHD AS EXT_INFO, '' AS EXT_INFO2 FROM TB_MEMBER_BASIC_INFO_DAY MBID INNER JOIN TB_GONGJE_ASIS_DATA GAD ON MBID.ACCNT_NO = GAD.ACCNT_NO AND GAD.KSTBIT = '정상' INNER JOIN ( SELECT LEVEL ROW_CNT FROM DUAL CONNECT BY LEVEL <= 1000 ) LVL ON LVL.ROW_CNT BETWEEN 1 AND (NVL(MBID.TRUE_CNT,0) - NVL(GAD.GONGJE_CNT,0)) WHERE 1=1 AND MBID.KSTBIT = '02' AND NVL(MBID.TRUE_CNT,0) > NVL(GAD.GONGJE_CNT,0)
UNION ALL
/* [007] [공제 전송 기초 대상 데이터] 입금취소정보등록 */ SELECT TO_CHAR(SYSDATE,'YYYYMMDD') AS GDATE, ACCNT_NO, 'P_C' AS CL, '00' AS GBIT, WORK_DM, NVL(PAY_NO,0) AS PAY_NO, TO_CHAR(ROW_CNT) AS EXT_INFO, TO_CHAR(AMT) AS EXT_INFO2 FROM ( SELECT MBID.ACCNT_NO, TO_CHAR(SYSDATE,'YYYYMMDD') AS WORK_DM, ((NVL(GONGJE_CNT,0) + 1) - LVL.ROW_CNT) AS PAY_NO, ROW_CNT, PND.AMT FROM TB_MEMBER_BASIC_INFO_DAY MBID INNER JOIN TB_GONGJE_ASIS_DATA GAD ON MBID.ACCNT_NO = GAD.ACCNT_NO AND GAD.KSTBIT = '정상' INNER JOIN ( SELECT LEVEL ROW_CNT FROM DUAL CONNECT BY LEVEL <= 1000 ) LVL ON LVL.ROW_CNT BETWEEN 1 AND (NVL(GAD.GONGJE_CNT,0) - NVL(MBID.TRUE_CNT,0)) INNER JOIN PRODUCT_NO_DATA PND ON MBID.PROD_CD = PND.PROD_CD AND PND.NO = ((NVL(GONGJE_CNT,0) + 1) - LVL.ROW_CNT) WHERE 1=1 AND MBID.KSTBIT = '02' AND NVL(MBID.TRUE_CNT,0) < NVL(GAD.GONGJE_CNT,0) )
UNION ALL
/* [008] [공제 전송 기초 대상 데이터] 해약 취소 회원 */ SELECT TO_CHAR(SYSDATE, 'YYYYMMDD') AS GDATE, RS.ACCNT_NO, 'R_C' AS CL, '00' AS GBIT, --NVL(D.PAY_AMT,0) PAY_AMT, TO_CHAR(RS.UPD_DM ,'YYYYMMDD') AS WORK_DM, 0 AS PAY_NO, '' AS EXT_INFO, '' AS EXT_INFO2 FROM RESCISSION RS INNER JOIN MEM_PROD_ACCNT MPA ON RS.ACCNT_NO = MPA.ACCNT_NO AND MPA.DEL_FG = 'N' WHERE 1=1 AND EXISTS (SELECT ACCNT_NO FROM LF_DMUSER.TB_GONGJE_ASIS_DATA WHERE ACCNT_NO = RS.ACCNT_NO AND KSTBIT IN ('청약철회','청약해지')) AND NOT EXISTS (SELECT ACCNT_NO FROM LF_DMUSER.RESCISSION WHERE ACCNT_NO = RS.ACCNT_NO AND DEL_FG = 'N') AND (RESN_CL = '02' OR (RESN_CL != '02' AND NVL(RESN_PROC_YN,'N') = 'Y'))
UNION ALL
/* [009] [공제 전송 기초 대상 데이터] 행사 취소 회원 */ SELECT TO_CHAR(SYSDATE, 'YYYYMMDD') AS GDATE, ACCNT_NO, 'E_C' AS CL, '00' AS GBIT, --NVL(D.PAY_AMT,0) PAY_AMT, TO_CHAR(WORK_DM ,'YYYYMMDD') AS WORK_DM, 0 AS PAY_NO, '' AS EXT_INFO, '' AS EXT_INFO2 FROM ( SELECT EV.ACCNT_NO, MAX(EV.CANCL_REG_DM) AS WORK_DM FROM EVENT EV INNER JOIN MEM_PROD_ACCNT MPA ON EV.ACCNT_NO = MPA.ACCNT_NO AND MPA.DEL_FG = 'N' WHERE 1=1 AND EXISTS (SELECT ACCNT_NO FROM LF_DMUSER.TB_GONGJE_ASIS_DATA WHERE ACCNT_NO = EV.ACCNT_NO AND KSTBIT = '행사') AND NOT EXISTS (SELECT ACCNT_NO FROM LF_DMUSER.EVENT WHERE ACCNT_NO = EV.ACCNT_NO AND DEL_FG = 'N') GROUP BY EV.ACCNT_NO )
UNION ALL
/*[010] [공제 전송 기초 대상 데이터] 레디캐시 사용 대상자*/ SELECT DISTINCT TO_CHAR(SYSDATE,'YYYYMMDD') AS GDATE, TMD.GOODS_ID, 'C' AS CL, '00' AS GBIT, TO_CHAR(TMD.CREATE_DATE,'YYYYMMDD') AS WORK_DM, 0 AS PAY_NO, '' AS EXT_INFO, TO_CHAR((NVL(TMD.USE_AMT,0) - NVL(GAD.READY_CASH,0))) AS EXT_INFO2 FROM TB_MEMBER_DLWMALL TMD LEFT OUTER JOIN TB_GONGJE_ASIS_DATA GAD ON TMD.GOODS_ID = GAD.ACCNT_NO WHERE NVL(GAD.READY_CASH,0) < NVL(TMD.USE_AMT,0) AND EXISTS (SELECT ACCNT_NO FROM TB_MEMBER_BASIC_INFO_DAY WHERE ACCNT_NO = TMD.GOODS_ID AND KSTBIT = '02') UNION ALL
/*[011] [공제 전송 기초 대상 데이터] 레디캐시 사용 대상자*/ SELECT DISTINCT TO_CHAR(SYSDATE,'YYYYMMDD') AS GDATE, TMD.GOODS_ID, 'C_C' AS CL, '00' AS GBIT, TO_CHAR(TMD.CREATE_DATE,'YYYYMMDD') AS WORK_DM, 0 AS PAY_NO, '' AS EXT_INFO, TO_CHAR((NVL(GAD.READY_CASH,0) - NVL(TMD.USE_AMT,0))) AS EXT_INFO2 FROM TB_MEMBER_DLWMALL TMD LEFT OUTER JOIN TB_GONGJE_ASIS_DATA GAD ON TMD.GOODS_ID = GAD.ACCNT_NO WHERE NVL(GAD.READY_CASH,0) > NVL(TMD.USE_AMT,0) AND EXISTS (SELECT ACCNT_NO FROM TB_MEMBER_BASIC_INFO_DAY WHERE ACCNT_NO = TMD.GOODS_ID AND KSTBIT = '02') ) ;
COMMIT;
END;

mapper.select:
/* 해약 회원 데이터 조회 */ SELECT /* DlwDeductionMap.getGongjeRList */ '51' GUBUN, EXT.ACCNT_NO, (SELECT DED_NO FROM MEM_PROD_ACCNT WHERE ACCNT_NO = EXT.ACCNT_NO AND DEL_FG = 'N') AS DED_NO, MB.MEM_NM, EXT.SI_DATE AS PROC_DAY, (SELECT SUM(AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD AND NO <= TRUE_CNT) AS TRUE_AMT, (CASE WHEN RS.RESN_CL = '02' THEN 'R' ELSE 'E' END) AS RESN_CL, EXT.RESULT_KEY FROM TB_GONGJE_DAY_EXT EXT INNER JOIN LF_DMUSER.TB_MEMBER_BASIC_INFO_DAY MBID ON EXT.ACCNT_NO = MBID.ACCNT_NO INNER JOIN MEMBER MB ON MBID.MEM_NO = MB.MEM_NO AND MB.DEL_FG = 'N' LEFT OUTER JOIN RESCISSION RS ON RS.ACCNT_NO = EXT.ACCNT_NO AND RS.DEL_FG = 'N' WHERE 1=1 AND EXT.CL = 'R' AND GBIT = '00'
UNION ALL
/* 해약 취소된 회원 데이터 조회 */ SELECT '51' GUBUN, EXT.ACCNT_NO, (SELECT DED_NO FROM MEM_PROD_ACCNT WHERE ACCNT_NO = EXT.ACCNT_NO AND DEL_FG = 'N') AS DED_NO, MB.MEM_NM, EXT.SI_DATE AS PROC_DAY, (SELECT SUM(AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD AND NO <= TRUE_CNT) AS TRUE_AMT, 'C' AS RESN_CL, EXT.RESULT_KEY FROM TB_GONGJE_DAY_EXT EXT INNER JOIN LF_DMUSER.TB_MEMBER_BASIC_INFO_DAY MBID ON EXT.ACCNT_NO = MBID.ACCNT_NO INNER JOIN MEMBER MB ON MBID.MEM_NO = MB.MEM_NO AND MB.DEL_FG = 'N' LEFT OUTER JOIN RESCISSION RS ON RS.ACCNT_NO = EXT.ACCNT_NO AND RS.DEL_FG = 'N' WHERE 1=1 AND EXT.CL = 'R_C' AND GBIT = '00'

/* 양도양수 회원 데이터 조회 */ SELECT /* DlwDeductionMap.getGongjeTList */ DISTINCT '41' GUBUN, EXT.ACCNT_NO, (SELECT DED_NO FROM MEM_PROD_ACCNT WHERE ACCNT_NO = EXT.ACCNT_NO AND DEL_FG = 'N') AS DED_NO, MBA.MEM_NM, EXT.SI_DATE AS TRNS_DT, 'N' AS CHG_YN, /* 소비자번호 변경여부(변경:Y / 미변경:N) */ EXT.ACCNT_NO AS YS_ACCNT_NO, /* 양수소비자번호 */ MBB.MEM_NM AS YS_MEM_NM, /* 양수소비자성명 */
CASE WHEN LENGTH(NVL(MBB.IDN_NO,' ')) != 10 AND SUBSTR(MBB.BRTH_MON_DAY,1,2) = '19' AND MBB.SEX = '0001' THEN SUBSTR(MBB.BRTH_MON_DAY,3,6) || '1000000' WHEN LENGTH(NVL(MBB.IDN_NO,' ')) != 10 AND SUBSTR(MBB.BRTH_MON_DAY,1,2) = '19' AND MBB.SEX = '0002' THEN SUBSTR(MBB.BRTH_MON_DAY,3,6) || '2000000' WHEN LENGTH(NVL(MBB.IDN_NO,' ')) != 10 AND SUBSTR(MBB.BRTH_MON_DAY,1,2) = '20' AND MBB.SEX = '0001' THEN SUBSTR(MBB.BRTH_MON_DAY,3,6) || '3000000' WHEN LENGTH(NVL(MBB.IDN_NO,' ')) != 10 AND SUBSTR(MBB.BRTH_MON_DAY,1,2) = '20' AND MBB.SEX = '0002' THEN SUBSTR(MBB.BRTH_MON_DAY,3,6) || '4000000' WHEN LENGTH(NVL(MBB.IDN_NO,' ')) = 10 THEN MBB.IDN_NO ELSE '' END BRTH_MON_DAY ,
PD.PROD_NM, 'N' AS YN, /* 상품변경여부(변경:Y / 미변경:N) */ '' AS AMT, /* 총계약금액(변경시 필수) */ '' AS NUM, /* 총계약회차(변경시 필수) */ '' AS EXP_DT, /* 계약만기일자(변경시 필수) */ '' AS PAY_DD, /* 월납입예정일(변경시 필수) */ '' AS PAY_AMT, /* 월납입계약금액 */ TRUE_CNT AS TOT_NUM, (SELECT SUM(AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD AND NO <= TRUE_CNT) AS TOT_AMT, SUBSTR(SI_DATE,1,6) AS YYMM, /* 정산년월(납부시) */ SI_DATE AS PAY_DT, /* 납입일자(납부시) */ TRUE_CNT AS PAY_NUM, (SELECT SUM(AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD AND NO <= TRUE_CNT) AS PAY_AMT2, CASE WHEN NVL(MBB.HOME_ZIP,'N') != 'N' THEN MBB.HOME_ZIP ELSE MBB.WRPL_ZIP END POST, /* 우편번호 */ CASE WHEN NVL(MBB.HOME_ADDR,'N') != 'N' THEN MBB.HOME_ADDR ELSE MBB.WRPL_ADDR END ADDR1, /* 주소1 */ CASE WHEN NVL(MBB.HOME_ADDR2,'N') != 'N' THEN MBB.HOME_ADDR2 ELSE MBB.WRPL_ADDR2 END ADDR2, /* 주소2 */ CASE WHEN NVL(MBB.CELL,'N') != 'N' THEN MBB.CELL ELSE '010-0000-0000' END TEL, /* 연락처 */ MBB.EMAIL, EXT.RESULT_KEY FROM TB_GONGJE_DAY_EXT EXT INNER JOIN TRANSFER_BRKDN TR ON EXT.ACCNT_NO = TR.ACCNT_NO AND EXT.SI_DATE = HANOV_DT AND EXT.CL = 'T' LEFT OUTER JOIN MEMBER MBA ON TR.TRSFO_MEM_NO = MBA.MEM_NO LEFT OUTER JOIN MEMBER MBB ON TR.TRSFE_MEM_NO = MBB.MEM_NO LEFT OUTER JOIN TB_MEMBER_BASIC_INFO_DAY MBID ON EXT.ACCNT_NO = MBID.ACCNT_NO INNER JOIN PRODUCT PD ON MBID.PROD_CD = PD.PROD_CD WHERE 1=1 AND EXT.CL = 'T' AND GBIT = '00'

/* 행사 회원 데이터 조회 */ SELECT /* DlwDeductionMap.getGongjeEList */ '52' GUBUN, EXT.ACCNT_NO, (SELECT DED_NO FROM MEM_PROD_ACCNT WHERE ACCNT_NO = EXT.ACCNT_NO AND DEL_FG = 'N') AS DED_NO, MB.MEM_NM, EXT.SI_DATE AS EVENT_PROC_DAY, (SELECT SUM(AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD AND NO <= TRUE_CNT) AS PAY_AMT, 'E' TYPE, EXT.RESULT_KEY FROM TB_GONGJE_DAY_EXT EXT INNER JOIN TB_MEMBER_BASIC_INFO_DAY MBID ON EXT.ACCNT_NO = MBID.ACCNT_NO INNER JOIN MEMBER MB ON MBID.MEM_NO = MB.MEM_NO AND MB.DEL_FG = 'N' WHERE 1=1 AND EXT.CL = 'E' AND GBIT = '00'
UNION ALL
/* 행사 취소된 회원 데이터 조회 */ SELECT '52' GUBUN, EXT.ACCNT_NO, (SELECT DED_NO FROM MEM_PROD_ACCNT WHERE ACCNT_NO = EXT.ACCNT_NO AND DEL_FG = 'N') AS DED_NO, MB.MEM_NM, EXT.SI_DATE AS EVENT_PROC_DAY, (SELECT SUM(AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD AND NO <= TRUE_CNT) AS PAY_AMT, 'C' TYPE, EXT.RESULT_KEY FROM TB_GONGJE_DAY_EXT EXT INNER JOIN TB_MEMBER_BASIC_INFO_DAY MBID ON EXT.ACCNT_NO = MBID.ACCNT_NO INNER JOIN MEMBER MB ON MBID.MEM_NO = MB.MEM_NO AND MB.DEL_FG = 'N' WHERE 1=1 AND EXT.CL = 'E_C' AND GBIT = '00'

SELECT /* DlwDeductionMap.getGongjeExtDt */ EXT_DT, EXT_CHK, DECODE(RS_PROC_YN, 'Y', '처리', 'N', '미처리', '') AS RS_PROC_YN, TO_CHAR(REG_DM, 'YYYYMMDD') AS REG_DM, REG_MAN, TO_CHAR(UPD_DM, 'YYYYMMDD') AS UPD_DM, UPD_MAN FROM TB_GONGJE_EXT_SCHED WHERE 1=1
AND SUBSTR(EXT_DT, 1, 6) = #{ext_month}
AND EXT_CHK = #{ext_chk}
ORDER BY EXT_DT DESC

SELECT /* DlwDeductionMap.getGongjeDayExtSendHistCount */ COUNT(*) AS ICNT FROM ( SELECT ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) AS PAGE_INDX ,GDE.GDATE ,GDE.ACCNT_NO ,GDE.CL ,GDE.GBIT ,GDE.SI_DATE ,GDER.FILE_TYPE ,GDER.GUAR_NO ,GDER.WORK_RESULT ,GDER.ERR_MSG ,GDER.GUBUN ,GDER.CODE ,GDER.MEMB_NO ,GDER.LIMIT_VALUE ,GDER.SEQ_NO ,GDER.PAY_NO ,GDER.PAY_TYPE FROM LF_DMUSER.TB_GONGJE_DAY_EXT GDE LEFT OUTER JOIN LF_DMUSER.TB_GONGJE_DAY_EXT_RESULT GDER ON GDE.GDATE = GDER.GDATE AND GDE.ACCNT_NO = GDER.ACCNT_NO AND GDE.RESULT_KEY = GDER.RESULT_KEY WHERE 1=1 AND GDE.GDATE = #{srchDt}
AND GDE.CL = #{cl}
AND GDE.GBIT = #{gbit}
AND GDER.FILE_TYPE = #{file_type}
AND GDER.WORK_RESULT = #{work_result}
AND GDE.ACCNT_NO = #{accnt_no}
) MAIN_TBL WHERE 1=1

SELECT /* DlwDeductionMap.getGongjeDayExtSendHist */ MAIN_TBL.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) AS PAGE_INDX ,GDE.GDATE ,GDE.ACCNT_NO ,GDE.CL ,GDE.GBIT ,GDE.SI_DATE ,GDER.FILE_TYPE ,GDER.GUAR_NO ,GDER.WORK_RESULT ,GDER.ERR_MSG ,GDER.GUBUN ,GDER.CODE ,GDER.MEMB_NO ,GDER.LIMIT_VALUE ,GDER.SEQ_NO ,GDER.PAY_NO ,GDER.PAY_TYPE FROM LF_DMUSER.TB_GONGJE_DAY_EXT GDE LEFT OUTER JOIN LF_DMUSER.TB_GONGJE_DAY_EXT_RESULT GDER ON GDE.GDATE = GDER.GDATE AND GDE.ACCNT_NO = GDER.ACCNT_NO AND GDE.RESULT_KEY = GDER.RESULT_KEY WHERE 1=1 AND GDE.GDATE = #{srchDt}
AND GDE.CL = #{cl}
AND GDE.GBIT = #{gbit}
AND GDER.FILE_TYPE = #{file_type}
AND GDER.WORK_RESULT = #{work_result}
AND GDE.ACCNT_NO = #{accnt_no}
) MAIN_TBL WHERE 1=1
AND PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT /* DlwDeductionMap.getGongjeAsisDataCount */ COUNT(*) AS ICNT FROM ( SELECT ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) AS PAGE_INDX ,ACCNT_NO ,DED_NO ,KSTBIT ,GONGJE_CNT ,GONGJE_AMT FROM TB_GONGJE_ASIS_DATA WHERE 1=1
AND ACCNT_NO = #{accnt_no}
) MAIN_TBL WHERE 1=1

SELECT /* DlwDeductionMap.getGongjeAsisDataList */ MAIN_TBL.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) AS PAGE_INDX ,ACCNT_NO ,DED_NO ,KSTBIT ,GONGJE_CNT ,GONGJE_AMT ,NVL(READY_CASH, 0) AS READY_CASH FROM TB_GONGJE_ASIS_DATA WHERE 1=1
AND ACCNT_NO = #{accnt_no}
) MAIN_TBL WHERE 1=1
AND PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT /* DlwDeductionMap.getGongjeAsisDataList */ COUNT(*) AS SUMMARY_SUCCESS_CNT ,SUM(GONGJE_AMT)-SUM(READY_CASH) AS SUMMARY_AMT FROM ( SELECT ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) AS PAGE_INDX ,ACCNT_NO ,DED_NO ,KSTBIT ,GONGJE_CNT ,GONGJE_AMT ,NVL(READY_CASH, 0) AS READY_CASH FROM TB_GONGJE_ASIS_DATA WHERE 1=1 AND KSTBIT = '정상'
AND ACCNT_NO = #{accnt_no}
) MAIN_TBL WHERE 1=1

mapper.update:
UPDATE /* DlwDeductionMap.updateGongjeAsisDataList */
       LF_DMUSER.TB_GONGJE_ASIS_DATA
   SET DED_NO = #{ded_no}
      ,KSTBIT = #{kstbit}
      ,GONGJE_CNT = #{gongje_cnt}
      ,GONGJE_AMT = #{gongje_amt}
      ,READY_CASH = #{READY_CASH}
 WHERE 1=1
   AND ACCNT_NO = #{accnt_no}
