mapper.select:
SELECT MST.MST_SEQ , MST.NP_TITLE FROM TB_NONPAYMENT_MST MST WHERE 1=1 AND EXT_DT = #{ext_dt}
AND MST.NP_GBN = #{np_gbn}
ORDER BY NP_TITLE

SELECT COUNT(*)
  FROM LF_DMUSER.TB_NONPAYMENT_MNG_TMP TNM
 WHERE 1=1
   AND TNM.EXT_DT = #{ext_dt}
   AND EXISTS (
               SELECT 'Y'
                 FROM LF_DMUSER.TB_NONPAYMENT_DTL
                WHERE 1=1
                  AND ACCNT_NO = TNM.ACCNT_NO
                  AND NP_SEQ LIKE #{ext_dt}||'%'
                  AND MST_SEQ IN (
                                  SELECT MST_SEQ
                                    FROM LF_DMUSER.TB_NONPAYMENT_MST MST
                                   WHERE MST.EXT_DT = #{ext_dt}

                                     AND MST.NP_GBN = #{np_gbn}


                                     AND MST.MST_SEQ = #{mst_seq}

                                  )
              )

   AND TNM.ACCNT_NO = #{accnt_no}


   AND TNM.SECTION_T_NP = #{section_t_np}


   AND TNM.THMN_PYIN_AMT IS NOT NULL


   AND TNM.THMN_PYIN_AMT IS NULL

WITH NO_PAYMENT_CUST AS ( SELECT TNM.MEM_NO, TNM.ACCNT_NO , MB.MEM_NM , MB.CELL , MB.HOME_ADDR || ' ' || MB.HOME_ADDR2 AS ADDR , MB.BRTH_MON_DAY AS BRYR_MNDY , LF_DMUSER.FN_COM_NM('0123', MB.SEX) AS SEX , TNM.PROD_CD, PD.PROD_NM , TNM.NEW_CHAN_GUNSU, TNM.KSTBIT, TNM.PAY_MTHD, TNM.JOIN_DT , TNM.SECTION_T_NP , (SELECT DECODE(SPECIAL_BIT, '01', 'Y', '') FROM LF_DMUSER.TB_MEMBER_EXT_SPECIAL WHERE ACCNT_NO = TNM.ACCNT_NO AND DEL_FG = 'N' AND SPECIAL_BIT = '01') AS SPCL_YN , (TRUNC(MONTHS_BETWEEN(TO_CHAR(SYSDATE,'YYYYMM')||'01',SUBSTR(TNM.JOIN_DT,0,6) || '01')+1)) AS MNTS_CNT , (SELECT COUNT(NO) FROM LF_DMUSER.PRODUCT_NO_DATA PND WHERE PND.PROD_CD = TNM.PROD_CD AND PND.AMT > 0) AS TOT_TRUE_CNT /* 상조(전체) */ , (SELECT COUNT(NO) FROM LF_DMUSER.PRODUCT_NO_DATA PND WHERE PND.PROD_CD = TNM.PROD_CD AND PND.REL_AMT > 0) AS TOT_REL_CNT /* 결합(전체) */ , (SELECT COUNT(NO) FROM LF_DMUSER.PRODUCT_NO_DATA PND WHERE PND.PROD_CD = TNM.PROD_CD AND PND.ADD_AMT > 0) AS TOT_ADD_CNT /* 추가(전체) */ , TNM.DIFF_TRUE_CNT, TNM.DIFF_REL_CNT , TNM.TRUE_CNT AS TRUE_CNT, TNM.REL_CNT AS REL_CNT, TNM.ADD_CNT AS ADD_CNT , TNM.TRUE_DAY, TNM.REL_DAY , TNM.ADD_DAY , TNM.THMN_PYIN_AMT, TNM.THMN_PYIN_DAY , CASE WHEN PAY_MTHD = '04' THEN CM.ICHAE_DT ELSE CARD.PAY_DT END AS TRAN_DT , (SELECT ACCNT_NO FROM LF_DMUSER.TB_DEBT_REG WHERE ACCNT_NO = TNM.ACCNT_NO AND CANCEL_YN = 'N') AS DEBT_YN , (SELECT MAX(SPECIAL_ETC) FROM LF_DMUSER.TB_MEMBER_EXT_SPECIAL WHERE ACCNT_NO = TNM.ACCNT_NO AND DEL_FG = 'N') AS EXCD_RSN , (SELECT MAX(BANKR_GBN) FROM LF_DMUSER.TB_BANKRUPTCY_MNG WHERE ACCNT_NO = TNM.ACCNT_NO AND DEL_FG = 'N') AS BNRU_YN FROM LF_DMUSER.TB_NONPAYMENT_MNG_TMP TNM INNER JOIN LF_DMUSER.MEMBER MB ON TNM.MEM_NO = MB.MEM_NO AND MB.DEL_FG = 'N' INNER JOIN LF_DMUSER.PRODUCT PD ON TNM.PROD_CD = PD.PROD_CD LEFT OUTER JOIN LF_DMUSER.CARD_MEM_DAMO CARD ON TNM.ACCNT_NO = CARD.ACCNT_NO AND CARD.DEL_FG = 'N' LEFT OUTER JOIN LF_DMUSER.CMS_MEM CM ON TNM.ACCNT_NO = CM.ACCNT_NO AND CM.DEL_FG = 'N' WHERE 1=1
AND TNM.EXT_DT = #{ext_dt} AND EXISTS ( SELECT 'Y' FROM LF_DMUSER.TB_NONPAYMENT_DTL WHERE 1=1 AND ACCNT_NO = TNM.ACCNT_NO AND NP_SEQ LIKE #{ext_dt}||'%' AND MST_SEQ IN ( SELECT MST_SEQ FROM LF_DMUSER.TB_NONPAYMENT_MST MST WHERE MST.EXT_DT = #{ext_dt}
AND MST.NP_GBN = #{np_gbn}
AND MST.MST_SEQ = #{mst_seq}
) )
AND TNM.ACCNT_NO = #{accnt_no}
AND TNM.SECTION_T_NP = #{section_t_np}
AND TNM.THMN_PYIN_AMT IS NOT NULL
AND TNM.THMN_PYIN_AMT IS NULL
) SELECT MNG.PAGE_INDX , MNG.ACCNT_NO /* 회원번호 */ , MNG.MEM_NO /* 고유번호 */ , MNG.MEM_NM /* 성명 */ , MNG.CELL /* 연락처 */ , ((MNG.MON_TRUE_AMT - MNG.INTL_TRUE_AMT) + (MNG.MON_REL_AMT - MNG.INTL_REL_AMT) + (MNG.MON_ADD_AMT - MNG.INTL_ADD_AMT)) AS NOPAY_AMT /* 미납금액(분배액) */ , MNG.ADDR /* 주소 */ , MNG.PROD_NM /* 상품명 */ , (MNG.THMN_TRUE_CNT - MNG.TRUE_CNT ) AS DIFF_TRUE_CNT /* 미납(상조) */ , (MNG.THMN_REL_CNT - MNG.REL_CNT ) AS DIFF_REL_CNT /* 미납(결합) */ , MNG.JOIN_DT /* 가입일자 */ , MNG.KSTBIT_NM /* 구분2 */ , (MNG.MON_TRUE_AMT - MNG.INTL_TRUE_AMT) AS TRUE_AMT /* 상조금액 */ , (MNG.MON_REL_AMT - MNG.INTL_REL_AMT) AS REL_AMT /* 결합금액 */ , (MNG.MON_ADD_AMT - MNG.INTL_ADD_AMT) AS ADD_AMT /* 추가금액 */ , MNG.LAST_MON_DAY AS LAST_MON_DAY /* 최종납일일자 */ , MNG.TRAN_DT /* 이체일자 */ , MNG.BRYR_MNDY /* 생년월일 */ , MNG.SEX /* 성별 */ , MNG.THMN_PYIN_AMT AS CUR_MON_PAY_AMT /* 당월납입금액 */ , MNG.THMN_PYIN_DAY AS CUR_MON_DAY /* 당월납입일자 */ , MNG.EXCD_RSN /* 제외사유 */ , MNG.DEBT_NNFL_YN /* 채무불이행 */ , MNG.BNRU_YN /* 회생파산 */ FROM ( SELECT A.PAGE_INDX , A.ACCNT_NO , A.MEM_NO, A.MEM_NM, A.CELL , A.ADDR, A.PROD_NM , A.DIFF_TRUE_CNT, A.DIFF_REL_CNT , A.THMN_TRUE_CNT , A.THMN_REL_CNT , A.JOIN_DT , A.KSTBIT_NM , NVL(A.ADD_DAY, NVL(REL_DAY, NVL(TRUE_DAY, ''))) AS LAST_MON_DAY , A.TRAN_DT , A.BRYR_MNDY , A.SEX , A.THMN_PYIN_AMT, A.THMN_PYIN_DAY , A.EXCD_RSN , CASE WHEN A.DEBT_YN IS NULL THEN '' ELSE '채무불이행등재' END AS DEBT_NNFL_YN , DECODE(A.BNRU_YN,'B','파산','R','회생') AS BNRU_YN , A.SECTION_T_NP, A.SPCL_YN , A.TRUE_CNT, A.REL_CNT, A.ADD_CNT , NVL((SELECT SUM(AMT) FROM LF_DMUSER.PRODUCT_NO_DATA WHERE PROD_CD = A.PROD_CD AND NO <= A.MNTS_TRUE_RNTM), 0) AS MON_TRUE_AMT /* 상조금액(월) */ , NVL((SELECT SUM(REL_AMT) FROM LF_DMUSER.PRODUCT_NO_DATA WHERE PROD_CD = A.PROD_CD AND NO <= A.MNTS_REL_RNTM), 0) AS MON_REL_AMT /* 결합금액(월) */ , NVL((SELECT SUM(ADD_AMT) FROM LF_DMUSER.PRODUCT_NO_DATA WHERE PROD_CD = A.PROD_CD AND NO <= A.MNTS_ADD_RNTM), 0) AS MON_ADD_AMT /* 추가금액(월) */ , NVL((SELECT SUM(AMT) FROM LF_DMUSER.PRODUCT_NO_DATA WHERE PROD_CD = A.PROD_CD AND NO <= A.TRUE_CNT), 0) AS INTL_TRUE_AMT /* 상조금액(초기) */ , NVL((SELECT SUM(REL_AMT) FROM LF_DMUSER.PRODUCT_NO_DATA WHERE PROD_CD = A.PROD_CD AND NO <= A.REL_CNT), 0) AS INTL_REL_AMT /* 결합금액(초기) */ , NVL((SELECT SUM(ADD_AMT) FROM LF_DMUSER.PRODUCT_NO_DATA WHERE PROD_CD = A.PROD_CD AND NO <= A.ADD_CNT), 0) AS INTL_ADD_AMT /* 추가금액(초기) */ FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEM_NO ASC) AS PAGE_INDX, ACCNT_NO , MEM_NO, MEM_NM, CELL , ADDR, PROD_NM , (CASE WHEN KSTBIT = '03' THEN TRUE_CNT ELSE CASE WHEN (TOT_TRUE_CNT - NEW_CHAN_GUNSU) > MNTS_CNT THEN MNTS_CNT ELSE (TOT_TRUE_CNT - NEW_CHAN_GUNSU) END END ) AS THMN_TRUE_CNT , (CASE WHEN MNTS_CNT >= TOT_REL_CNT THEN TOT_REL_CNT ELSE MNTS_CNT END) AS THMN_REL_CNT , DIFF_TRUE_CNT, DIFF_REL_CNT -- 미납회차(상조/결합) , JOIN_DT , DECODE(KSTBIT,'01','접수','02','정상','03','해약','행사') AS KSTBIT_NM , TRUE_DAY, REL_DAY, ADD_DAY -- 납입정보(일자=>상조/결합/추가) , TRAN_DT , BRYR_MNDY, SEX , THMN_PYIN_AMT, THMN_PYIN_DAY -- 당월납입정보(당월금액/당월입금일자) , DEBT_YN, EXCD_RSN, BNRU_YN , PROD_CD , SECTION_T_NP, SPCL_YN , TRUE_CNT, REL_CNT, ADD_CNT -- 납입정보(회수=>상조/결합/추가) , CASE WHEN SECTION_T_NP = '001' THEN '비채권' WHEN SECTION_T_NP = '002' THEN '채권' ELSE SECTION_T_NP END SECTION_T_NP_NM , (CASE WHEN KSTBIT = '03' THEN TRUE_CNT ELSE CASE WHEN (TOT_TRUE_CNT - NEW_CHAN_GUNSU) > MNTS_CNT THEN MNTS_CNT ELSE (TOT_TRUE_CNT - NEW_CHAN_GUNSU) END END) AS MNTS_TRUE_RNTM -- 당월정보(상조) , (CASE WHEN MNTS_CNT >= TOT_REL_CNT THEN TOT_REL_CNT ELSE MNTS_CNT END) AS MNTS_REL_RNTM -- 당월정보(결합) , (CASE WHEN MNTS_CNT >= TOT_ADD_CNT THEN TOT_ADD_CNT ELSE MNTS_CNT END) AS MNTS_ADD_RNTM -- 당월정보(추가) FROM NO_PAYMENT_CUST ) A ) MNG WHERE 1=1
AND PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

