/* DlwItoMngMap.getDebtListCount */ WITH DEBT_TB AS ( SELECT DEBT_DT, MEM_NO, ACCNT_NO, MEM_NM, CELL1 AS CELL, DEBT_TOT_AMT, DEBT_YEN_CNT, DEBT_REL_AMT, (DEBT_TOT_AMT - DEBT_REL_AMT) AS DEBT_DIFF_AMT, (REAL_MONTH_COUNT - REL_CNT) AS REAL_YEN_CNT, REAL_REL_AMT, (REAL_TOT_AMT - REAL_REL_AMT) AS REAL_DIFF_AMT, SAFE_KEY, CANCEL_YN, CANCEL_DT, REL_DAY, CASE WHEN DEBT_REL_CNT >= REL_CNT THEN 'N' ELSE 'Y' END AS PAY_YN FROM ( SELECT DR.DEBT_DT, DR.ACCNT_NO, MB.MEM_NM, MB.CELL1, MB.MEM_NO, (SELECT SUM(REL_AMT) + SUM(ADD_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD AND NO <= DR.MONTH_COUNT) AS DEBT_TOT_AMT, DR.REL_CNT AS DEBT_REL_CNT, (SELECT SUM(REL_AMT) + SUM(ADD_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD AND NO <= DR.REL_CNT ) AS DEBT_REL_AMT, (SELECT SUM(REL_AMT) + SUM(ADD_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD AND NO <= MBID.REL_CNT) AS REAL_REL_AMT, DR.SAFE_KEY, CANCEL_YN, CANCEL_DT,
CASE WHEN MBID.MONTH_COUNT >= TOT_REL_CNT THEN TOT_REL_CNT ELSE MBID.MONTH_COUNT END AS REAL_MONTH_COUNT,
CASE WHEN MBID.MONTH_COUNT >= TOT_REL_CNT THEN (SELECT SUM(REL_AMT) + SUM(ADD_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD) ELSE (SELECT SUM(REL_AMT) + SUM(ADD_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD AND NO <= MBID.MONTH_COUNT) END AS REAL_TOT_AMT,
MBID.REL_CNT, (DR.MONTH_COUNT - DR.REL_CNT) AS DEBT_YEN_CNT, MBID.REL_DAY FROM TB_DEBT_REG DR INNER JOIN TB_MEMBER_BASIC_INFO_DAY MBID ON DR.ACCNT_NO = MBID.ACCNT_NO INNER JOIN MEMBER MB ON MBID.MEM_NO = MB.MEM_NO AND MB.DEL_FG = 'N' WHERE 1=1
AND DEBT_DT = #{debt_dt}
AND DR.ACCNT_NO = #{accnt_no}
AND CANCEL_YN = #{cancel_yn}
) )
SELECT COUNT(*) FROM DEBT_TB WHERE 1=1
AND PAY_YN = #{pay_yn}

/* DlwItoMngMap.getDebtList */ WITH DEBT_TB AS ( SELECT ROW_NUMBER() OVER(ORDER BY DEBT_DT ASC, ACCNT_NO ASC) AS PAGE_INDX, DEBT_DT, MEM_NO, ACCNT_NO, MEM_NM, CELL1 AS CELL, DEBT_TOT_AMT, DEBT_YEN_CNT, DEBT_REL_AMT, (DEBT_TOT_AMT - DEBT_REL_AMT) AS DEBT_DIFF_AMT, (REAL_MONTH_COUNT - REL_CNT) AS REAL_YEN_CNT, REAL_REL_AMT, (REAL_TOT_AMT - REAL_REL_AMT) AS REAL_DIFF_AMT, SAFE_KEY, CANCEL_YN, CANCEL_DT, REL_DAY, CASE WHEN DEBT_REL_CNT >= REL_CNT THEN 'N' ELSE 'Y' END AS PAY_YN, MST_SEQ FROM ( SELECT DR.DEBT_DT, DR.ACCNT_NO, MB.MEM_NM, MB.CELL1, MB.MEM_NO, (SELECT SUM(REL_AMT) + SUM(ADD_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD AND NO <= DR.MONTH_COUNT) AS DEBT_TOT_AMT, DR.REL_CNT AS DEBT_REL_CNT, (SELECT SUM(REL_AMT) + SUM(ADD_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD AND NO <= DR.REL_CNT ) AS DEBT_REL_AMT, (SELECT SUM(REL_AMT) + SUM(ADD_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD AND NO <= MBID.REL_CNT) AS REAL_REL_AMT, DR.SAFE_KEY, CANCEL_YN, CANCEL_DT,
CASE WHEN MBID.MONTH_COUNT >= TOT_REL_CNT THEN TOT_REL_CNT ELSE MBID.MONTH_COUNT END AS REAL_MONTH_COUNT,
CASE WHEN MBID.MONTH_COUNT >= TOT_REL_CNT THEN (SELECT SUM(REL_AMT) + SUM(ADD_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD) ELSE (SELECT SUM(REL_AMT) + SUM(ADD_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MBID.PROD_CD AND NO <= MBID.MONTH_COUNT) END AS REAL_TOT_AMT,
MBID.REL_CNT, (DR.MONTH_COUNT - DR.REL_CNT) AS DEBT_YEN_CNT, MBID.REL_DAY, DR.MST_SEQ FROM TB_DEBT_REG DR INNER JOIN TB_MEMBER_BASIC_INFO_DAY MBID ON DR.ACCNT_NO = MBID.ACCNT_NO INNER JOIN MEMBER MB ON MBID.MEM_NO = MB.MEM_NO AND MB.DEL_FG = 'N' WHERE 1=1
AND DR.DEBT_DT = #{debt_dt}
AND DR.ACCNT_NO = #{accnt_no}
AND DR.CANCEL_YN = #{cancel_yn}
) )
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY DEBT_DT ASC, ACCNT_NO ASC) AS PAGE_INDX, DEBT_DT, MEM_NO, ACCNT_NO, MEM_NM, CELL, DEBT_TOT_AMT, DEBT_YEN_CNT, DEBT_REL_AMT, DEBT_DIFF_AMT, REAL_YEN_CNT, REAL_REL_AMT, REAL_DIFF_AMT, SAFE_KEY, CANCEL_YN, CANCEL_DT, REL_DAY, PAY_YN, MST_SEQ FROM DEBT_TB TB WHERE 1=1
AND PAY_YN = #{pay_yn}
) TBL WHERE 1=1
AND PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

