WITH NO_PAYMENT_CUST AS ( SELECT /* DlwItoMngMap.getNoPaymentCustListCount */ TNM.MEM_NO, TNM.ACCNT_NO, TNM.PROD_CD, TNM.NEW_CHAN_GUNSU, TNM.KSTBIT, TNM.PAY_MTHD, TNM.JOIN_DT , TNM.SECTION_T_NP , (SELECT DECODE(SPECIAL_BIT, '01', 'Y', '') FROM LF_DMUSER.TB_MEMBER_EXT_SPECIAL WHERE ACCNT_NO = TNM.ACCNT_NO AND DEL_FG = 'N' AND SPECIAL_BIT = '01') AS SPCL_YN , (TRUNC(MONTHS_BETWEEN(TO_CHAR(SYSDATE,'YYYYMM')||'01',SUBSTR(TNM.JOIN_DT,0,6) || '01')+1)) AS MNTS_CNT , (SELECT COUNT(NO) FROM LF_DMUSER.PRODUCT_NO_DATA PND WHERE PND.PROD_CD = TNM.PROD_CD AND PND.AMT > 0) AS TOT_TRUE_CNT /* 상조(전체) */ , (SELECT COUNT(NO) FROM LF_DMUSER.PRODUCT_NO_DATA PND WHERE PND.PROD_CD = TNM.PROD_CD AND PND.REL_AMT > 0) AS TOT_REL_CNT /* 결합(전체) */ , (SELECT COUNT(NO) FROM LF_DMUSER.PRODUCT_NO_DATA PND WHERE PND.PROD_CD = TNM.PROD_CD AND PND.ADD_AMT > 0) AS TOT_ADD_CNT /* 추가(전체) */ , TNM.DIFF_TRUE_CNT, TNM.DIFF_REL_CNT , TNM.TRUE_CNT AS TRUE_CNT, TNM.REL_CNT AS REL_CNT, TNM.ADD_CNT AS ADD_CNT , TNM.TRUE_DAY, TNM.REL_DAY , TNM.ADD_DAY , TNM.THMN_PYIN_AMT, TNM.THMN_PYIN_DAY , TCA.CNSR_ID, TCA.CNSR_NM FROM LF_DMUSER.TB_NONPAYMENT_MNG_TMP TNM LEFT OUTER JOIN (SELECT A.CNSR_ID, B.USER_NM AS CNSR_NM, A.SUB_TRGT_LIST_ID, A.TRGT_CUST_DTPT_ID FROM PS_WILLVI.TB_TRGT_CUST_ALCT A INNER JOIN PS_WILLVI.TB_USER B ON (B.CNTR_CD = A.CNTR_CD AND B.USER_ID = A.CNSR_ID) WHERE 1=1 AND A.TRGT_LIST_ID = 'TAR202212267976435' AND A.SUB_TRGT_LIST_ID IN (SELECT 'STLNP' || EXT_DT || SUBSTR('0000000'|| MST_SEQ,-7) FROM LF_DMUSER.TB_NONPAYMENT_MST WHERE 1=1 AND EXT_DT = #{ext_dt}) ) TCA ON (TCA.SUB_TRGT_LIST_ID = TNM.SUB_TRGT_LIST_ID AND TCA.TRGT_CUST_DTPT_ID = TNM.TRGT_CUST_DTPT_ID) WHERE TNM.EXT_DT = #{ext_dt}
