SELECT MNG.PAGE_INDX , MNG.MEM_NO AS MEM_NO /* 고유번호 */ , (SELECT MEM_NM FROM LF_DMUSER.MEMBER_DAMO WHERE MEM_NO = MNG.MEM_NO AND DEL_FG = 'N') AS MEM_NM /* 성명 */ , (SELECT REPLACE(CELL, '-', '') FROM LF_DMUSER.MEMBER_DAMO WHERE MEM_NO = MNG.MEM_NO AND DEL_FG = 'N') AS CELL /* 연락처 */ , MNG.ACCNT_NO AS ACCNT_NO /* 회원번호 */ , MNG.PAY_MTHD_NM AS PAY_MTHD_NM /* 납입방법(명) */ , MNG.SECTION_T_NP_NM AS SECTION_T_NP_NM /* 구분정보(채권/비채권) */ , MNG.LAST_MON_DAY AS LAST_MON_DAY /* 최종납일일자 */ , MNG.DIFF_TRUE_CNT, MNG.DIFF_REL_CNT , ((MNG.MON_TRUE_AMT - MNG.INTL_TRUE_AMT) + (MNG.MON_REL_AMT - MNG.INTL_REL_AMT) + (MNG.MON_ADD_AMT - MNG.INTL_ADD_AMT)) AS NOPAY_AMT /* 미납금액(분배액) */ , (MNG.MON_TRUE_AMT - MNG.INTL_TRUE_AMT) AS TRUE_AMT /* 상조금액 */ , (MNG.MON_REL_AMT - MNG.INTL_REL_AMT) AS REL_AMT /* 결합금액 */ , (MNG.MON_ADD_AMT - MNG.INTL_ADD_AMT) AS ADD_AMT /* 추가금액 */ , MNG.THMN_PYIN_AMT AS CUR_MON_PAY_AMT /* 당월납입금액 */ , MNG.THMN_PYIN_DAY AS CUR_MON_DAY /* 당월납입일자 */ , MNG.SPCL_YN AS SPCL_YN /* 임의지정회원여부 */ , MNG.CNSR_ID AS CNSR_ID /* 상담사ID */ , MNG.CNSR_NM AS CNSR_NM /* 상담사명 */ FROM ( SELECT ROW_NUMBER() OVER(ORDER BY A.MEM_NO ASC) AS PAGE_INDX , A.MEM_NO, A.ACCNT_NO, A.PAY_MTHD_NM, A.SECTION_T_NP, A.SECTION_T_NP_NM , A.SPCL_YN , A.MNTS_TRUE_RNTM, A.MNTS_REL_RNTM, A.MNTS_ADD_RNTM , A.TRUE_CNT, A.REL_CNT, A.ADD_CNT , A.DIFF_TRUE_CNT, A.DIFF_REL_CNT , NVL(A.ADD_DAY, NVL(REL_DAY, NVL(TRUE_DAY, ''))) AS LAST_MON_DAY , NVL((SELECT SUM(AMT) FROM LF_DMUSER.PRODUCT_NO_DATA WHERE PROD_CD = A.PROD_CD AND NO <= A.MNTS_TRUE_RNTM), 0) AS MON_TRUE_AMT /* 상조금액(월) */ , NVL((SELECT SUM(REL_AMT) FROM LF_DMUSER.PRODUCT_NO_DATA WHERE PROD_CD = A.PROD_CD AND NO <= A.MNTS_REL_RNTM), 0) AS MON_REL_AMT /* 결합금액(월) */ , NVL((SELECT SUM(ADD_AMT) FROM LF_DMUSER.PRODUCT_NO_DATA WHERE PROD_CD = A.PROD_CD AND NO <= A.MNTS_ADD_RNTM), 0) AS MON_ADD_AMT /* 추가금액(월) */ , NVL((SELECT SUM(AMT) FROM LF_DMUSER.PRODUCT_NO_DATA WHERE PROD_CD = A.PROD_CD AND NO <= A.TRUE_CNT), 0) AS INTL_TRUE_AMT /* 상조금액(초기) */ , NVL((SELECT SUM(REL_AMT) FROM LF_DMUSER.PRODUCT_NO_DATA WHERE PROD_CD = A.PROD_CD AND NO <= A.REL_CNT), 0) AS INTL_REL_AMT /* 결합금액(초기) */ , NVL((SELECT SUM(ADD_AMT) FROM LF_DMUSER.PRODUCT_NO_DATA WHERE PROD_CD = A.PROD_CD AND NO <= A.ADD_CNT), 0) AS INTL_ADD_AMT /* 추가금액(초기) */ , A.THMN_PYIN_AMT, A.THMN_PYIN_DAY , A.CNSR_ID, A.CNSR_NM FROM ( SELECT MEM_NO, ACCNT_NO, PROD_CD, SECTION_T_NP, SPCL_YN , MNTS_CNT , CASE WHEN SECTION_T_NP = '001' THEN '비채권' WHEN SECTION_T_NP = '002' THEN '채권' ELSE SECTION_T_NP END SECTION_T_NP_NM , CASE WHEN PAY_MTHD = '01' THEN '손수' WHEN PAY_MTHD = '04' THEN 'CMS' WHEN PAY_MTHD = '06' THEN '카드' ELSE PAY_MTHD END PAY_MTHD_NM , DIFF_TRUE_CNT, DIFF_REL_CNT /* 미납회차(상조/결합) */ , TRUE_CNT, REL_CNT, ADD_CNT /* 납입정보(회수=>상조/결합/추가) */ , TRUE_DAY, REL_DAY, ADD_DAY /* 납입정보(일자=>상조/결합/추가) */ , (CASE WHEN KSTBIT = '03' THEN TRUE_CNT ELSE CASE WHEN (TOT_TRUE_CNT - NEW_CHAN_GUNSU) > MNTS_CNT THEN MNTS_CNT ELSE (TOT_TRUE_CNT - NEW_CHAN_GUNSU) END END) AS MNTS_TRUE_RNTM -- 당월정보(상조) , (CASE WHEN MNTS_CNT >= TOT_REL_CNT THEN TOT_REL_CNT ELSE MNTS_CNT END) AS MNTS_REL_RNTM -- 당월정보(결합) , (CASE WHEN MNTS_CNT >= TOT_ADD_CNT THEN TOT_ADD_CNT ELSE MNTS_CNT END) AS MNTS_ADD_RNTM -- 당월정보(추가) , THMN_PYIN_AMT, THMN_PYIN_DAY /* 당월납입정보(당월금액/당월입금일자) */ , CNSR_ID, CNSR_NM FROM NO_PAYMENT_CUST ) A ) MNG WHERE 1=1
AND PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}
ORDER BY PAGE_INDX
