mapper.select:
SELECT /* ExamBasiMap.getExamBasiList */ B.* FROM (SELECT ROWNUM AS PAGE_INDX, EXAM_ID, EMSH_ID, EXAM_NM, EXAM_EXPI , PRBM_ARNM_CD, EXAM_PRGR_STAT_CD, EXAM_STT_DTTM, EXAM_END_DTTM , CNTR_CD, RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM , PRBM_ARNM_NM, EXAM_PRGR_STAT_NM, EMSH_NM, EXAM_TRPR_CNT , RGSR_NM, AMND_NM FROM (SELECT EXAM_ID, EMSH_ID, EXAM_NM, EXAM_EXPI, PRBM_ARNM_CD , EXAM_PRGR_STAT_CD, TO_CHAR(EXAM_STT_DTTM, 'YYYYMMDDHH24MI') AS EXAM_STT_DTTM, TO_CHAR(EXAM_END_DTTM, 'YYYYMMDDHH24MI') AS EXAM_END_DTTM , CNTR_CD, RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM , GETCODENM('EXM050', PRBM_ARNM_CD, A.CNTR_CD) AS PRBM_ARNM_NM , GETCODENM('EXM060', EXAM_PRGR_STAT_CD, A.CNTR_CD) AS EXAM_PRGR_STAT_NM , (SELECT EMSH_NM FROM TB_EMSH WHERE EMSH_ID = A.EMSH_ID) AS EMSH_NM , (SELECT COUNT(*) FROM TB_EXAM_TRPR WHERE EXAM_ID = A.EXAM_ID) AS EXAM_TRPR_CNT , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.RGSR_ID AND CNTR_CD = A.CNTR_CD) AS RGSR_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.AMND_ID AND CNTR_CD = A.CNTR_CD) AS AMND_NM FROM TB_EXAM_BASI A WHERE 1=1
AND A.BZPT_DIV = #{bzpt_div}
ORDER BY ${orderBy} ${orderDirection}
) ) B
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

mapper.update:
UPDATE /* ExamBasiMap.updateExamBasi */
       TB_EXAM_BASI A
   SET A.AMND_ID      = #{amnd_id}
     , A.AMNT_DTTM          = TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')

     , A.EMSH_ID    = #{emsh_id}


     , A.EXAM_NM         = #{exam_nm}


     , A.EXAM_EXPI       = #{exam_expi}


     , A.PRBM_ARNM_CD    = #{prbm_arnm_cd}


     , A.EXAM_PRGR_STAT_CD   = #{exam_prgr_stat_cd_change}


     , A.EXAM_STT_DTTM        = #{exam_stt_dttm}


     , A.EXAM_END_DTTM          = #{exam_end_dttm}


 WHERE A.EXAM_ID         = #{exam_id}

mapper.delete:
DELETE /* ExamBasiMap.deleteExamBasi */
  FROM TB_EXAM_BASI
 WHERE EXAM_ID = #{exam_id}

mapper.select:
SELECT /* ExamBasiMap.getExamBasiMyPageList */ B.* FROM (SELECT ROWNUM AS PAGE_INDX, EXAM_ID, EMSH_ID, EXAM_NM, EXAM_EXPI , PRBM_ARNM_CD, EXAM_PRGR_STAT_CD, EXAM_STT_DTTM, EXAM_END_DTTM, EXAM_TRPR_ID , LAST_SCR_VL, EXAM_RNKN_SQNO, EXAM_PRGR_STAT_NM, EMSH_NM, EXAM_TRPR_NM , ANSR_YN, EXAM_TRPR_CNT, EXAM_PRBM_CNT, EXAM_ANSR_CNT, TODAY FROM (SELECT A.EXAM_ID, A.EMSH_ID, A.EXAM_NM, A.EXAM_EXPI , A.PRBM_ARNM_CD, A.EXAM_PRGR_STAT_CD, TO_CHAR(A.EXAM_STT_DTTM, 'YYYYMMDDHH24MI') AS EXAM_STT_DTTM, TO_CHAR(A.EXAM_END_DTTM, 'YYYYMMDDHH24MI') AS EXAM_END_DTTM , B.EXAM_TRPR_ID, B.LAST_SCR_VL, B.EXAM_RNKN_SQNO , GETCODENM('EXM050', PRBM_ARNM_CD, A.CNTR_CD) AS PRBM_ARNM_NM , GETCODENM('EXM060', EXAM_PRGR_STAT_CD, A.CNTR_CD) AS EXAM_PRGR_STAT_NM , (SELECT EMSH_NM FROM TB_EMSH WHERE EMSH_ID = A.EMSH_ID) AS EMSH_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = B.EXAM_TRPR_ID) AS EXAM_TRPR_NM , (CASE WHEN A.EXAM_PRGR_STAT_CD = '20' AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MI') BETWEEN A.EXAM_STT_DTTM AND A.EXAM_END_DTTM THEN 'Y' ELSE 'N' END) AS ANSR_YN , (SELECT COUNT(*) FROM TB_EXAM_TRPR WHERE EXAM_ID = A.EXAM_ID) AS EXAM_TRPR_CNT , (SELECT COUNT(*) FROM TB_EXAM_PRBM WHERE EMSH_ID = A.EMSH_ID) AS EXAM_PRBM_CNT , (SELECT COUNT(*) FROM TB_EXAM_PRBM_ANSR WHERE EXAM_ID = A.EXAM_ID AND EMSH_ID = A.EMSH_ID AND EXAM_TRPR_ID = B.EXAM_TRPR_ID) AS EXAM_ANSR_CNT , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') AS TODAY FROM TB_EXAM_BASI A JOIN TB_EXAM_TRPR B ON A.EXAM_ID = B.EXAM_ID AND B.EXAM_TRPR_ID = #{exam_trpr_id} WHERE 1=1
ORDER BY ${orderBy} ${orderDirection}
) ) B
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT /* ExamBasiMap.getExamResultList */ PAGE_INDX, EXAM_ID, EMSH_ID, EXAM_NM, EXAM_EXPI , PRBM_ARNM_CD, EXAM_PRGR_STAT_CD, EXAM_STT_DTTM, EXAM_END_DTTM , PRBM_ARNM_NM, EXAM_PRGR_STAT_NM, EXAM_TRPR_CNT, EXAM_ANSR_TRPR_CNT, EXAM_PRBM_DTSC_VL , LAST_SCR_VL, EXAM_PRBM_CNT, TODAY, EXAM_TRPR_AVG, EXAM_ANSR_TRPR_AVG , EXAM_MARKING_TRPR_AVG, CHART_EXAM_NM FROM (SELECT ROWNUM AS PAGE_INDX, EXAM_ID, EMSH_ID, EXAM_NM, EXAM_EXPI , PRBM_ARNM_CD, EXAM_PRGR_STAT_CD, EXAM_STT_DTTM, EXAM_END_DTTM , PRBM_ARNM_NM, EXAM_PRGR_STAT_NM, EXAM_TRPR_CNT, EXAM_ANSR_TRPR_CNT, EXAM_PRBM_DTSC_VL , LAST_SCR_VL, EXAM_PRBM_CNT, TODAY , (CASE WHEN LAST_SCR_VL > 0 AND EXAM_ANSR_TRPR_CNT > 0 THEN ROUND(LAST_SCR_VL / EXAM_ANSR_TRPR_CNT, 1) ELSE 0 END) AS EXAM_TRPR_AVG , (CASE WHEN LAST_SCR_VL > 0 AND EXAM_ANSR_TRPR_CNT > 0 THEN ROUND(LAST_SCR_VL / EXAM_ANSR_TRPR_CNT, 1) ELSE 0 END) AS EXAM_ANSR_TRPR_AVG , (CASE WHEN LAST_SCR_VL > 0 AND EXAM_MARKING_TRPR_CNT > 0 THEN ROUND(LAST_SCR_VL / EXAM_MARKING_TRPR_CNT, 1) ELSE 0 END) AS EXAM_MARKING_TRPR_AVG , EXAM_NM || '(' || TO_CHAR(TO_DATE(EXAM_STT_DTTM, 'YYYYMMDDHH24MI'), 'MM') || '월)' AS CHART_EXAM_NM FROM (SELECT A.EXAM_ID, A.EMSH_ID, A.EXAM_NM, A.EXAM_EXPI , A.PRBM_ARNM_CD, A.EXAM_PRGR_STAT_CD, TO_CHAR(A.EXAM_STT_DTTM, 'YYYYMMDDHH24MI') AS EXAM_STT_DTTM, TO_CHAR(A.EXAM_END_DTTM, 'YYYYMMDDHH24MI') AS EXAM_END_DTTM , GETCODENM('EXM050', PRBM_ARNM_CD, A.CNTR_CD) AS PRBM_ARNM_NM , GETCODENM('EXM060', EXAM_PRGR_STAT_CD, A.CNTR_CD) AS EXAM_PRGR_STAT_NM , (SELECT COUNT(*) FROM TB_EXAM_TRPR B WHERE B.EXAM_ID = A.EXAM_ID
) AS EXAM_TRPR_CNT , (SELECT COUNT(*) FROM TB_EXAM_TRPR B WHERE B.EXAM_ID = A.EXAM_ID
AND EXISTS (SELECT 'Y' FROM TB_EXAM_PRBM_ANSR C WHERE C.EXAM_ID = B.EXAM_ID AND C.EXAM_TRPR_ID = B.EXAM_TRPR_ID AND C.PRBM_ANSR_CNTN IS NOT NULL)) AS EXAM_ANSR_TRPR_CNT , (SELECT COUNT(*) FROM TB_EXAM_TRPR B WHERE B.EXAM_ID = A.EXAM_ID AND B.LAST_SCR_VL IS NOT NULL
) AS EXAM_MARKING_TRPR_CNT , (SELECT NVL(SUM(B.EXAM_PRBM_DTSC_VL), 0) FROM TB_EXAM_PRBM B WHERE B.EMSH_ID = A.EMSH_ID) AS EXAM_PRBM_DTSC_VL , (SELECT SUM(LAST_SCR_VL) FROM TB_EXAM_TRPR B WHERE B.EXAM_ID = A.EXAM_ID
) AS LAST_SCR_VL , (SELECT COUNT(*) FROM TB_EXAM_PRBM B WHERE B.EMSH_ID = A.EMSH_ID) AS EXAM_PRBM_CNT , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') AS TODAY FROM TB_EXAM_BASI A WHERE 1=1
AND A.BZPT_DIV = #{bzpt_div}
ORDER BY ${orderBy} ${orderDirection}
) )
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT /* ExamBasiMap.getExamResultScoreList */ EXAM_ID, EXAM_NM, TRPR_CNT, EXAM_TRPR_NM, EXAM_TRPR_ID, DECODE(LAST_SCR_VL, '', 0, LAST_SCR_VL) AS LAST_SCR_VL , EXAM_CNT, EXAM_RNKN_SQNO, AMNT_DTTM , ROUND(EXAM_TOTL_SCR/EXAM_TRPR_CNT, 1) AS EXAM_AVG FROM (SELECT A.EXAM_ID, A.EXAM_NM, A.EXAM_TRPR_NM, A.EXAM_TRPR_ID, A.TRPR_CNT, B.LAST_SCR_VL, A.AMNT_DTTM , B.EXAM_RNKN_SQNO , COUNT(B.EXAM_ID) OVER(PARTITION BY B.EXAM_TRPR_ID) AS EXAM_CNT , SUM(B.LAST_SCR_VL) OVER(PARTITION BY A.EXAM_ID) AS EXAM_TOTL_SCR , COUNT(B.EXAM_RNKN_SQNO) OVER(PARTITION BY A.EXAM_ID) AS EXAM_TRPR_CNT FROM (SELECT A.EXAM_ID, A.EXAM_NM, A.CNTR_CD, A.AMNT_DTTM, B.EXAM_TRPR_ID, B.EXAM_TRPR_NM , COUNT(B.EXAM_TRPR_ID) OVER(PARTITION BY A.EXAM_ID) TRPR_CNT FROM (SELECT EXAM_ID, EXAM_NM, EMSH_ID, CNTR_CD, AMNT_DTTM FROM TB_EXAM_BASI A WHERE 1=1
AND A.BZPT_DIV = #{bzpt_div}
) A , (SELECT EXAM_TRPR_ID , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.EXAM_TRPR_ID) AS EXAM_TRPR_NM FROM TB_EXAM_TRPR A WHERE EXAM_ID IN (SELECT EXAM_ID FROM TB_EXAM_BASI A WHERE 1=1
) GROUP BY EXAM_TRPR_ID ) B ) A , TB_EXAM_TRPR B WHERE A.EXAM_ID = B.EXAM_ID(+) AND A.EXAM_TRPR_ID = B.EXAM_TRPR_ID(+) AND A.CNTR_CD = B.CNTR_CD(+) ORDER BY A.AMNT_DTTM DESC )
