sql.where:
	AND TMP_CD > ' '

    AND CHAT_CNTS_ID = #{chat_cnts_id}


    AND CNTR_CD = #{cntr_cd}


    AND CHAT_CNTS_DIV_CD = #{chat_cnts_div_cd}


    AND CHAT_MSG_TYP_CD = #{chat_msg_typ_cd}



    AND CHAT_MSG_CNTN LIKE '%' || #{chat_msg_cntn} || '%'




    AND CHAT_DPMS_DIV_CD = #{chat_dpms_div_cd}


    AND USE_YN = #{use_yn}


    AND SUBSTR(TMP_CD,1,2) = #{cbit}


    AND GETCODENM('WEB120', TMP_CD,'CCA') LIKE '%' || #{chat_msg_titl} || '%'

mapper.select:
SELECT /* ChatCntsMap.getChatCntsList */ C.* , GETCODENM('KMS070', CHAT_MSG_TYP_CD, C.CNTR_CD) AS CHAT_MSG_TYP_NM , GETCODENM('KMS080', CHAT_CNTS_DIV_CD, C.CNTR_CD) AS CHAT_CNTS_DIV_NM , GETCODENM('KMS090', CHAT_DPMS_DIV_CD, C.CNTR_CD) AS CHAT_DPMS_DIV_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = C.RGSR_ID AND CNTR_CD = C.CNTR_CD) AS RGSR_NM , (SELECT FILE_ID FROM TB_FILE WHERE RLTN_ITEM_ID = C.CHAT_CNTS_ID AND RLTN_TBL_NM = 'TB_CHAT_CNTS_ADMN' AND ATCH_TYP_CD != '30') AS FILE_ID , (CASE WHEN EXISTS (SELECT * FROM TB_FILE C WHERE C.RLTN_ITEM_ID = C.CHAT_CNTS_ID AND C.RLTN_TBL_NM = 'TB_CHAT_CNTS_ADMN' AND C.ATCH_TYP_CD != '30') THEN 'Y' ELSE 'N' END) AS FILE_FLAG FROM (SELECT ROWNUM AS PAGE_INDX, B.* FROM (SELECT CHAT_CNTS_ID, CNTR_CD, CHAT_CNTS_DIV_CD, CHAT_MSG_TYP_CD, CHAT_MSG_LNGT , CHAT_MSG_CNTN, RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM , DPMS_TLNO, USE_YN, CHAT_MSG_TITL, CHAT_DPMS_DIV_CD, IMG_URL_ADDR , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.AMND_ID AND CNTR_CD = A.CNTR_CD) AS AMND_NM ,TMP_CD,BUTTON_GUBUN,BUTTON_NM,BUTTON_URL FROM TB_CHAT_CNTS_ADMN A
ORDER BY ${orderBy} ${orderDirection}
) B ) C
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT /* ChatCntsMap.getcjChatCntsList */ C.* , GETCODENM('KMS070', CHAT_MSG_TYP_CD, C.CNTR_CD) AS CHAT_MSG_TYP_NM , GETCODENM('KMS080', CHAT_CNTS_DIV_CD, C.CNTR_CD) AS CHAT_CNTS_DIV_NM , GETCODENM('KMS090', CHAT_DPMS_DIV_CD, C.CNTR_CD) AS CHAT_DPMS_DIV_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = C.RGSR_ID AND CNTR_CD = C.CNTR_CD) AS RGSR_NM , (SELECT FILE_ID FROM TB_FILE WHERE RLTN_ITEM_ID = C.CHAT_CNTS_ID AND RLTN_TBL_NM = 'TB_CHAT_CNTS_ADMN' AND ATCH_TYP_CD != '30') AS FILE_ID , (CASE WHEN EXISTS (SELECT * FROM TB_FILE C WHERE C.RLTN_ITEM_ID = C.CHAT_CNTS_ID AND C.RLTN_TBL_NM = 'TB_CHAT_CNTS_ADMN' AND C.ATCH_TYP_CD != '30') THEN 'Y' ELSE 'N' END) AS FILE_FLAG FROM (SELECT ROWNUM AS PAGE_INDX, B.* FROM (SELECT CHAT_CNTS_ID, CNTR_CD, CHAT_CNTS_DIV_CD, CHAT_MSG_TYP_CD, CHAT_MSG_LNGT , CHAT_MSG_CNTN, RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM , DPMS_TLNO, USE_YN, CHAT_MSG_TITL, CHAT_DPMS_DIV_CD, IMG_URL_ADDR , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.AMND_ID AND CNTR_CD = A.CNTR_CD) AS AMND_NM ,TMP_CD,BUTTON_GUBUN,BUTTON_NM,BUTTON_URL FROM TB_CHAT_CNTS_ADMN A
ORDER BY ${orderBy} ${orderDirection}
) B ) C
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

mapper.update:
        UPDATE /* ChatCntsMap.updateChatCnts */
               TB_CHAT_CNTS_ADMN
           SET CHAT_CNTS_DIV_CD = #{chat_cnts_div_cd}
             , CHAT_MSG_TYP_CD = #{chat_msg_typ_cd}
             , CHAT_MSG_LNGT = #{chat_msg_lngt}
             , CHAT_MSG_CNTN = #{chat_msg_cntn} 
             , AMND_ID = #{amnd_id}
             , AMNT_DTTM = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
             , DPMS_TLNO = REPLACE(#{dpms_tlno}, '-', '')
             , USE_YN = #{use_yn}
             , CHAT_MSG_TITL = #{chat_msg_titl}
             , CHAT_DPMS_DIV_CD = #{chat_dpms_div_cd}
             , IMG_URL_ADDR = #{img_url_addr}

               ,TMP_CD  =  #{tmp_cd}
	           ,BUTTON_GUBUN =#{button_gubun} 

	               ,BUTTON_NM   = #{button_nm}
	           	   ,BUTTON_URL =#{button_url}


	               ,BUTTON_NM   = ''
	           	   ,BUTTON_URL  = ''




               ,TMP_CD  =  ''
	           ,BUTTON_GUBUN = ''
	           ,BUTTON_NM   = ''
	           ,BUTTON_URL = ''


        WHERE CHAT_CNTS_ID = #{chat_cnts_id}
          AND CNTR_CD = #{cntr_cd}

mapper.delete:
DELETE /* ChatCntsMap.deleteChatCnts */
  FROM TB_CHAT_CNTS_ADMN
 WHERE CHAT_CNTS_ID = #{chat_cnts_id}
