mapper.select:
     	SELECT /* DlwOnlnMngMap.getMallMemberCount */
     	       COUNT(*)
		FROM MB_MEM_MST
		WHERE 1=1

   			AND TO_CHAR(REG_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt} 


   		 	AND TO_CHAR(LAST_LGN_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt}


			AND ID = #{id}


			AND NAME = #{name}

SELECT /* DlwOnlnMngMap.getOnlnMemberList */ * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY REG_DTM DESC) PAGE_INDX, MST.ID , MST.UNQ_NO , MST.NAME , MST.BIRTH , MST.EMAIL , MST.HP , MST.TEL , MST.ZIPCD , MST.ADR , MST.ADR_DTL , MST.USE_YN , MST.LAST_LGN_DTM , MST.REG_DTM , MST.SMS_RCV_YN , MST.EMAIL_RCV_YN , MST.AGREEMENT_FOR_THIRD_PARTY , MST.MARKETING_YN , MST.CI FROM MB_MEM_MST MST WHERE 1=1
AND TO_CHAR(REG_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt}
AND TO_CHAR(LAST_LGN_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt}
AND ID = #{id}
AND NAME = #{name}
)
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT COUNT(*) FROM ( SELECT /*DlwOnlnMngMap.getOnlnMemberPayChgCnt*/ CCP.ID, MAX(MST.NAME) AS NAME, MAX(MST.BIRTH) AS BIRTH, MAX(MST.UNQ_NO) AS UNQ_NO FROM CM_ACNT_CHNG_PTC CCP INNER JOIN MB_MEM_MST MST ON CCP.ID = MST.ID WHERE 1=1
AND TO_CHAR(CCP.REG_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt}
AND TO_CHAR(MST.LAST_LGN_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt}
AND CCP.ID = #{id}
AND MST.NAME = #{name}
GROUP BY CCP.ID ) TBL


				SELECT  
					/*DlwOnlnMngMap.getOnlnMemberPayChgList*/ 
						*
				FROM
				(
					SELECT 
						ROW_NUMBER() OVER(ORDER BY CCP.ID ASC) PAGE_INDX,
					    CCP.ID,
					    MAX(MST.NAME) AS NAME,
					    MAX(MST.BIRTH) AS BIRTH,
					    MAX(MST.UNQ_NO) AS UNQ_NO,
                        MAX(CCP.REG_DTM) AS REG_DM					    
					FROM CM_ACNT_CHNG_PTC CCP INNER JOIN
					    MB_MEM_MST MST ON CCP.ID = MST.ID 
					WHERE 1=1

				   			AND TO_CHAR(CCP.REG_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt} 


				   		 	AND TO_CHAR(MST.LAST_LGN_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt}


							AND CCP.ID = #{id}


							AND MST.NAME = #{name}

					GROUP BY CCP.ID  
				) TBL



            WHERE PAGE_INDX >= #{startLine} 
              AND PAGE_INDX < #{endLine}


        	ORDER BY REG_DM DESC

        	SELECT 
        		/* DlwOnlnMngMap.getOnlnMemberDormancyCnt */
        		COUNT(*)
            FROM
            (
				SELECT 
				    QM.ID,
				    QM.NAME,
				    QM.BIRTH,
				    QM.UNQ_NO
				FROM MB_QSCN_MEM_MST QM 
				WHERE 1=1

			   			AND TO_CHAR(REG_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt} 


			   		 	AND TO_CHAR(LAST_LGN_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt}


						AND ID = #{id}


						AND NAME = #{name}

	        )


        SELECT /* DlwOnlnMngMap.getOnlnMemberDormancyList */
               * 
        FROM 
        (
				SELECT  
					ROW_NUMBER() OVER(ORDER BY QM.ID DESC) PAGE_INDX,
				    QM.ID,
				    QM.NAME,
				    QM.BIRTH,
				    QM.UNQ_NO
				FROM MB_QSCN_MEM_MST QM 
				WHERE 1=1

			   			AND TO_CHAR(REG_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt} 


			   		 	AND TO_CHAR(LAST_LGN_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt}


						AND ID = #{id}


						AND NAME = #{name}

        )


            WHERE PAGE_INDX >= #{startLine}
              AND PAGE_INDX < #{endLine}

SELECT /* DlwOnlnMngMap.getOnlnMemberWdrawCnt */ COUNT(*) FROM ( SELECT ID , DROT_RSN , DROT_DTM AS REG_DTM FROM MB_DROT_MEM_MST WHERE 1=1
AND TO_CHAR(DROT_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt}
AND ID = #{id}
)


        SELECT /* DlwOnlnMngMap.getOnlnMemberWdrawList */
               * 
        FROM 
        (
				SELECT  
				   ROW_NUMBER() OVER(ORDER BY DROT_DTM DESC) PAGE_INDX,
				   ID ,
				   DROT_RSN AS REASON, 
				   DROT_DTM AS REG_DTM
				FROM MB_DROT_MEM_MST
				WHERE 1=1

			   			AND TO_CHAR(DROT_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt} 


						AND ID = #{id}

        )


            WHERE PAGE_INDX >= #{startLine}
              AND PAGE_INDX < #{endLine}

mapper.update:
	/* DlwOnlnMngMap.updateWebMemInfo */
    UPDATE MB_MEM_MST          	
       SET 

      		UNQ_NO  = #{mem_no}, MOD_DTM  = SYSDATE, MOD_ID =  #{rgsr_id} 


      		USE_YN  = #{use_yn}, MOD_DTM  = SYSDATE, MOD_ID =  #{rgsr_id} 


      		USE_YN  = #{use_yn}, MOD_DTM  = SYSDATE, MOD_ID =  #{rgsr_id} 

     WHERE 1=1
       AND ID = #{id}

/* DlwOnlnMngMap.updateWebDormancy */ BEGIN INSERT INTO MB_MEM_MST (ID, PWD, UNQ_NO, NAME, SEX, BIRTH, EMAIL, HP, TEL, ZIPCD, ADR, ADR_DTL, SMS_RCV_YN, TEL_RCV_YN, CTLG_RCV_YN, EMAIL_RCV_YN, USE_YN, LAST_LGN_DTM, DROT_YN, DROT_RSN, DROT_DTM, QSCN_YN, USER_GB, OLD_AUTH_YN, MARKETING_YN, CI, REG_ID, REG_IP, REG_DTM, MOD_ID, MOD_IP, MOD_DTM, TEMP_HP, AGREEMENT_FOR_THIRD_PARTY, MARKETING_INFO_YN , RETRY , INTR_FIELD) SELECT ID, PWD, UNQ_NO, NAME, SEX, BIRTH, EMAIL, HP, TEL, ZIPCD, ADR, ADR_DTL, SMS_RCV_YN, TEL_RCV_YN, CTLG_RCV_YN, EMAIL_RCV_YN, USE_YN, SYSDATE, DROT_YN, DROT_RSN, DROT_DTM, 'N', USER_GB, OLD_AUTH_YN, MARKETING_YN, CI, REG_ID, REG_IP, REG_DTM, 'devadmin', '0:0:0:0:0:0:0:1', SYSDATE, TEMP_HP, AGREEMENT_FOR_THIRD_PARTY, MARKETING_INFO_YN, RETRY, INTR_FIELD FROM MB_QSCN_MEM_MST WHERE ID =#{id};
DELETE MB_QSCN_MEM_MST WHERE ID = #{id};
END;

    	/* DlwOnlnMngMap.updateWebPayChgComp */
		UPDATE CM_ACNT_CHNG_PTC
		SET PRCS_YN = 'Y', MOD_ID = #{rgsr_id}, MOD_IP = SYSDATE 
		WHERE ACCNT_NO = #{accnt_no}
		    AND PRCS_YN = 'N'

mapper.select:
	    SELECT
	    	/* DlwOnlnMngMap.getOnlnConsCount */
	    	COUNT(*)
	    FROM
	    (
		    SELECT 
		        MST.PRSN_CNSL_SEQ,
		        MAX(CASE WHEN TASK_GB = 'R' THEN TO_CHAR(PCL.MOD_DTM,'YYYYMMDDHH24MISS') ELSE '' END) AS CHECK_DT,
		        MAX(CASE WHEN TASK_GB = 'A' THEN TO_CHAR(PCL.MOD_DTM,'YYYYMMDDHH24MISS') ELSE '' END) AS REPLY_DT,
		        MAX(CASE WHEN TASK_GB = 'S' THEN TO_CHAR(PCL.MOD_DTM,'YYYYMMDDHH24MISS') ELSE '' END) AS CONFIRM_DT
		    FROM CS_PRSN_CNSL_MST MST LEFT OUTER JOIN    
		        CS_PRSN_CNSL_LOG PCL ON MST.PRSN_CNSL_SEQ = PCL.PRSN_CNSL_SEQ
		    WHERE 1=1

					AND ID = #{id}


					AND MST.PRCS_CD = #{prcs_cd}


		   			AND TO_CHAR(REG_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt}

		    GROUP BY MST.PRSN_CNSL_SEQ 
	    ) TBL 
	    WHERE 1=1

   		 	AND CONFIRM_DT BETWEEN #{stt_dt} AND #{end_dt}

SELECT /* DlwOnlnMngMap.getOnlnConsList */ * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MST.REG_DTM DESC) PAGE_INDX, MST.PRSN_CNSL_SEQ, MST.ID, MST.INQRY_CD, (SELECT CD_NM FROM CO_CD_DTL WHERE CD_ID = 'PRSN_INQRY_GB' AND CD = MST.INQRY_CD) AS INQRY_NM, MST.INQRY_DTL_CD, (SELECT CD_NM FROM CO_CD_DTL WHERE CD_ID = 'PRSN_INQRY_DTL_GB' AND CD = MST.INQRY_DTL_CD) AS INQRY_DTL_NM, MST.TITL, TO_CHAR(SUBSTR(MST.CNTN, 1, 4000)) AS CNTN, TO_CHAR(SUBSTR(MST.ANSW, 1, 4000)) AS ANSW, MST.PRCS_CD, (SELECT CD_NM FROM CO_CD_DTL WHERE CD_ID = 'PROCESS_TYPE' AND CD = MST.PRCS_CD) AS PRCS_NM, MST.REG_DTM, MB.NAME, MB.EMAIL, MB.HP, TBL.CHECK_USER, TBL.CHECK_DT, TBL.REPLY_USER, TBL.REPLY_DT, TBL.CONFIRM_USER, TBL.CONFIRM_DT FROM ( SELECT MST.PRSN_CNSL_SEQ, MAX(CASE WHEN TASK_GB = 'R' THEN PCL.MOD_ID ELSE '' END) AS CHECK_USER, MAX(CASE WHEN TASK_GB = 'R' THEN TO_CHAR(PCL.MOD_DTM,'YYYYMMDDHH24MISS') ELSE '' END) AS CHECK_DT, MAX(CASE WHEN TASK_GB = 'A' THEN PCL.MOD_ID ELSE '' END) AS REPLY_USER, MAX(CASE WHEN TASK_GB = 'A' THEN TO_CHAR(PCL.MOD_DTM,'YYYYMMDDHH24MISS') ELSE '' END) AS REPLY_DT, MAX(CASE WHEN TASK_GB = 'S' THEN PCL.MOD_ID ELSE '' END) AS CONFIRM_USER, MAX(CASE WHEN TASK_GB = 'S' THEN TO_CHAR(PCL.MOD_DTM,'YYYYMMDDHH24MISS') ELSE '' END) AS CONFIRM_DT FROM CS_PRSN_CNSL_MST MST LEFT OUTER JOIN CS_PRSN_CNSL_LOG PCL ON MST.PRSN_CNSL_SEQ = PCL.PRSN_CNSL_SEQ WHERE 1=1
AND ID = #{id}
AND MST.PRCS_CD = #{prcs_cd}
AND TO_CHAR(REG_DTM, 'YYYYMMDD') BETWEEN #{stt_dt} AND #{end_dt}
GROUP BY MST.PRSN_CNSL_SEQ ) TBL INNER JOIN CS_PRSN_CNSL_MST MST ON MST.PRSN_CNSL_SEQ = TBL.PRSN_CNSL_SEQ LEFT OUTER JOIN MB_MEM_MST MB ON MST.ID = MB.ID AND MB.USE_YN = 'Y' WHERE 1=1
AND CONFIRM_DT BETWEEN #{stt_dt} AND #{end_dt}
)
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

mapper.insert:
	/* DlwOnlnMngMap.insertOnlnConsLog */
    INSERT INTO CS_PRSN_CNSL_LOG(PRSN_CNSL_SEQ, TASK_GB, MOD_ID, MOD_IP, MOD_DTM)
    VALUES (#{prsn_cnsl_seq}, #{task_gb}, #{rgsr_id}, #{uip}, SYSDATE)

mapper.update:
	/* DlwOnlnMngMap.updateOnlnConsMst */
    UPDATE CS_PRSN_CNSL_MST
    SET PRCS_CD = #{prcs_cd}
       ,ANSW = #{answ}
       ,MOD_ID = #{rgsr_id}
       ,MOD_IP = #{uip}
       ,MOD_DTM = SYSDATE
    WHERE PRSN_CNSL_SEQ = #{prsn_cnsl_seq}
