mapper.select:
SELECT /* BswrDmndDtlMap.getTrctConsList */ F.* FROM (SELECT ROWNUM AS PAGE_INDX , A.* , GETCODENM('USR080', A.TRCT_CONS_SCTR_CD, A.CNTR_CD) AS TRCT_CONS_SCTR_NM , GETCODENM('USR090', A.TRCT_TRGT_DIV_CD, A.CNTR_CD) AS TRCT_TRGT_DIV_NM
FROM (SELECT A.BSWR_DMND_ID , A.CONSNO , A.BSWR_DMND_STAT_CD , GETCODENM('CNS160', A.BSWR_DMND_STAT_CD, C.CNTR_CD) AS BSWR_DMND_STAT_NM , (SELECT GETCODENM('USR100', TRCT_TYP_CD, CNTR_CD) FROM TB_TRCT_BOX_ADMN WHERE TRCT_BOX_ID = A.TRCT_BOX_ID) AS TRCT_TYP_NM , (SELECT TRCT_TYP_CD FROM TB_TRCT_BOX_ADMN WHERE TRCT_BOX_ID = A.TRCT_BOX_ID) AS TRCT_TYP_CD , (SELECT TRCT_CONS_SCTR_CD FROM TB_TRCT_BOX_ADMN WHERE TRCT_BOX_ID = A.TRCT_BOX_ID) AS TRCT_CONS_SCTR_CD , (SELECT TRCT_TRGT_DIV_CD FROM TB_TRCT_BOX_ADMN WHERE TRCT_BOX_ID = A.TRCT_BOX_ID) AS TRCT_TRGT_DIV_CD , (SELECT TRCT_BOX_ID FROM TB_TRCT_BOX_ADMN WHERE TRCT_BOX_ID = A.TRCT_BOX_ID) AS TRCT_BOX_ID , (SELECT TRCT_BOX_NM FROM TB_TRCT_BOX_ADMN WHERE TRCT_BOX_ID = A.TRCT_BOX_ID) AS TRCT_BOX_NM , C.CONS_STAT_CD , GETCODENM('CNS030', C.CONS_STAT_CD, C.CNTR_CD) AS CONS_STAT_NM , A.TRCT_ACTP_ID , A.TRCT_ACPG_DEPT_CD , (SELECT TEAM_NM FROM TB_CONS_TEAM WHERE TEAM_CD = A.TRCT_ACPG_DEPT_CD AND CNTR_CD = A.CNTR_CD) AS TRCT_ACPG_DEPT_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.TRCT_ACTP_ID AND CNTR_CD = A.CNTR_CD) AS TRCT_ACTP_NM , A.TRCT_ACPG_DTTM , A.TRCT_CHPR_ID , (SELECT TEAM_NM FROM TB_CONS_TEAM WHERE TEAM_CD = A.TRCT_CHPR_DEPT_CD AND CNTR_CD = A.CNTR_CD) AS TRCT_CHPR_DEPT_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.TRCT_CHPR_ID AND CNTR_CD = A.CNTR_CD) AS TRCT_CHPR_NM , A.BSWR_DMND_CNFT_DTTM , A.TRNM_DSPS_DTTM , (CASE WHEN A.BSWR_DMND_STAT_CD = '00' OR A.BSWR_DMND_STAT_CD = '10' OR A.BSWR_DMND_STAT_CD = '20' THEN TRUNC(SYSDATE - TO_DATE(A.TRCT_ACPG_DTTM,'YYYYMMDDHH24MISS')) || '일 ' || LPAD(TRUNC(MOD((SYSDATE - TO_DATE(A.TRCT_ACPG_DTTM,'YYYYMMDDHH24MISS')), 1) * 24), 2, 0) || '시간 ' || LPAD(TRUNC(MOD((SYSDATE - TO_DATE(A.TRCT_ACPG_DTTM,'YYYYMMDDHH24MISS')) * 24, 1) * 60), 2, 0) || '분' ELSE TRUNC(TO_DATE(A.TRNM_DSPS_DTTM,'YYYYMMDDHH24MISS') - TO_DATE(A.TRCT_ACPG_DTTM,'YYYYMMDDHH24MISS')) || '일 ' || LPAD(TRUNC(MOD((TO_DATE(A.TRNM_DSPS_DTTM,'YYYYMMDDHH24MISS') - TO_DATE(A.TRCT_ACPG_DTTM,'YYYYMMDDHH24MISS')), 1) * 24), 2, 0) || '시간 ' || LPAD(TRUNC(MOD((TO_DATE(A.TRNM_DSPS_DTTM,'YYYYMMDDHH24MISS') - TO_DATE(A.TRCT_ACPG_DTTM,'YYYYMMDDHH24MISS')) * 24, 1) * 60), 2, 0) || '분' END) AS CALB_RESR_TERM , A.BSWR_DMND_ACPG_CNTN , A.BSWR_DMND_DSPS_CNTN , A.CONS_ATMT_CMPL_YN AS TRCT_CONS_ATMT_CMPL_YN , C.CONS_TYP1_CD , C.CONS_TYP2_CD , C.CONS_TYP3_CD , (SELECT CONS_TYP_NM FROM TB_CONS_TYP_CD WHERE CONS_TYP_CD = C.CONS_TYP1_CD AND CNTR_CD = C.CNTR_CD) AS CONS_TYP1_NM , (SELECT CONS_TYP_NM FROM TB_CONS_TYP_CD WHERE CONS_TYP_CD = C.CONS_TYP2_CD AND CNTR_CD = C.CNTR_CD) AS CONS_TYP2_NM , (SELECT CONS_TYP_NM FROM TB_CONS_TYP_CD WHERE CONS_TYP_CD = C.CONS_TYP3_CD AND CNTR_CD = C.CNTR_CD) AS CONS_TYP3_NM , C.CONS_MEMO_CNTN, C.MEM_NO, C.CONSNO_GROP_NO, C.CONSNO_SQNO , MEM.MEM_NM, MEM.HOME_TEL, MEM.CELL, MEM.EMAIL , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.RGSR_ID AND CNTR_CD = A.CNTR_CD) AS RGSR_NM , A.RGSN_DTTM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.AMND_ID AND CNTR_CD = A.CNTR_CD) AS AMND_NM , A.AMNT_DTTM , A.CNTR_CD FROM TB_BSWR_DMND_DTL A LEFT JOIN TB_CONS C ON A.CONSNO = C.CONSNO AND A.CNTR_CD = C.CNTR_CD LEFT OUTER JOIN LF_DMUSER.MEMBER MEM ON C.MEM_NO = MEM.MEM_NO
ORDER BY ${orderBy} ${orderDirection}
) A
)F
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

