SELECT /* BswrDmndDtlMap.getBswrDmndDspsCount */ COUNT(*) FROM TB_BSWR_DMND_DTL WHERE BSWR_DMND_ID = #{bswr_dmnd_id} AND CNTR_CD = #{cntr_cd}
AND TRCT_CHPR_ID IS NULL
AND BSWR_DMND_STAT_CD IN('00','10','20')

mapper.update:
UPDATE /* BswrDmndDtlMap.updateBswrDmndDsps */
       TB_BSWR_DMND_DTL
   SET AMND_ID   = #{amnd_id}
     , AMNT_DTTM = TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')


     , TRCT_CHPR_ID = #{trct_chpr_id}
     , TRCT_CHPR_DEPT_CD = (SELECT TEAM_CD FROM TB_USER WHERE USER_ID = #{trct_chpr_id})


     , TRCT_CHPR_ID = #{trct_chpr_id}
     , TRCT_CHPR_DEPT_CD = (SELECT TEAM_CD FROM TB_USER WHERE USER_ID = #{trct_chpr_id})
     , BSWR_DMND_STAT_CD = '10'
     , BSWR_DMND_CNFT_DTTM = TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')


     , BSWR_DMND_DSPS_CNTN   = #{bswr_dmnd_dsps_cntn}
     , TRNM_DSPS_DTTM  = TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')
     , BSWR_DMND_STAT_CD = #{bswr_dmnd_stat_cd}
     , TRCT_CHPR_ID = #{trct_chpr_id}
     , TRCT_CHPR_DEPT_CD = (SELECT TEAM_CD FROM TB_USER WHERE USER_ID = #{trct_chpr_id})


 WHERE BSWR_DMND_ID = #{bswr_dmnd_id}
   AND CNTR_CD = #{cntr_cd}

mapper.delete:
DELETE /* BswrDmndDtlMap.deleteBswrDmnd */
  FROM TB_BSWR_DMND_DTL
 WHERE BSWR_DMND_ID = #{bswr_dmnd_id}
   AND CNTR_CD = #{cntr_cd}
