mapper.select:
SELECT /* CalbDtlMap.getCalbDtlList */ C.* , GETCODENM('CNS040', CALB_RESL_CD, CNTR_CD) AS CALB_RESL_NM , GETCODENM('CNS050', CALB_DSPS_RSN_CD, CNTR_CD) AS CALB_DSPS_RSN_NM , GETCODENM('CNS060', ARS_SRVC_ADDR, CNTR_CD) AS ARS_SRVC_NM , GETCODENM('CNS200', CALB_DIV_CD, CNTR_CD) AS CALB_DIV_NM , (SELECT D.TEAM_NM FROM TB_CONS_TEAM D WHERE D.TEAM_CD = C.TEAM1_CD AND D.CNTR_CD = C.CNTR_CD) AS TEAM1_NM , (SELECT D.TEAM_NM FROM TB_CONS_TEAM D WHERE D.TEAM_CD = C.TEAM2_CD AND D.CNTR_CD = C.CNTR_CD) AS TEAM2_NM , (CASE WHEN CALB_RESL_CD = '30' OR SYSDATE - TO_DATE(CALB_RESR_DTTM,'YYYYMMDDHH24MISS') < 0 THEN NULL ELSE TRUNC(SYSDATE - TO_DATE(CALB_RESR_DTTM,'YYYYMMDDHH24MISS')) || '일 ' || LPAD(TRUNC(MOD((SYSDATE - TO_DATE(CALB_RESR_DTTM,'YYYYMMDDHH24MISS')), 1) * 24), 2, 0) || '시간 ' || LPAD(TRUNC(MOD((SYSDATE - TO_DATE(CALB_RESR_DTTM,'YYYYMMDDHH24MISS')) * 24, 1) * 60), 2, 0) || '분' END) AS CALB_RESR_TERM FROM (SELECT ROWNUM AS PAGE_INDX , B.* , (SELECT C.HGRN_TEAM_CD FROM TB_CONS_TEAM C WHERE C.TEAM_CD = B.TEAM2_CD AND C.CNTR_CD = B.CNTR_CD) AS TEAM1_CD FROM (SELECT CALB_ID, CALB_DIV_CD, ARS_SRVC_ADDR, INCO_TLNO, CALB_RESR_TLNO , CALB_RESR_DTTM, CONSNO, SIDO_TMCNT, MAX_SIDO_TMCNT, CHPR_ID , CALB_RESL_CD, CALB_DSPS_RSN_CD , CNTR_CD, RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM , (SELECT B.USER_NM FROM TB_USER B WHERE B.USER_ID = A.CHPR_ID AND B.CNTR_CD = A.CNTR_CD) AS CHPR_NM , (SELECT B.TEAM_CD FROM TB_USER B WHERE B.USER_ID = A.CHPR_ID AND B.CNTR_CD = A.CNTR_CD) AS TEAM2_CD FROM TB_CALB_DTL A
ORDER BY ${orderBy} ${orderDirection}
) B ) C
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

mapper.update:
UPDATE /* CalbDtlMap.updateCalbDtl */
       TB_CALB_DTL


       CHPR_ID = #{chpr_id}
     , CALB_RESL_CD = '20'


       CHPR_ID = NULL
     , CALB_RESL_CD = '10'


       CALB_RESL_CD = '30'
     , CALB_DSPS_RSN_CD = '40'
     , CHPR_ID = #{chpr_id}
     , AMND_ID = #{amnd_id}
     , AMNT_DTTM = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')


       CALB_RESL_CD = #{calb_resl_cd}
     , CALB_DSPS_RSN_CD = #{calb_dsps_rsn_cd}
     , SIDO_TMCNT = SIDO_TMCNT + 1
     , AMND_ID = #{amnd_id}
     , AMNT_DTTM = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')


 WHERE CALB_ID = #{calb_id}
