AND EXISTS (SELECT 'Y' FROM TB_USER B , TB_CONS_TEAM C WHERE B.USER_ID = A.AMND_ID AND B.CNTR_CD = A.CNTR_CD AND B.TEAM_CD = C.TEAM_CD AND B.CNTR_CD = C.CNTR_CD AND C.HGRN_TEAM_CD = #{team1_cd})
AND A.CONS_TYP1_CD = #{cons_typ1_cd}
AND A.CONS_TYP2_CD = #{cons_typ2_cd}
AND A.CONS_TYP3_CD = #{cons_typ3_cd}
