SELECT /* DlwNewTypeConsMap.getChatSndgHstrList */ C.* , CASE WHEN CHAT_SNDG_DIV_CD = '0' THEN 'SMS' WHEN CHAT_SNDG_DIV_CD = '2' THEN 'MMS' WHEN CHAT_SNDG_DIV_CD = '3' THEN 'LMS' WHEN CHAT_SNDG_DIV_CD = '4' THEN '알림톡' ELSE '' END CHAT_SNDG_DIV_NM /*********************************************** 2017-09-26 김준호 알림톡 관련 코드값 추가 및 수정 ***********************************************/ , CASE WHEN C.GUBUN= 'B' THEN NVL((SELECT CD_NM FROM TB_CD T WHERE CD_SET_CD = 'MSG001' AND CD= CHAT_SNDG_STAT_CD),'알림톡실패') ELSE CASE WHEN CHAT_SNDG_STAT_CD = '3' THEN '완료' WHEN CHAT_SNDG_STAT_CD = '2' THEN '결과대기' WHEN CHAT_SNDG_STAT_CD = '1' THEN '전송대기' WHEN CHAT_SNDG_STAT_CD = 'U' THEN '전송대기' ELSE '' END END CHAT_SNDG_STAT_NM /***************************************** 2017-09-26 김준호 알림톡 관련 코드값 추가 및 수정 *****************************************/ , CASE WHEN C.GUBUN = 'B' THEN NVL((SELECT CD_NM FROM TB_CD T WHERE CD_SET_CD = 'MSG002' AND (CD= CHAT_SNDG_RESL_CD OR ADTL_CD = CHAT_SNDG_RESL_CD)),'알림톡실패') ELSE CASE WHEN CHAT_SNDG_RESL_CD = '1000' THEN '성공' WHEN CHAT_SNDG_RESL_CD IS NOT NULL THEN '실패' ELSE '' END END CHAT_SNDG_RESL_NM , (SELECT D.TEAM_NM FROM TB_CONS_TEAM D WHERE D.TEAM_CD = C.TEAM_CD) AS TEAM_NM , (SELECT D.TEAM_NM FROM TB_CONS_TEAM D WHERE D.TEAM_CD = C.HGRN_TEAM_CD) AS HGRN_TEAM_NM FROM (SELECT ROWNUM AS PAGE_INDX , B.* , (SELECT C.TEAM_CD FROM TB_USER C WHERE C.USER_ID = B.SNDG_CHPR_ID AND C.CNTR_CD = B.CNTR_CD) AS TEAM_CD , (SELECT D.HGRN_TEAM_CD FROM TB_USER C , TB_CONS_TEAM D WHERE C.USER_ID = B.SNDG_CHPR_ID AND C.CNTR_CD = B.CNTR_CD AND C.TEAM_CD = D.TEAM_CD AND C.CNTR_CD = D.CNTR_CD) AS HGRN_TEAM_CD FROM (SELECT CNTR_CD, CHAT_SNDG_HSTR_ID, CELL, CHAT_SNDG_DIV_CD, CHAT_MSG_TITL , CHAT_MSG_CNTN, MSG_TRNM_SCS_YN, MSG_TRNM_FALR_RSN , TO_CHAR(SNDG_DTTM, 'YYYY-MM-DD HH24:MI') AS SNDG_DTTM , SNDG_CHPR_ID , RESR_YN, TO_CHAR(RESR_DTTM, 'YYYY-MM-DD HH24:MI') AS RESR_DTTM, CNTR_RPRS_TLNO, CONSNO, MEM_NO , MEM_NM, CMPG_ID, TRGT_LIST_ID, TRGT_CUST_ID , CHAT_SNDG_STAT_CD, CHAT_SNDG_RESL_CD , RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.SNDG_CHPR_ID AND CNTR_CD = A.CNTR_CD) AS SNDG_CHPR_NM , A.GUBUN FROM (select TC.*, 'A' AS GUBUN from TB_CHAT_SNDG_HSTR TC union all select TN.*, 'B' AS GUBUN from TB_CHAT_SNDG_HSTR_NEW TN) A
WHERE CNTR_CD = #{cntr_cd} AND (MEM_NO = #{mem_no} OR CELL = REPLACE(#{v_cell}, '-', ''))
WHERE 1=1
AND CNTR_CD = #{cntr_cd}
AND CELL LIKE '%' || REPLACE(#{cell}, '-', '') || '%'
AND CHAT_SNDG_DIV_CD = #{chat_sndg_div_cd}
AND CHAT_MSG_TITL LIKE '%' || #{chat_msg_titl} || '%'
AND CHAT_MSG_CNTN LIKE '%' || #{chat_msg_cntn} || '%'
AND SNDG_DTTM >= REPLACE(#{stt_dt}, '-', '') || '000000'
AND SNDG_DTTM <= REPLACE(#{end_dt}, '-', '') || '235959'
AND CONS_DSPS_DTTM BETWEEN TO_CHAR(SYSDATE, 'YYYYMMDD') || '000000' AND TO_CHAR(SYSDATE, 'YYYYMMDD') || '235959'
AND SNDG_CHPR_ID = #{sndg_chpr_id}
AND EXISTS (SELECT 'Y' FROM TB_USER B WHERE B.USER_ID = A.SNDG_CHPR_ID AND B.CNTR_CD = A.CNTR_CD AND B.TEAM_CD = #{team_cd})
AND EXISTS (SELECT 'Y' FROM TB_USER B , TB_CONS_TEAM C WHERE B.USER_ID = A.SNDG_CHPR_ID AND B.CNTR_CD = A.CNTR_CD AND B.TEAM_CD = C.TEAM_CD AND B.CNTR_CD = C.CNTR_CD AND C.HGRN_TEAM_CD = #{hgrn_team_cd})
AND RESR_YN = #{resr_yn}
AND CNTR_RPRS_TLNO = REPLACE(#{cntr_rprs_tlno}, '-', '')
AND CONSNO = #{consno}
AND MEM_NO = #{mem_no}
AND MEM_NM LIKE '%' || #{mem_nm} || '%'
AND CHAT_SNDG_STAT_CD = #{chat_sndg_stat_cd}
AND CHAT_SNDG_RESL_CD IS NOT NULL AND CHAT_SNDG_RESL_CD != '1000'
AND CHAT_SNDG_RESL_CD = #{chat_sndg_resl_cd}
AND CHAT_SNDG_HSTR_ID = #{chat_sndg_hstr_id}
ORDER BY ${orderBy} ${orderDirection}
) B ) C
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT /* DlwNewTypeConsMap.getDlwConsList */ A.*,
ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) AS PAGE_INDX
ROW_NUMBER() OVER(CNSL_DT DESC) AS PAGE_INDX
FROM (SELECT A.SEQ , A.MEM_NO , A.ACCNT_NO , (CASE WHEN A.ACCNT_NO = '00000' THEN '기타문의' ELSE (LF_DMUSER.FN_PROD_NM((SELECT PROD_CD FROM LF_DMUSER.MEM_PROD_ACCNT WHERE MEM_PROD_ACCNT.ACCNT_NO = A.ACCNT_NO))) END) AS PROD_NM , A.CNSL_DT AS CNSL_DT , REPLACE(A.CNSL_CON, CHR(13), '') AS CNSL_CON , (SELECT (SELECT GRP_NM FROM LF_DMUSER.COM_CD_GRP WHERE GRP_CD = GUBN AND CON LIKE '%상담구분%') FROM LF_DMUSER.CNSL_DTL WHERE MEM_NO = A.MEM_NO AND SEQ = A.SEQ AND CNSL_SEQ = A.CNSL_SEQ AND ROWNUM = 1) AS GUBN_NM , A.CNSL_MAN , A.CALL_TEL , A.REC_PATH , A.REC_FILE , LF_DMUSER.FN_EMPLE_NM(A.CNSL_MAN) AS CNSL_MAN_NM , (SELECT DEPT_NM FROM LF_DMUSER.DEPARTMENT WHERE DEPT_CD = (SELECT DEPT_CD FROM LF_DMUSER.EMPLOYEE WHERE EMPLOYEE.EMPLE_NO = CNSL_MAN)) AS CNSL_MAN_DEPT_NM , A.CNSL_SEQ , CASE WHEN REG_CL = '02' AND (CD.CNSL_CON NOT LIKE '%주소:%' AND CD.CNSL_CON NOT LIKE '%휴대폰:%' AND CD.CNSL_CON NOT LIKE '%전화:%' AND CD.CNSL_CON NOT LIKE '%비고:%') THEN A.CNSL_CON ELSE CD.CNSL_CON END CNSL_CON_SHOW FROM LF_DMUSER.CNSL_MNG A LEFT OUTER JOIN LF_DMUSER.MEM_PROD_ACCNT ACCNT ON A.ACCNT_NO = ACCNT.ACCNT_NO left outer join LF_DMUSER.CNSL_DTL cd on cd.MEM_NO = A.MEM_NO AND cd.SEQ = A.SEQ AND cd.CNSL_SEQ = A.CNSL_SEQ WHERE A.MEM_NO = #{mem_no} AND NVL(ACCNT.DEL_FG, 'N') = 'N'
AND A.ACCNT_NO = #{accnt_no}
) A

SELECT * FROM (SELECT /* DlwNewTypeConsMap.getDlwResnAmtList */ SEQ, M.PROD_CD, P.PROD_NM, M.MODEL_CL_CD , LF_DMUSER.FN_MODEL_CL_NM(M.MODEL_CL_CD) MODEL_CL_NM , M.APP_DAY AS APP_DAY , P.PROD_AMT, P.EXPR_NO , CASE WHEN P.SALE_CL ='Y' THEN '판매중' ELSE '판매중지' END SALE_CL , CASE WHEN M.USE_YN ='Y' THEN '사용' ELSE '미사용' END USE_YN FROM LF_DMUSER.RESNSTD_M M INNER JOIN LF_DMUSER.PRODUCT P ON M.PROD_CD = P.PROD_CD WHERE P.DEL_FG ='N' AND M.PROD_CD = #{prod_cd}
AND M.MODEL_CL_CD = #{model_cl_cd}
AND M.APP_DAY <= #{join_dt} ORDER BY APP_DAY DESC ) WHERE ROWNUM = 1

SELECT /* DlwNewTypeConsMap.getDlwPayMngList */ A.*,
ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) AS PAGE_INDX
ROW_NUMBER() OVER(ORDER BY A.NO DESC) AS PAGE_INDX
FROM ( SELECT A.ACCNT_NO, A.SEQ, A.NO, A.PAY_AMT, A.PAY_MTHD , A.STAT, A.DC_AMT_SEQ, C.DC_AMT, A.RC_STAT , '상조부금' AS PYIN_TYP , nvl(A.PAY_DAY,'') AS PAY_DAY , LF_DMUSER.FN_COM_NM('01', A.PAY_MTHD) AS PAY_MTHD_NM , CASE WHEN A.STAT = 0 THEN '정상' ELSE (CASE WHEN A.STAT = '1' THEN '선납' ELSE '연체' END) END AS STAT_NM , substr( add_months(B.JOIN_DT, -1),0,7) AS MON_NO , CASE WHEN A.PAY_MTHD ='04' THEN CASE WHEN A.WDRW_INV_DT IS NULL THEN WR.APP_DT ELSE A.WDRW_INV_DT END WHEN A.PAY_MTHD ='06' THEN CASE WHEN A.WDRW_INV_DT IS NULL THEN CWR.APP_DT ELSE A.WDRW_INV_DT END ELSE '' END AS APP_DT , CASE WHEN (substr(A.PAY_DAY,1, 6) = NVL((SELECT CM.CLOSE_DT FROM LF_DMUSER.CLOSE_MNG CM WHERE CM.DEL_FG = 'N' AND substr((A.PAY_DAY), 1,4)||'/'||substr((A.PAY_DAY), 5,2) =CM.CLOSE_DT),'')) AND (substr((A.PAY_DAY), 1,4)||'/'||substr((A.PAY_DAY), 5,2) NOT IN (SELECT CER.CLOSE_DT FROM LF_DMUSER.CLOSE_EDT_REQ CER WHERE CER.ACCNT_NO = A.ACCNT_NO AND CER.CLOSE_CL = '0001' AND CER.EDT_YN = 'Y' AND CER.DEL_FG = 'N' GROUP BY CER.CLOSE_DT)) THEN 'N' ELSE 'Y' END EDT_YN FROM LF_DMUSER.PAY_MNG A INNER JOIN LF_DMUSER.MEM_PROD_ACCNT B ON A.ACCNT_NO = B.ACCNT_NO LEFT OUTER JOIN LF_DMUSER.DC_AMT_REG C ON A.ACCNT_NO = C.ACCNT_NO AND A.DC_AMT_SEQ = C.SEQ AND C.DEL_FG = 'N' AND A.NO = (SELECT MAX(NO) FROM LF_DMUSER.PAY_MNG WHERE ACCNT_NO = A.ACCNT_NO AND DC_AMT_SEQ = C.SEQ) LEFT OUTER JOIN (
select a.accnt_no , a.inv_no , max(b.inv_dt) app_dt , max(b.inv_no) prev_inv_no from (select /*+INDEX(wdrw_req IDX_WDRW_REQ_ACCNT_STAT) */ * from LF_DMUSER.wdrw_req where accnt_no = #{accnt_no} and stat = '04' ) a left outer join (select /*+INDEX(wdrw_req IDX_WDRW_REQ_ACCNT_STAT) */ * from LF_DMUSER.wdrw_req where accnt_no = #{accnt_no} and stat = '04' ) b on a.inv_no > b.inv_no group by a.accnt_no , a.inv_no
) WR ON WR.ACCNT_NO = B.ACCNT_NO AND (A.NO <= WR.INV_NO AND A.NO > WR.PREV_INV_NO) AND A.DEL_FG = 'N' LEFT outer JOIN (
select a.accnt_no , a.inv_no , max(b.inv_dt) app_dt , max(b.inv_no) prev_inv_no from (select /*+INDEX(card_wdrw_req IDX_CARD_WDRW_REQ_ACCNT_STAT) */ * from LF_DMUSER.card_wdrw_req where accnt_no = '201202000960' and stat = '04' ) a left outer join (select /*+INDEX(card_wdrw_req IDX_CARD_WDRW_REQ_ACCNT_STAT) */ * from LF_DMUSER.card_wdrw_req where accnt_no = '201202000960' and stat = '04' ) b on a.inv_no > b.inv_no group by a.accnt_no , a.inv_no
) CWR ON CWR.ACCNT_NO = B.ACCNT_NO AND (A.NO <= CWR.INV_NO AND A.NO > CWR.PREV_INV_NO) WHERE A.DEL_FG = 'N' AND A.ACCNT_NO = #{accnt_no}
) A

SELECT /* DlwNewTypeConsMap.getMainWdrwExceList */ '0001' AS EXCEPT_GBN, MES.ACCNT_NO, 1 AS PAGE_INDX, TO_CHAR(MES.REG_DM, 'YYYYMMDD') AS REQ_DAY, MES.REG_DM, CASE WHEN MES.SPECIAL_BIT = '01' THEN '임의지정' WHEN MES.SPECIAL_BIT = '02' THEN '격월납' WHEN MES.SPECIAL_BIT = '03' THEN '산출취소' ELSE '기타사항' END AS REG_REASON, MES.SPECIAL_ETC FROM LF_DMUSER.TB_MEMBER_EXT_SPECIAL MES WHERE 1=1 AND MES.DEL_FG = 'N' AND MES.ACCNT_NO = #{accnt_no}
