(pay.NO, 0) ELSE NVL(pay.NO, 0) END END last_no , NVL((SELECT CASE WHEN ADDAMT.EXPR_NO IS NULL THEN CASE WHEN ALLT.EXPR_NO = NVL(PAY.NO, 0) THEN 0 ELSE ALLT_AMT END ELSE CASE WHEN ALLT.EXPR_NO = NVL(PAY.NO, 0) AND ADDAMT.EXPR_NO = NVL(PAY1.NO, 0) THEN 0 WHEN ALLT.EXPR_NO = NVL(PAY.NO, 0) AND ADDAMT.EXPR_NO != NVL(PAY1.NO, 0) THEN ADD_AMT WHEN ALLT.EXPR_NO != NVL(PAY.NO, 0) AND ADDAMT.EXPR_NO = NVL(PAY1.NO, 0) THEN ALLT_AMT ELSE ALLT_AMT + ADD_AMT END END FROM PRODUCT_DTL WHERE PROD_CD = accnt.PROD_CD AND CASE WHEN ADDAMT.EXPR_NO IS NULL THEN CASE WHEN ALLT.EXPR_NO = NVL(PAY.NO, 0) THEN 0 ELSE NVL(PAY.NO, 0) + 1 END ELSE CASE WHEN ALLT.EXPR_NO = NVL(PAY.NO, 0) AND ADDAMT.EXPR_NO = NVL(PAY1.NO, 0) THEN 0 WHEN ALLT.EXPR_NO = NVL(PAY.NO, 0) AND ADDAMT.EXPR_NO != NVL(PAY1.NO, 0) THEN NVL(PAY1.NO, 0)+1 WHEN ALLT.EXPR_NO != NVL(PAY.NO, 0) AND ADDAMT.EXPR_NO = NVL(PAY1.NO, 0) THEN NVL(PAY.NO, 0)+1 ELSE NVL(PAY.NO, 0)+1 END END BETWEEN ST_NO AND END_NO), 0) AMT , CASE WHEN PROD.PAY_TYPE = '001' THEN PROD.MON_PAY_AMT ELSE NVL((SELECT MONTH_PAY_AMT + ALLT_AMT + ADD_AMT FROM PRODUCT_DTL WHERE PROD_CD = ACCNT.PROD_CD AND 1 BETWEEN ST_NO AND END_NO), 0) END AMT1 , CASE WHEN ( (ADDAMT.EXPR_NO IS NOT NULL AND NVL(PAY.NO, 0) = NVL(PAY1.NO, 0)) OR (ADDAMT.EXPR_NO IS NOT NULL AND NVL(PAY.NO, 0) != NVL(PAY1.NO, 0) AND ALLT.EXPR_NO = NVL(PAY.NO, 0) AND ADDAMT.EXPR_NO != NVL(PAY1.NO, 0)) OR (ADDAMT.EXPR_NO IS NOT NULL AND NVL(PAY.NO, 0) != NVL(PAY1.NO, 0) AND ALLT.EXPR_NO != NVL(PAY.NO, 0) AND ADDAMT.EXPR_NO = NVL(PAY1.NO, 0)) OR (ADDAMT.EXPR_NO IS NULL AND ALLT.EXPR_NO != NVL(PAY.NO, 0)) OR (ALLT.EXPR_NO IS NOT NULL AND ADDAMT.EXPR_NO IS NOT NULL AND NVL(PAY.NO, 0) = 0 AND NVL(PAY1.NO, 0) = 0) OR (ALLT.EXPR_NO IS NOT NULL AND ADDAMT.EXPR_NO IS NOT NULL AND ALLT.EXPR_NO = NVL(PAY.NO, 0) AND NVL(PAY1.NO, 0) = 0) OR (ALLT.EXPR_NO IS NOT NULL AND ADDAMT.EXPR_NO IS NOT NULL AND ADDAMT.EXPR_NO = NVL(PAY1.NO, 0) AND NVL(PAY.NO, 0) = 0) ) THEN 'true' ELSE 'false' END COMB_COND_1 , CASE WHEN WR.ACCNT_NO IS NOT NULL THEN 'CMS' WHEN CWR.ACCNT_NO IS NOT NULL THEN 'CARD' WHEN WRCC.ACCNT_NO IS NOT NULL THEN 'Call' ELSE '' END WDRW_STAT FROM MEM_PROD_ACCNT ACCNT INNER JOIN MEMBER MEM ON ACCNT.MEM_NO = MEM.MEM_NO INNER JOIN PRODUCT PROD ON PROD.PROD_CD = ACCNT.PROD_CD AND NVL(PROD.PROD_CL, '00') = '03' AND prod.PROD_CD NOT IN ('S9', 'T0') LEFT OUTER JOIN (SELECT * FROM WDRW_REQ WR WHERE WR.STAT IN ('01', '02')) WR ON WR.ACCNT_NO = ACCNT.ACCNT_NO LEFT OUTER JOIN (SELECT * FROM CARD_WDRW_REQ CWR WHERE CWR.STAT IN ('01', '02')) CWR ON CWR.ACCNT_NO = ACCNT.ACCNT_NO LEFT OUTER JOIN (SELECT * FROM WDRW_REQ_CALL_CENTER WRCC WHERE NVL(WRCC.WDRW_REQ_YN, 'N') = 'N' AND WRCC.DEL_FG IS NULL) WRCC ON WRCC.ACCNT_NO = ACCNT.ACCNT_NO LEFT OUTER JOIN ( SELECT VAD.ACCNT_NO FROM NICE_VRTL_ACCNT_WDRW_REQ_DTL VAD INNER JOIN NICE_VRTL_ACCNT_WDRW_REQ_MST VAM ON VAM.SEQ = VAD.SEQ AND ( (VAM.STAT = '02' AND VAM.EXPIRY_YYYYMMDD || VAM.EXPIRY_HHMMSS > TO_CHAR(SYSDATE-(1/24), 'YYYYMMDDHH24MISS') ) OR VAM.STAT = '01') ) VAD ON ACCNT.ACCNT_NO = VAD.ACCNT_NO LEFT OUTER JOIN PAY_MNG_DTL pay ON accnt.ACCNT_NO = pay.ACCNT_NO AND pay.DEL_FG = 'N' AND pay.NO = (SELECT MAX(NO) FROM PAY_MNG_DTL WHERE accnt_no = accnt.accnt_no AND del_fg = 'N') LEFT OUTER JOIN PAY_MNG_DTL1 pay1 ON accnt.ACCNT_NO = pay1.ACCNT_NO AND pay1.DEL_FG = 'N' AND pay1.NO = (SELECT MAX(NO) FROM PAY_MNG_DTL1 WHERE accnt_no = accnt.accnt_no AND del_fg = 'N') LEFT OUTER JOIN (SELECT sum(PD.end_no - PD.st_no + 1) EXPR_NO , PD.PROD_CD PROD_CD FROM PRODUCT_DTL PD WHERE ALLT_AMT > 0 GROUP BY PD.PROD_CD) ALLT ON PROD.PROD_CD = ALLT.PROD_CD LEFT OUTER JOIN (SELECT sum(PD.end_no - PD.st_no + 1) EXPR_NO , PD.PROD_CD PROD_CD FROM PRODUCT_DTL PD WHERE ADD_AMT > 0 GROUP BY PD.PROD_CD ) ADDAMT ON PROD.PROD_CD = ADDAMT.PROD_CD WHERE 1=1
AND VAD.ACCNT_NO IS NULL AND NVL(PROD.JOIN_TYPE, '') != '0002'
AND PROD.PROD_CD = #{prod_cd}
AND ACCNT.ACCNT_NO = #{accnt_no}
AND MEM.MEM_NM = #{mem_nm}
AND MEM.MEM_NO = #{mem_no}
AND JOIN_DT BETWEEN #{stt_dt} AND #{end_dt}
) TBL WHERE 1=1 AND TBL.EVT_NM NOT IN ('', '부활', '청약철회') AND COMB_COND_1 = 'true'
AND 1=2
AND TBL.EVT_NM = '행사'
AND TBL.EVT_NM IN ('해약', '무환급해약')
AND SUBSTR(TBL.YEN_CHE, -2) = '연체'
) TBL1 WHERE 1=1 AND TBL1.EXPR_NO - TBL1.LAST_NO > 0
AND TBL1.YEN_CHE_NO BETWEEN #{yen_stt_dt} AND #{yen_end_dt}

