SELECT /* DlwVrtlAcntTestMap.getPayNo */
       DECODE(PAY_NO, 0, 'I', 'U') AS IU_GUBUN
  FROM (SELECT TO_CHAR(NVL(MAX(PAY_NO),0)) AS PAY_NO
          FROM GASU_AMT_REG
         WHERE DEL_FG = 'N'
           AND ACCNT_NO = #{accnt_no}
           AND PAY_NO = #{inv_no}
        )

WITH /* DlwVrtlAcntTestMap.getYenCheChk */ W_PAY_MNG AS ( SELECT MAX(NO) NO , ACCNT_NO FROM PAY_MNG WHERE del_fg = 'N'
AND ACCNT_NO = #{accnt_no}
GROUP BY accnt_no ),
W_PAY_MNG_DTL AS ( SELECT MAX(NO) AS NO , ACCNT_NO FROM PAY_MNG_DTL WHERE del_fg = 'N'
AND ACCNT_NO = #{accnt_no}
GROUP BY accnt_no ),
W_PAY_MNG_DTL1 AS ( SELECT MAX(NO) AS NO , ACCNT_NO FROM PAY_MNG_DTL1 WHERE del_fg = 'N'
AND ACCNT_NO = #{accnt_no}
GROUP BY accnt_no )
SELECT CASE WHEN TBL.PROD_CL = '03' THEN CASE WHEN EVT_NM = '' THEN CASE WHEN TBL.ALLT_EXPR_NO = TBL.PAY1_NO AND TBL.EXPR_NO = TBL.PAY_NO THEN FN_YEN_CHE(TBL.ACCNT_NO) WHEN TBL.ALLT_EXPR_NO = TBL.PAY1_NO THEN FN_YEN_CHE(TBL.ACCNT_NO) WHEN TBL.EXPR_NO = TBL.PAY_NO THEN FN_YEN_CHE_COMB(TBL.ACCNT_NO) ELSE FN_YEN_CHE(TBL.ACCNT_NO) END ELSE CASE WHEN TBL.ADDAMT_EXPR_NO IS NULL THEN CASE WHEN TBL.ALLT_EXPR_NO = NVL(TBL.PAY_NO, 0) THEN '' ELSE FN_YEN_CHE_COMB(TBL.ACCNT_NO) END ELSE CASE WHEN TBL.ALLT_EXPR_NO = NVL(TBL.PAY1_NO, 0) AND TBL.ADDAMT_EXPR_NO = NVL(TBL.PAY2_NO, 0) THEN '' WHEN TBL.ALLT_EXPR_NO = NVL(TBL.PAY1_NO, 0) AND TBL.ADDAMT_EXPR_NO != NVL(TBL.PAY2_NO, 0) THEN FN_YEN_CHE_COMB_ADD(TBL.ACCNT_NO) WHEN TBL.ALLT_EXPR_NO != NVL(TBL.PAY1_NO, 0) AND TBL.ADDAMT_EXPR_NO = NVL(TBL.PAY2_NO, 0) THEN FN_YEN_CHE_COMB(TBL.ACCNT_NO) ELSE FN_YEN_CHE_COMB(TBL.ACCNT_NO) END END END ELSE CASE WHEN FN_YEN_CHE(TBL.ACCNT_NO) IN ('연체', '미납') THEN '연체' ELSE '정상' END END FROM ( SELECT ACCNT.ACCNT_NO , FN_EVENT(ACCNT.ACCNT_NO) EVT_NM , NVL(PROD.PROD_CL, '') PROD_CL , NVL(PAY.NO, 0) PAY_NO , NVL(PAY1.NO, 0) PAY1_NO , NVL(PAY2.NO, 0) PAY2_NO , PROD.EXPR_NO , NVL(ALLT.EXPR_NO, 0) ALLT_EXPR_NO , NVL(ADDAMT.EXPR_NO, 0) ADDAMT_EXPR_NO FROM MEM_PROD_ACCNT ACCNT INNER JOIN PRODUCT PROD ON ACCNT.PROD_CD = PROD.PROD_CD LEFT OUTER JOIN W_PAY_MNG pay ON accnt.ACCNT_NO = pay.ACCNT_NO LEFT OUTER JOIN W_PAY_MNG_DTL pay1 ON accnt.ACCNT_NO = pay1.ACCNT_NO LEFT OUTER JOIN W_PAY_MNG_DTL1 pay2 ON accnt.ACCNT_NO = pay2.ACCNT_NO LEFT OUTER JOIN ( SELECT sum(PD.end_no - PD.st_no + 1) EXPR_NO , PD.PROD_CD PROD_CD FROM PRODUCT_DTL PD WHERE ALLT_AMT > 0 GROUP BY PD.PROD_CD ) ALLT ON prod.PROD_CD = ALLT.PROD_CD LEFT OUTER JOIN ( SELECT sum(PD.end_no - PD.st_no + 1) EXPR_NO , PD.PROD_CD PROD_CD FROM PRODUCT_DTL PD WHERE ADD_AMT > 0 GROUP BY PD.PROD_CD ) ADDAMT ON prod.PROD_CD = ADDAMT.PROD_CD WHERE 1=1 AND ACCNT.ACCNT_NO = #{accnt_no} ) TBL

/* * 1. 가상계좌 입금 대상 회원 기본 정보 산출 로직 기준 데이터 * (1). PAY_BIT 설명 NOR : 일반 상품 회차(일반상품정상) REL : 결합만 있는 회차(해약) ADD : 추가만 있는 회차(행사) NRA : 일반/결합 상품의 회차(결합상품정상) * (2). CMS 출금의뢰가 들어오지 않은 고객, 카드출금의뢰가 들어오지 않은 고객, 출금의뢰신청이 들어오지 않은 고객 * (3). 해피콜의 상태가 01, 011, 11인 고객 (01 : 승인, 011 : 재승인, 11 : 정의되지 않음) * (4). CMS출금의뢰 상태가 (01: 신청전, 02:신청중) 인 고객 * (5). 카드출금의뢰 테이블(01: 신청전, 02:신청중) 인 고객 * (6). 출금의뢰신청_고객만족센터의 청구 상태가 미청구 인 고객 * (7). 고객의 상태가 정상인 회원 (KSTBIT = 02) * (8). 삭제되지 아니한 회원 */ /* 가상 계좌 산출 기준데이터 */ WITH VIRTUAL_TAR_LIST AS ( SELECT ACCNT_NO ,PAY_MTHD ,MEM_NO ,PROD_CL ,PROD_CD ,MEM_NM ,KSTBIT ,RESN_CNT ,MONTH_COUNT ,(CASE WHEN MONTH_COUNT > EXPR_NO2 THEN EXPR_NO2 ELSE MONTH_COUNT END) AS MONTH_COUNT2 ,LAST_NO + 1 AS LAST_NO ,LAST_NO2 + 1 AS LAST_NO2 ,LAST_NO3 + 1 AS LAST_NO3 ,EXPR_NO ,EXPR_NO2 ,EXPR_NO3 ,NEW_CHAN_GUNSU ,ACOYEN_YN ,CASE WHEN RESN_CNT >= 1 THEN (CASE WHEN LAST_NO2 + 1 < EXPR_NO2 AND LAST_NO3 + 1 < EXPR_NO3 THEN 'REL' WHEN LAST_NO2 + 1 >= EXPR_NO2 AND LAST_NO3 + 1 < EXPR_NO3 THEN 'ADD' ELSE 'REL' END ) ELSE (CASE WHEN LAST_NO + 1 < (EXPR_NO - NEW_CHAN_GUNSU) AND LAST_NO2 + 1 = EXPR_NO2 AND LAST_NO3 + 1 = EXPR_NO3 THEN 'NOR' WHEN LAST_NO + 1 >= (EXPR_NO - NEW_CHAN_GUNSU) AND LAST_NO2 + 1 < EXPR_NO2 AND LAST_NO3 + 1 < EXPR_NO3 THEN 'REL' WHEN LAST_NO + 1 >= (EXPR_NO - NEW_CHAN_GUNSU) AND LAST_NO2 + 1 >= EXPR_NO2 AND LAST_NO3 + 1 < EXPR_NO3 THEN 'ADD' ELSE 'NRA' END ) END AS PAY_BIT FROM ( SELECT MPA.ACCNT_NO ,MPA.PAY_MTHD ,MB.MEM_NO ,MB.MEM_NM ,PD.PROD_CL ,PD.PROD_CD ,MPA.KSTBIT ,(SELECT COUNT(*) FROM RESCISSION WHERE 1=1 AND ACCNT_NO = MPA.ACCNT_NO AND CMS_WDRW_REQ_YN = 'Y' AND DEL_FG = 'N' AND ACCNT_NO = MPA.ACCNT_NO) AS RESN_CNT ,(TRUNC(MONTHS_BETWEEN(LAST_DAY(SYSDATE),(SUBSTR(MPA.JOIN_DT,0,6) || '01')))+ 1) AS MONTH_COUNT ,NVL((SELECT COUNT(NO) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'),0) AS LAST_NO ,NVL((SELECT COUNT(NO) FROM PAY_MNG_DTL WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'),0) AS LAST_NO2 ,NVL((SELECT COUNT(NO) FROM PAY_MNG_DTL1 WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'),0) AS LAST_NO3 ,NVL((SELECT COUNT(NO) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND AMT > 0),0) AS EXPR_NO ,NVL((SELECT COUNT(NO) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND REL_AMT > 0),0) AS EXPR_NO2 ,NVL((SELECT COUNT(NO) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND ADD_AMT > 0),0) AS EXPR_NO3 ,NVL(MPA.NEW_CHAN_GUNSU,0) AS NEW_CHAN_GUNSU ,MPA.ACOYEN_YN ,(SELECT STAT FROM HP_CALL WHERE ACCNT_NO = MPA.ACCNT_NO) AS STAT FROM MEM_PROD_ACCNT MPA INNER JOIN MEMBER MB ON MPA.MEM_NO = MB.MEM_NO AND MB.DEL_FG = 'N' INNER JOIN PRODUCT PD ON MPA.PROD_CD = PD.PROD_CD WHERE 1=1 AND MPA.DEL_FG = 'N'
AND MPA.JOIN_DT BETWEEN #{stt_dt} AND #{end_dt}
AND MPA.ACCNT_NO = #{accnt_no}
AND MB.MEM_NM = #{mem_nm}
AND MB.MEM_NO = #{mem_no}
AND PD.PROD_CD = #{prod_cd}
) WHERE 1=1 AND STAT IN ('01','011','11') ) /* 정상 고객 산출 */ SELECT VIR_TBL.ACCNT_NO ,PAY_MTHD ,FN_COM_NM('01', PAY_MTHD) AS PAY_MTHD_NM ,MEM_NM ,MEM_NO ,PROD_CL ,FN_COM_NM('85', PROD_CL) AS PROD_CL_NM ,MONTH_COUNT ,EXPR_NO ,LAST_NO ,VIR_TBL.PAY_STAT ,CASE WHEN PAY_BIT = 'NRA' THEN (NVL(AMT,0) + NVL(REL_AMT,0) + NVL(ADD_AMT,0)) WHEN PAY_BIT = 'NOR' THEN NVL(AMT,0) WHEN PAY_BIT = 'REL' THEN NVL(REL_AMT,0) + NVL(ADD_AMT,0) WHEN PAY_BIT = 'ADD' THEN NVL(ADD_AMT,0) ELSE (NVL(AMT,0) + NVL(REL_AMT,0) + NVL(ADD_AMT,0)) END AS MON_PAY_AMT ,KSTBIT AS JOIN_STAT ,(MONTH_COUNT - LAST_NO) AS YEN_CHE_NO ,((EXPR_NO - NEW_CHAN_GUNSU) - LAST_NO) + 1 AS ICHAE_LIMIT_GUNSU ,PAY_BIT ,INV_GUNSU FROM ( SELECT ACCNT_NO ,PAY_MTHD ,MEM_NM ,MEM_NO ,PROD_CL ,CASE WHEN PAY_BIT = 'NRA' THEN MONTH_COUNT WHEN PAY_BIT = 'NOR' THEN MONTH_COUNT WHEN PAY_BIT = 'REL' THEN MONTH_COUNT2 WHEN PAY_BIT = 'ADD' THEN MONTH_COUNT2 ELSE MONTH_COUNT END AS MONTH_COUNT ,CASE WHEN PAY_BIT = 'NRA' THEN EXPR_NO WHEN PAY_BIT = 'NOR' THEN EXPR_NO WHEN PAY_BIT = 'REL' THEN EXPR_NO2 WHEN PAY_BIT = 'ADD' THEN EXPR_NO3 ELSE EXPR_NO END AS EXPR_NO ,CASE WHEN PAY_BIT = 'NRA' THEN LAST_NO WHEN PAY_BIT = 'NOR' THEN LAST_NO WHEN PAY_BIT = 'REL' THEN LAST_NO2 WHEN PAY_BIT = 'ADD' THEN LAST_NO3 ELSE LAST_NO END AS LAST_NO
,CASE WHEN PAY_BIT = 'NOR' AND LAST_NO = (EXPR_NO - NEW_CHAN_GUNSU) THEN '05' WHEN PAY_BIT = 'NOR' AND LAST_NO = MONTH_COUNT THEN '01' WHEN PAY_BIT = 'NOR' AND (LAST_NO + 1) = MONTH_COUNT THEN '02' WHEN PAY_BIT = 'NOR' AND (LAST_NO + 1) < MONTH_COUNT THEN '03' WHEN PAY_BIT = 'NOR' AND LAST_NO > MONTH_COUNT THEN '04' WHEN PAY_BIT = 'REL' AND LAST_NO2 = EXPR_NO2 THEN '05' WHEN PAY_BIT = 'REL' AND LAST_NO2 < EXPR_NO2 AND (LAST_NO2) = MONTH_COUNT THEN '01' WHEN PAY_BIT = 'REL' AND LAST_NO2 < EXPR_NO2 AND (LAST_NO2 + 1) = MONTH_COUNT THEN '02' WHEN PAY_BIT = 'REL' AND LAST_NO2 < EXPR_NO2 AND (LAST_NO2 + 1) < MONTH_COUNT THEN '03' WHEN PAY_BIT = 'REL' AND LAST_NO2 < EXPR_NO2 AND LAST_NO2 > MONTH_COUNT THEN '04' WHEN PAY_BIT = 'ADD' AND LAST_NO3 = EXPR_NO3 THEN '05' WHEN PAY_BIT = 'ADD' AND LAST_NO3 < EXPR_NO3 AND (LAST_NO3) = MONTH_COUNT THEN '01' WHEN PAY_BIT = 'ADD' AND LAST_NO3 < EXPR_NO3 AND (LAST_NO3 + 1) = MONTH_COUNT THEN '02' WHEN PAY_BIT = 'ADD' AND LAST_NO3 < EXPR_NO3 AND (LAST_NO3 + 1) < MONTH_COUNT THEN '03' WHEN PAY_BIT = 'ADD' AND LAST_NO3 < EXPR_NO3 AND LAST_NO3 > MONTH_COUNT THEN '04' WHEN PAY_BIT = 'NRA' AND LAST_NO = (EXPR_NO - NEW_CHAN_GUNSU) THEN '05' WHEN PAY_BIT = 'NRA' AND LAST_NO = MONTH_COUNT THEN '01' WHEN PAY_BIT = 'NRA' AND (LAST_NO + 1) = MONTH_COUNT THEN '02' WHEN PAY_BIT = 'NRA' AND (LAST_NO + 1) < MONTH_COUNT THEN '03' WHEN PAY_BIT = 'NRA' AND LAST_NO > MONTH_COUNT THEN '04' ELSE '99' END AS PAY_STAT ,NEW_CHAN_GUNSU ,DECODE(KSTBIT,'01','접수','02','정상','03','해약','행사') AS KSTBIT ,1 AS INV_GUNSU ,FN_COM_NM('01', PAY_MTHD) AS PAY_MTHD_NM ,FN_COM_NM('85', PROD_CL) AS PROD_CL_NM --,WDRW_STAT ,ACOYEN_YN ,PROD_CD ,PAY_BIT FROM VIRTUAL_TAR_LIST WHERE KSTBIT = '02' ) VIR_TBL INNER JOIN PRODUCT_NO_DATA B ON VIR_TBL.PROD_CD = B.PROD_CD AND VIR_TBL.LAST_NO = B.NO LEFT OUTER JOIN (SELECT ACCNT_NO FROM WDRW_REQ WR WHERE WR.STAT IN ('01', '02')) WR ON WR.ACCNT_NO = VIR_TBL.ACCNT_NO LEFT OUTER JOIN (SELECT ACCNT_NO FROM CARD_WDRW_REQ CWR WHERE CWR.STAT IN ('01', '02')) CWR ON CWR.ACCNT_NO = VIR_TBL.ACCNT_NO LEFT OUTER JOIN (SELECT ACCNT_NO FROM WDRW_REQ_CALL_CENTER WRCC WHERE NVL(WRCC.WDRW_REQ_YN, 'N') = 'N' AND WRCC.DEL_FG IS NULL) WRCC ON WRCC.ACCNT_NO = VIR_TBL.ACCNT_NO WHERE NVL(WR.ACCNT_NO,'N') = 'N' AND NVL(CWR.ACCNT_NO,'N') = 'N' AND NVL(WRCC.ACCNT_NO,'N') = 'N'
UNION ALL
