mapper.select:
SELECT /* EvstDtlMap.getEvstDtlList */ A.* FROM (SELECT
ROW_NUMBER() OVER (ORDER BY ${orderBy} ${orderDirection}) AS PAGE_INDX
ROW_NUMBER() OVER (ORDER BY AMNT_DTTM DESC) AS PAGE_INDX
, A.* FROM (SELECT EVST_ID, EVST_NM, EVLT_QSIT_CNT, EVLT_TOTL_SCR, EVST_EXPI , CNTR_CD, RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.RGSR_ID AND CNTR_CD = A.CNTR_CD) AS RGSR_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.AMND_ID AND CNTR_CD = A.CNTR_CD) AS AMND_NM , CASE WHEN EXISTS (SELECT * FROM TB_EVLT_PLAN WHERE EVST_ID = A.EVST_ID AND CNTR_CD = A.CNTR_CD) THEN 'Y' ELSE 'N' END AS EVLT_PLAN_USE_YN FROM (SELECT A.* FROM TB_EVST_DTL A
) A ) A ) A
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

mapper.update:
UPDATE /* EvstDtlMap.updateEvstDtl */
       TB_EVST_DTL A
   SET A.AMND_ID      = #{amnd_id}
     , A.AMNT_DTTM    = TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')

     , A.EVST_NM    = #{evst_nm}


     , A.EVLT_QSIT_CNT         = #{evlt_qsit_cnt}


     , A.EVLT_TOTL_SCR     = #{evlt_totl_scr}


     , A.EVST_EXPI= #{evst_expi}

 WHERE A.EVST_ID    = #{evst_id}

mapper.delete:
DELETE /* EvstDtlMap.deleteEvstDtl */
  FROM TB_EVST_DTL
 WHERE EVST_ID = #{evst_id}
   AND CNTR_CD = #{cntr_cd}
