mapper.select:
SELECT /* EmshMap.getEmshList */ B.* FROM (SELECT ROWNUM AS PAGE_INDX, EMSH_ID, EMSH_NM, EMSH_EXPI, EXAM_DTSC_BAS_CD , LVL1_SCR_VL, LVL2_SCR_VL, LVL3_SCR_VL , CNTR_CD, RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM , EXAM_DTSC_BAS_NM, RGSR_NM, AMND_NM, EXAM_PRGR_STAT_CD, EXAM_PRBM_CNT , TOTL_EXAM_PRBM_DTSC_VL, ADDFG FROM (SELECT EMSH_ID, EMSH_NM, EMSH_EXPI, EXAM_DTSC_BAS_CD, LVL1_SCR_VL , LVL2_SCR_VL, LVL3_SCR_VL , CNTR_CD, RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM , GETCODENM('EXM010', EXAM_DTSC_BAS_CD, A.CNTR_CD) AS EXAM_DTSC_BAS_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.RGSR_ID AND CNTR_CD = A.CNTR_CD) AS RGSR_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.AMND_ID AND CNTR_CD = A.CNTR_CD) AS AMND_NM , (SELECT EXAM_PRGR_STAT_CD FROM TB_EXAM_BASI WHERE EMSH_ID = A.EMSH_ID AND CNTR_CD = A.CNTR_CD) AS EXAM_PRGR_STAT_CD , (SELECT COUNT(*) FROM TB_EXAM_PRBM WHERE EMSH_ID = A.EMSH_ID AND CNTR_CD = A.CNTR_CD) AS EXAM_PRBM_CNT , (SELECT SUM(EXAM_PRBM_DTSC_VL) FROM TB_EXAM_PRBM WHERE EMSH_ID = A.EMSH_ID AND CNTR_CD = A.CNTR_CD) AS TOTL_EXAM_PRBM_DTSC_VL , CASE WHEN EXISTS (SELECT * FROM TB_EXAM_BASI WHERE EMSH_ID = A.EMSH_ID AND CNTR_CD = A.CNTR_CD) THEN 'Y' ELSE 'N' END AS ADDFG FROM TB_EMSH A
ORDER BY ${orderBy} ${orderDirection}
) ) B
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

mapper.update:
UPDATE /* EmshMap.updateEmsh */
       TB_EMSH A
   SET A.AMND_ID      = #{amnd_id}
     , A.AMNT_DTTM          = TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')

     , A.EMSH_NM    = #{emsh_nm}


     , A.EMSH_EXPI  = #{emsh_expi}


     , A.EXAM_DTSC_BAS_CD = #{exam_dtsc_bas_cd}
     , A.LVL1_SCR_VL      = #{lvl1_scr_vl}
     , A.LVL2_SCR_VL    = #{lvl2_scr_vl}
     , A.LVL3_SCR_VL       = #{lvl3_scr_vl}

 WHERE A.EMSH_ID    = #{emsh_id}

mapper.delete:
DELETE /* EmshMap.deleteEmsh */
  FROM TB_EMSH
 WHERE EMSH_ID = #{emsh_id}
