mapper.select:
SELECT /* FaqDtlMap.getFaqView */ * FROM (SELECT ROWNUM AS PAGE_INDX, B.* FROM (SELECT FAQ_ID, CNTR_CD, CONS_TYP1_CD, CONS_TYP2_CD, CONS_TYP3_CD , FAQ_QUST_CNTN, FAQ_ANSR_CNTN, FAQ_ANSR_HTML_CNTN, OPEN_STAT_CD, INQ_CNT , RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM , (SELECT CONS_TYP_NM FROM TB_CONS_TYP_CD WHERE CONS_TYP_CD = A.CONS_TYP1_CD AND CNTR_CD = A.CNTR_CD) AS CONS_TYP1_NM , (SELECT CONS_TYP_NM FROM TB_CONS_TYP_CD WHERE CONS_TYP_CD = A.CONS_TYP2_CD AND CNTR_CD = A.CNTR_CD) AS CONS_TYP2_NM , (SELECT CONS_TYP_NM FROM TB_CONS_TYP_CD WHERE CONS_TYP_CD = A.CONS_TYP3_CD AND CNTR_CD = A.CNTR_CD) AS CONS_TYP3_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.AMND_ID AND CNTR_CD = A.CNTR_CD) AS AMND_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.RGSR_ID AND CNTR_CD = A.CNTR_CD) AS RGSR_NM , CASE WHEN EXISTS (SELECT * FROM TB_FILE WHERE RLTN_ITEM_ID = A.FAQ_ID AND ATCH_TYP_CD != '30') THEN 'Y' ELSE 'N' END AS FILE_FG FROM TB_FAQ_DTL A
ORDER BY ${orderBy} ${orderDirection}
) B )
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT /* FaqDtlMap.getFaqList */ * FROM (SELECT ROWNUM AS PAGE_INDX, B.* FROM (SELECT FAQ_ID, CNTR_CD, CONS_TYP1_CD, CONS_TYP2_CD, CONS_TYP3_CD , FAQ_QUST_CNTN, OPEN_STAT_CD, INQ_CNT , RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM , (SELECT CONS_TYP_NM FROM TB_CONS_TYP_CD WHERE CONS_TYP_CD = A.CONS_TYP1_CD AND CNTR_CD = A.CNTR_CD) AS CONS_TYP1_NM , (SELECT CONS_TYP_NM FROM TB_CONS_TYP_CD WHERE CONS_TYP_CD = A.CONS_TYP2_CD AND CNTR_CD = A.CNTR_CD) AS CONS_TYP2_NM , (SELECT CONS_TYP_NM FROM TB_CONS_TYP_CD WHERE CONS_TYP_CD = A.CONS_TYP3_CD AND CNTR_CD = A.CNTR_CD) AS CONS_TYP3_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.AMND_ID AND CNTR_CD = A.CNTR_CD) AS AMND_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = A.RGSR_ID AND CNTR_CD = A.CNTR_CD) AS RGSR_NM , CASE WHEN EXISTS (SELECT * FROM TB_FILE WHERE RLTN_ITEM_ID = A.FAQ_ID AND ATCH_TYP_CD != '30') THEN 'Y' ELSE 'N' END AS FILE_FG FROM TB_FAQ_DTL A
ORDER BY ${orderBy} ${orderDirection}
) B )
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

mapper.update:
UPDATE /* FaqDtlMap.updateFaq */
       TB_FAQ_DTL
   SET CONS_TYP1_CD = #{cons_typ1_cd}
     , CONS_TYP2_CD = #{cons_typ2_cd}
     , CONS_TYP3_CD = #{cons_typ3_cd}
     , FAQ_QUST_CNTN = #{faq_qust_cntn}
     , FAQ_ANSR_CNTN = #{faq_ansr_cntn}
     , OPEN_STAT_CD = #{open_stat_cd}
     , AMND_ID = #{amnd_id}
     , AMNT_DTTM = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
     , FAQ_ANSR_HTML_CNTN = #{faq_ansr_html_cntn:CLOB}
WHERE FAQ_ID = #{faq_id}
