mapper.insert:
BEGIN UPDATE OM_OSC_CNSL_MST@DMWEB SET CALL_STTS = 'P' WHERE B2B_STTS = 'SMART' AND CNSLR IS NULL AND CALL_STTS IS NULL;
INSERT INTO TB_TRGT_CUST_DTPT (TRGT_CUST_DTPT_ID, TRGT_LIST_ID, SUB_TRGT_LIST_ID, CUST_NM , CLPH_TLNO, HOME_TLNO, CNTR_CD , RGSR_ID, RGSN_DTTM, AMND_ID, AMNT_DTTM, PRMV_ID , USER_DEFN1_CNTN, USER_DEFN2_CNTN, USER_DEFN3_CNTN, USER_DEFN4_CNTN, USER_DEFN5_CNTN , USER_DEFN6_CNTN, USER_DEFN7_CNTN, USER_DEFN8_CNTN, USER_DEFN10_CNTN) SELECT ('TAR' || TO_CHAR(SYSDATE, 'YYYYMMDD') || LPAD(SEQ_COMMON.NEXTVAL, 7, '0')) AS TRGT_CUST_DTPT_ID , #{trgt_list_id} AS TRGT_LIST_ID --변수 , #{sub_trgt_list_id} AS SUB_TRGT_LIST_ID --변수 , NAME AS CUST_NM , REPLACE(HP, '-', '') AS CLPH_TLNO , REPLACE(TEL, '-', '') AS HOME_TLNO , 'CCA' AS CNTR_CD , 'ADMIN' AS RGSR_ID , TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS') AS RGSN_DTTM , 'ADMIN' AS AMND_ID , TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS') AS AMNT_DTTM , OSC_CNSL_SEQ AS PRMV_ID , PRDCT_NM AS USER_DEFN1_CNTN , PRDCT_NM2 AS USER_DEFN2_CNTN , AGENT_CD AS USER_DEFN3_CNTN , AGENT_EMP_NM AS USER_DEFN4_CNTN , MAIN_CONT_TYPE AS USER_DEFN5_CNTN , FUNNEL AS USER_DEFN6_CNTN , MEMO AS USER_DEFN7_CNTN , AGENT_CODE1 AS USER_DEFN8_CNTN , SLLR_CTEL AS USER_DEFN10_CNTN FROM OM_OSC_CNSL_MST@DMWEB WHERE B2B_STTS = #{b2b_stts} AND CNSLR IS NULL AND CALL_STTS = 'P';
UPDATE TB_SUB_TRGT_LIST_DTL SET CUST_DATA_EXTC_CNT = (SELECT COUNT(*) FROM TB_TRGT_CUST_DTPT B WHERE B.SUB_TRGT_LIST_ID = #{sub_trgt_list_id} AND B.TRGT_LIST_ID = #{trgt_list_id} AND B.CNTR_CD = #{cntr_cd}) , DFCT_DATA_CNT = (SELECT COUNT(*) FROM TB_DFCT_DATA B WHERE B.SUB_TRGT_LIST_ID = #{sub_trgt_list_id} AND B.TRGT_LIST_ID = #{trgt_list_id} AND B.CNTR_CD = #{cntr_cd}) WHERE SUB_TRGT_LIST_ID = #{sub_trgt_list_id};
UPDATE TB_TRGT_LIST_DTL SET CUST_DATA_EXTC_CNT = (SELECT COUNT(*) FROM TB_TRGT_CUST_DTPT B WHERE B.TRGT_LIST_ID = #{trgt_list_id} AND B.CNTR_CD = #{cntr_cd}) WHERE TRGT_LIST_ID = #{trgt_list_id} AND CNTR_CD = #{cntr_cd};
UPDATE OM_OSC_CNSL_MST@DMWEB SET CALL_STTS = #{call_stts} WHERE B2B_STTS = #{b2b_stts} AND CNSLR IS NULL AND CALL_STTS = 'P'; END;

mapper.select:
SELECT /* SmartLifePSMap.getTrgtAlctCustCount */ COUNT(*) FROM (SELECT TG.TRGT_LIST_ID, TG.CNTR_CD, TG.SUB_TRGT_LIST_ID, TG.TRGT_CUST_DTPT_ID , TG.CUST_NM, TG.MEM_NO, TG.ACCNT_NO ,TG.ETC_TLNO , TG.CLPH_TLNO, TG.HOME_TLNO, TG.COMP_TLNO, TG.UNTY_CASE_ADTL_YN, TG.USER_DEFN1_CNTN, TG.USER_DEFN2_CNTN , TG.USER_DEFN3_CNTN, TG.USER_DEFN4_CNTN, TG.USER_DEFN5_CNTN, TG.USER_DEFN6_CNTN, TG.USER_DEFN7_CNTN, TG.USER_DEFN8_CNTN , TG.USER_DEFN9_CNTN, TG.USER_DEFN10_CNTN, TG.RGSR_ID, TG.RGSN_DTTM, TG.AMND_ID, TG.AMNT_DTTM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = TG.RGSR_ID) AS RGSR_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = TG.AMND_ID) AS AMND_NM , TA.DPMS_STAT_CD, TA.DPMS_RESL_CD, TA.CNSR_ID , TA.DPMS_RESL_CNTN , (SELECT USER_NM FROM TB_USER WHERE USER_ID = TA.CNSR_ID) AS USER_NM , NVL2((SELECT 'Y' FROM TB_TRGT_CUST_ALCT WHERE TRGT_LIST_ID=TG.TRGT_LIST_ID AND TRGT_CUST_DTPT_ID = TG.TRGT_CUST_DTPT_ID), 'Y','N') AS ALCT_YN FROM TB_TRGT_CUST_DTPT TG , TB_TRGT_CUST_ALCT TA , TB_CMPG_BASI CP WHERE TG.SUB_TRGT_LIST_ID = TA.SUB_TRGT_LIST_ID(+) AND TG.TRGT_CUST_DTPT_ID = TA.TRGT_CUST_DTPT_ID(+) AND TG.TRGT_LIST_ID = CP.TRGT_LIST_ID AND CP.CMPG_ID = #{cmpg_id} AND TG.TRGT_LIST_ID = #{trgt_list_id}
AND SUBSTR(TG.CLPH_TLNO, -1) IN (SELECT COM_CD FROM LF_DMUSER.COM_CD WHERE GRP_CD = '0231' AND CD_NM = #{cmpg_prfm_team_cd} OR SUBSTR(TG.RGSN_DTTM, 0, 8) <= #{cmpg_day}) AND (TA.CNSR_ID IS NULL OR TA.CNSR_ID IN (SELECT USER_ID FROM TB_USER WHERE TEAM_CD = #{cmpg_prfm_team_cd}))
)

SELECT /* SmartLifePSMap.getTrgtAlctCustList */ B.* , GETCODENM('CAM090', B.DPMS_RESL_CD, B.CNTR_CD) AS DPMS_RESL_NM , GETCODENM('CAM100', B.DPMS_STAT_CD, B.CNTR_CD) AS DPMS_STAT_NM , GETCODENM('CAM130', B.DPMS_TLPH_DIV_CD, B.CNTR_CD) AS DPMS_TLPH_DIV_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = B.RGSR_ID) AS RGSR_NM , (SELECT USER_NM FROM TB_USER WHERE USER_ID = B.AMND_ID) AS AMND_NM , (SELECT ADTL_CD FROM TB_CD WHERE CD = B.DPMS_RESL_CD AND CD_SET_CD = 'CAM090') AS ADTL_CD FROM (SELECT ROWNUM AS PAGE_INDX , A.CNTR_CD, A.TRGT_LIST_ID, A.SUB_TRGT_LIST_ID, A.TRGT_CUST_DTPT_ID, TRIM(A.CUST_NM) AS CUST_NM , A.MEM_NO, A.ACCNT_NO, A.CLPH_TLNO, A.USER_NM, A.DPMS_RESL_CNTN , A.HOME_TLNO, A.COMP_TLNO, A.USER_DEFN1_CNTN, A.USER_DEFN2_CNTN ,A.PRMV_ID,A.ETC_TLNO , A.USER_DEFN3_CNTN, A.USER_DEFN4_CNTN, A.USER_DEFN5_CNTN, A.USER_DEFN6_CNTN, A.USER_DEFN7_CNTN, A.USER_DEFN8_CNTN , A.USER_DEFN9_CNTN, A.USER_DEFN10_CNTN, A.RGSR_ID, A.RGSN_DTTM, A.AMND_ID, A.AMNT_DTTM , A.SUB_TRGT_LIST_NM , A.CNSR_ID, A.CTI_CRNC_DTL_ID, A.ALCT_YN , A.DPMS_RESL_CD , A.DPMS_STAT_CD, A.PROD_JOIN_YN , A.DPMS_TLPH_DIV_CD , A.DPMS_TLNO FROM (SELECT TG.CNTR_CD, TG.TRGT_LIST_ID, TG.SUB_TRGT_LIST_ID, TG.TRGT_CUST_DTPT_ID, TG.CUST_NM , TG.MEM_NO, TG.ACCNT_NO, TG.CLPH_TLNO , TG.HOME_TLNO, TG.COMP_TLNO, TG.USER_DEFN1_CNTN, TG.USER_DEFN2_CNTN , TG.PRMV_ID, TG.ETC_TLNO , TG.USER_DEFN3_CNTN, TG.USER_DEFN4_CNTN, TG.USER_DEFN5_CNTN, TG.USER_DEFN6_CNTN, TG.USER_DEFN7_CNTN, TG.USER_DEFN8_CNTN , TG.USER_DEFN9_CNTN, TG.USER_DEFN10_CNTN , TG.RGSR_ID, TG.RGSN_DTTM, TG.AMND_ID, TG.AMNT_DTTM , TA.DPMS_STAT_CD, TA.DPMS_RESL_CD, TA.CNSR_ID, TA.PROD_JOIN_YN, TA.DPMS_TLPH_DIV_CD, TA.DPMS_TLNO, TA.CTI_CRNC_DTL_ID , (SELECT USER_NM FROM TB_USER WHERE USER_ID = TA.CNSR_ID) AS USER_NM,TA.DPMS_RESL_CNTN , (SELECT SUB_TRGT_LIST_NM FROM TB_SUB_TRGT_LIST_DTL TS WHERE TS.SUB_TRGT_LIST_ID=TG.SUB_TRGT_LIST_ID) AS SUB_TRGT_LIST_NM , NVL2((SELECT 'Y' FROM TB_TRGT_CUST_ALCT WHERE SUB_TRGT_LIST_ID=TG.SUB_TRGT_LIST_ID AND TRGT_CUST_DTPT_ID = TG.TRGT_CUST_DTPT_ID), 'Y','N') AS ALCT_YN FROM TB_TRGT_CUST_DTPT TG , TB_TRGT_CUST_ALCT TA , TB_CMPG_BASI CP WHERE TG.SUB_TRGT_LIST_ID = TA.SUB_TRGT_LIST_ID(+) AND TG.TRGT_CUST_DTPT_ID = TA.TRGT_CUST_DTPT_ID(+) AND TG.TRGT_LIST_ID = CP.TRGT_LIST_ID AND CP.CMPG_ID = #{cmpg_id} AND TG.TRGT_LIST_ID = #{trgt_list_id}
AND SUBSTR(TG.CLPH_TLNO, -1) IN (SELECT COM_CD FROM LF_DMUSER.COM_CD WHERE GRP_CD = '0231' AND CD_NM = #{cmpg_prfm_team_cd} OR SUBSTR(TG.RGSN_DTTM, 0, 8) <= #{cmpg_day}) AND (TA.CNSR_ID IS NULL OR TA.CNSR_ID IN (SELECT USER_ID FROM TB_USER WHERE TEAM_CD = #{cmpg_prfm_team_cd}))
AND NOT EXISTS (SELECT ACCNT_NO FROM LF_DMUSER.RESCISSION RES WHERE RESON IN ('011', '23') AND DEL_FG = 'N' AND ACCNT_NO = TG.ACCNT_NO)
ORDER BY ${orderBy} ${orderDirection}
ORDER BY (case when length(prmv_id) < 8 then substr('000000'|| to_char(prmv_id),-7) else prmv_id end ) DESC ,AMNT_DTTM DESC
) A
) B
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

mapper.update:
UPDATE /* SmartLifePSMap.updateChangeCnsr 사용*/
       TB_TRGT_CUST_ALCT
   SET CNSR_ID = #{user_id}
     , AMND_ID = #{amnd_id}
     , AMNT_DTTM = TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')
 WHERE CNSR_ID = #{cnsr_id}

   AND TRGT_CUST_DTPT_ID = #{trgt_cust_dtpt_id}
   AND ((SELECT ADTL_CD FROM TB_CD WHERE CD = DPMS_RESL_CD  AND CD_SET_CD = 'CAM090') = '99'
   OR (SELECT ADTL_CD FROM TB_CD WHERE CD = DPMS_RESL_CD  AND CD_SET_CD = 'CAM090') IS NULL)

mapper.select:
SELECT /* SmartLifePSMap.getChkAlct */
       COUNT(*)
  FROM TB_CMPG_ALCT_DTL
 WHERE CMPG_ID = #{cmpg_id}
   AND SUB_TRGT_LIST_ID  = #{sub_trgt_list_id}
   AND CNSR_ID = #{cnsr_id}
