mapper.update:
/*ReqCustMap.uptReqResultStat*/ BEGIN
/*[001] 카드 유승인 성공 업데이트*/ MERGE INTO TB_MEMBER_WDRW_REQ_DAMO AAA USING ( SELECT ACCNT_NO, RESULT_KEY, REQ_DAY FROM TB_MEMBER_WDRW_RESULT WHERE 1=1 AND REQ_DAY = #{req_day} AND PAY_MTHD = '06' AND PAY_KIND = '01' AND NVL(INI_YN,'N') = 'N' AND NVL(AUTH_YN,'Y') = 'Y' AND NVL(RESULT_CD,'0000') = '3001' ) BBB ON ( AAA.ACCNT_NO = BBB.ACCNT_NO AND AAA.RESULT_KEY = BBB.RESULT_KEY AND AAA.REQ_DAY = BBB.REQ_DAY AND AAA.PAY_MTHD = '06' AND NVL(AAA.INI_YN,'N') = 'N') WHEN MATCHED THEN UPDATE SET AAA.DEL_FG = 'C', AAA.UPD_MAN = #{upd_man}, AAA.UPD_DM = SYSDATE;
/*[002] 카드 유승인 실패 업데이트*/ MERGE INTO TB_MEMBER_WDRW_REQ_DAMO AAA USING ( SELECT ACCNT_NO, RESULT_KEY, REQ_DAY FROM TB_MEMBER_WDRW_RESULT WHERE 1=1 AND REQ_DAY = #{req_day} AND PAY_MTHD = '06' AND PAY_KIND = '01' AND NVL(INI_YN,'N') = 'N' AND NVL(AUTH_YN,'Y') = 'Y' AND NVL(RESULT_CD,'0000') != '3001' ) BBB ON ( AAA.ACCNT_NO = BBB.ACCNT_NO AND AAA.RESULT_KEY = BBB.RESULT_KEY AND AAA.REQ_DAY = BBB.REQ_DAY AND AAA.PAY_MTHD = '06' AND NVL(AAA.INI_YN,'N') = 'N') WHEN MATCHED THEN UPDATE SET AAA.DEL_FG = 'F', AAA.UPD_MAN = #{upd_man}, AAA.UPD_DM = SYSDATE;
/*[001-1] 카드 유승인(이니시스) 성공 업데이트*/ MERGE INTO TB_MEMBER_WDRW_REQ_DAMO AAA USING ( SELECT ACCNT_NO, RESULT_KEY, REQ_DAY FROM TB_MEMBER_WDRW_RESULT WHERE 1=1 AND REQ_DAY = #{req_day} AND PAY_MTHD = '06' AND PAY_KIND = '01' AND NVL(INI_YN,'N') = 'Y' AND NVL(AUTH_YN,'Y') = 'Y' AND NVL(RESULT_CD,'FF') = '00' ) BBB ON ( AAA.ACCNT_NO = BBB.ACCNT_NO AND AAA.RESULT_KEY = BBB.RESULT_KEY AND AAA.REQ_DAY = BBB.REQ_DAY AND AAA.PAY_MTHD = '06' AND NVL(AAA.INI_YN,'N') = 'Y') WHEN MATCHED THEN UPDATE SET AAA.DEL_FG = 'C', AAA.UPD_MAN = #{upd_man}, AAA.UPD_DM = SYSDATE;
/*[002-1] 카드 유승인(이니시스) 실패 업데이트*/ MERGE INTO TB_MEMBER_WDRW_REQ_DAMO AAA USING ( SELECT ACCNT_NO, RESULT_KEY, REQ_DAY FROM TB_MEMBER_WDRW_RESULT WHERE 1=1 AND REQ_DAY = #{req_day} AND PAY_MTHD = '06' AND PAY_KIND = '01' AND NVL(INI_YN,'N') = 'Y' AND NVL(AUTH_YN,'Y') = 'Y' AND NVL(RESULT_CD,'FF') != '00' ) BBB ON ( AAA.ACCNT_NO = BBB.ACCNT_NO AND AAA.RESULT_KEY = BBB.RESULT_KEY AND AAA.REQ_DAY = BBB.REQ_DAY AND AAA.PAY_MTHD = '06' AND NVL(AAA.INI_YN,'N') = 'Y') WHEN MATCHED THEN UPDATE SET AAA.DEL_FG = 'F', AAA.UPD_MAN = #{upd_man}, AAA.UPD_DM = SYSDATE;
/*[003] CMS 유승인 실패 결과 업데이트*/ MERGE INTO TB_MEMBER_WDRW_REQ_DAMO AAA USING ( SELECT ACCNT_NO, RESULT_KEY, REQ_DAY FROM TB_MEMBER_WDRW_RESULT WHERE 1=1 AND REQ_DAY = #{req_day} AND PAY_MTHD = '04' AND NVL(AUTH_YN,'Y') = 'Y' ) BBB ON ( AAA.ACCNT_NO = BBB.ACCNT_NO AND AAA.RESULT_KEY = BBB.RESULT_KEY AND AAA.REQ_DAY = BBB.REQ_DAY AND AAA.PAY_MTHD = '04') WHEN MATCHED THEN UPDATE SET AAA.DEL_FG = 'F', AAA.UPD_MAN = #{upd_man}, AAA.UPD_DM = SYSDATE;
/*[004] CMS 유승인 성공 업데이트*/ UPDATE TB_MEMBER_WDRW_REQ_DAMO AAA SET DEL_FG = 'C', UPD_MAN = #{upd_man}, UPD_DM = SYSDATE WHERE REQ_DAY = #{req_day} AND DEL_FG = 'R' AND PAY_MTHD = '04' AND NVL(AUTH_YN,'Y') = 'Y';
/*[005] 카드 무승인 성공 업데이트*/ MERGE INTO TB_MEMBER_WDRW_REQ_DAMO AAA USING ( SELECT ACCNT_NO, RESULT_KEY, REQ_DAY FROM TB_MEMBER_WDRW_RESULT WHERE 1=1 AND REQ_DAY = #{req_day} AND PAY_KIND = '01' AND NVL(AUTH_YN,'Y') = 'N' AND NVL(RESULT_CD,'99') = '00' ) BBB ON ( AAA.ACCNT_NO = BBB.ACCNT_NO AND AAA.RESULT_KEY = BBB.RESULT_KEY AND AAA.REQ_DAY = BBB.REQ_DAY ) WHEN MATCHED THEN UPDATE SET AAA.DEL_FG = 'C', AAA.UPD_MAN = #{upd_man}, AAA.UPD_DM = SYSDATE;
/*[006] 카드 무승인 실패 업데이트*/ MERGE INTO TB_MEMBER_WDRW_REQ_DAMO AAA USING ( SELECT ACCNT_NO, RESULT_KEY, REQ_DAY FROM TB_MEMBER_WDRW_RESULT WHERE 1=1 AND REQ_DAY = #{req_day} AND PAY_KIND = '01' AND NVL(AUTH_YN,'Y') = 'N' AND NVL(RESULT_CD,'99') != '00' ) BBB ON ( AAA.ACCNT_NO = BBB.ACCNT_NO AND AAA.RESULT_KEY = BBB.RESULT_KEY AND AAA.REQ_DAY = BBB.REQ_DAY) WHEN MATCHED THEN UPDATE SET AAA.DEL_FG = 'F', AAA.UPD_MAN = #{upd_man}, AAA.UPD_DM = SYSDATE ;
/*[007] 카드 무승인 취소 성공 업데이트*/ MERGE INTO TB_MEMBER_REQ_NAUTH_CNCL AAA USING ( SELECT ACCNT_NO, RESULT_KEY, REQ_DAY FROM TB_MEMBER_WDRW_RESULT WHERE 1=1 AND REQ_DAY = #{req_day} AND PAY_KIND = '01' AND NVL(AUTH_YN,'Y') = 'N' AND NVL(RESULT_CD,'99') = '00' ) BBB ON ( AAA.ACCNT_NO = BBB.ACCNT_NO AND AAA.CC_RESULT_KEY = BBB.RESULT_KEY AND AAA.REQ_DAY = BBB.REQ_DAY) WHEN MATCHED THEN UPDATE SET AAA.DEL_FG = 'C', AAA.UPD_MAN = #{upd_man}, AAA.UPD_DM = SYSDATE;
/*[008] 카드 무승인 취소 실패 업데이트*/ MERGE INTO TB_MEMBER_REQ_NAUTH_CNCL AAA USING ( SELECT ACCNT_NO, RESULT_KEY, REQ_DAY FROM TB_MEMBER_WDRW_RESULT WHERE 1=1 AND REQ_DAY = #{req_day} AND PAY_KIND = '01' AND NVL(AUTH_YN,'Y') = 'N' AND NVL(RESULT_CD,'99') != '00' ) BBB ON ( AAA.ACCNT_NO = BBB.ACCNT_NO AND AAA.CC_RESULT_KEY = BBB.RESULT_KEY AND AAA.REQ_DAY = BBB.REQ_DAY) WHEN MATCHED THEN UPDATE SET AAA.DEL_FG = 'F', AAA.UPD_MAN = #{upd_man}, AAA.UPD_DM = SYSDATE;
END;

UPDATE /*ReqCustMap.uptMemJoinStatus*/ MEM_PROD_ACCNT MPA SET KSTBIT = '02' , UPD_DM = SYSDATE , UPD_MAN = #{upd_man}
, JOIN_DT = #{req_day}
, JOIN_DT = TO_CHAR(SYSDATE,'YYYYMMDD')
, JOIN_DT = TO_CHAR(SYSDATE,'YYYYMMDD')
WHERE 1=1 AND DEL_FG = 'N' AND KSTBIT = '01' AND EXISTS ( SELECT ACCNT_NO FROM TB_MEMBER_WDRW_REQ WHERE 1=1 AND ACCNT_NO = MPA.ACCNT_NO
AND RESULT_KEY = #{result_key} AND REQ_PAY_NO = 1
AND DEL_FG = 'C' AND PAY_MTHD = #{pay_mthd} AND REQ_DAY = #{req_day} AND REQ_PAY_NO = 1
)

mapper.insert:
INSERT ALL WHEN TRUE_AMT > 0 THEN INTO PAY_MNG(SEQ,ACCNT_NO, NO, PAY_AMT,PAY_DAY,PAY_MTHD,STAT,REG_DM,REG_MAN,DEL_FG,NEW_YN) VALUES ( NVL(TRUE_CNT,0) + 1, ACCNT_NO, REQ_PAY_NO, TRUE_AMT, REQ_DAY, PAY_MTHD, STAT, --STAT SYSDATE, REG_MAN, 'N', --DEL_FG 'Y' --NEW_N ) WHEN REL_AMT > 0 THEN INTO PAY_MNG_DTL(SEQ,ACCNT_NO, NO, PAY_AMT,PAY_DAY,PAY_MTHD,STAT,REG_DM,REG_MAN,DEL_FG) VALUES ( NVL(REL_CNT,0) + 1, ACCNT_NO, REQ_PAY_NO, REL_AMT, REQ_DAY, PAY_MTHD, STAT, --STAT SYSDATE, REG_MAN, 'N' --DEL_FG ) WHEN ADD_AMT > 0 THEN INTO PAY_MNG_DTL1(SEQ,ACCNT_NO, NO, PAY_AMT,PAY_DAY,PAY_MTHD,STAT,REG_DM,REG_MAN,DEL_FG) VALUES ( NVL(ADD_CNT,0) + 1, ACCNT_NO, REQ_PAY_NO, ADD_AMT, REQ_DAY, PAY_MTHD, STAT, --STAT SYSDATE, REG_MAN, 'N' --DEL_FG )
SELECT * FROM ( SELECT ACCNT_NO, REQ.PROD_CD, REQ_PAY_NO, PAY_MTHD, REQ_DAY, DEL_FG, CASE WHEN REQ_PAY_NO = 1 THEN '0' WHEN (MONTH_CNT - REQ_PAY_NO) = 0 THEN '0' WHEN (MONTH_CNT - REQ_PAY_NO) < 0 THEN '1' ELSE '2' END AS STAT ,
'RTIME_AUTO' AS REG_MAN,
'FILE_AUTO' AS REG_MAN,
'ETC_AUTO' AS REG_MAN,
(SELECT MAX(NVL(SEQ,0)) FROM LF_DMUSER.PAY_MNG WHERE ACCNT_NO = REQ.ACCNT_NO) AS TRUE_CNT, (SELECT MAX(NVL(SEQ,0)) FROM LF_DMUSER.PAY_MNG_DTL WHERE ACCNT_NO = REQ.ACCNT_NO) AS REL_CNT, (SELECT MAX(NVL(SEQ,0)) FROM LF_DMUSER.PAY_MNG_DTL1 WHERE ACCNT_NO = REQ.ACCNT_NO) AS ADD_CNT, (CASE WHEN SUBSTR(PAY_BIT,1,1) != 'X' THEN PND.AMT ELSE 0 END) AS TRUE_AMT, (CASE WHEN SUBSTR(PAY_BIT,2,1) != 'X' THEN PND.REL_AMT ELSE 0 END) AS REL_AMT, (CASE WHEN SUBSTR(PAY_BIT,3,1) != 'X' THEN PND.ADD_AMT ELSE 0 END) AS ADD_AMT FROM TB_MEMBER_WDRW_REQ REQ INNER JOIN PRODUCT_NO_DATA PND ON REQ.PROD_CD = PND.PROD_CD AND PND.NO = REQ.REQ_PAY_NO WHERE 1=1
AND RESULT_KEY = #{result_key}
AND RESULT_KIND = '01' AND REQ.DEL_FG = 'C' AND REQ.PAY_MTHD = #{pay_mthd} AND REQ_DAY = #{req_day}
)

mapper.select:
SELECT SUM(ICNT) AS SUM_ICNT FROM ( SELECT COUNT(*) AS ICNT FROM TB_MEMBER_WDRW_REQ_DAMO WHERE 1=1 AND REQ_BIT = '01' AND RESULT_KIND = '01' AND DEL_FG = 'N' AND AUTH_YN = 'N' AND REQ_DAY = #{req_day}
UNION ALL
SELECT COUNT(*) AS ICNT FROM LF_DMUSER.TB_MEMBER_REQ_NAUTH_CNCL WHERE 1=1 AND DEL_FG IN ('P', 'F') ) WHERE 1=1

mapper.update:
/* ReqCustMap.uptReqNauthResultKey */ BEGIN MERGE INTO TB_MEMBER_WDRW_REQ_DAMO AAA USING ( SELECT * FROM ( SELECT ACCNT_NO, REQ_PAY_NO, PAY_MTHD, REQ_DAY, FRANCHISEE_NO, RESULT_BIT, SUBSTR('000' || DENSE_RANK() OVER(ORDER BY FRANCHISEE_NO ASC),-4) || SUBSTR('00000' || (RANK() OVER(PARTITION BY FRANCHISEE_NO ORDER BY REQ_PAY_NO ASC, ACCNT_NO ASC)),-6) || REQ_DAY AS RANK FROM ( SELECT ACCNT_NO, REQ_PAY_NO, PAY_MTHD, REQ_DAY, FRANCHISEE_NO, 'NA' AS RESULT_BIT FROM TB_MEMBER_WDRW_REQ WHERE 1=1 AND DEL_FG = 'N' AND PAY_MTHD = '06' AND REQ_DAY = #{req_day} AND AUTH_YN = 'N'
UNION ALL
SELECT ACCNT_NO, REQ_PAY_NO, PAY_MTHD, REQ_DAY, FRANCHISEE_NO, 'CC' AS RESULT_BIT FROM TB_MEMBER_REQ_NAUTH_CNCL WHERE DEL_FG = 'N' ) ) MAIN_TABLE WHERE MAIN_TABLE.RESULT_BIT = 'NA' ) BBB ON ( AAA.ACCNT_NO = BBB.ACCNT_NO AND AAA.REQ_DAY = BBB.REQ_DAY AND AAA.REQ_PAY_NO = BBB.REQ_PAY_NO AND AAA.AUTH_YN = 'N' AND AAA.DEL_FG = 'N' )
WHEN MATCHED THEN UPDATE SET AAA.RESULT_KEY = BBB.RANK, AAA.RESULT_KIND = '01', AAA.UPD_MAN = #{upd_man}, AAA.UPD_DM = SYSDATE;
MERGE INTO TB_MEMBER_REQ_NAUTH_CNCL AAA USING ( SELECT * FROM ( SELECT ACCNT_NO, REQ_PAY_NO, PAY_MTHD, REQ_DAY, FRANCHISEE_NO, RESULT_BIT, SUBSTR('000' || DENSE_RANK() OVER(ORDER BY FRANCHISEE_NO ASC),-4) || SUBSTR('00000' || (RANK() OVER(PARTITION BY FRANCHISEE_NO ORDER BY REQ_PAY_NO ASC, ACCNT_NO ASC)),-6) || REQ_DAY AS RANK FROM ( SELECT ACCNT_NO, REQ_PAY_NO, PAY_MTHD, REQ_DAY, FRANCHISEE_NO, 'NA' AS RESULT_BIT FROM TB_MEMBER_WDRW_REQ WHERE 1=1 AND DEL_FG = 'N' AND PAY_MTHD = '06' AND REQ_DAY = #{req_day} AND AUTH_YN = 'N'
UNION ALL
SELECT ACCNT_NO, REQ_PAY_NO, PAY_MTHD, REQ_DAY, FRANCHISEE_NO, 'CC' AS RESULT_BIT FROM TB_MEMBER_REQ_NAUTH_CNCL WHERE DEL_FG = 'N' ) ) MAIN_TABLE WHERE MAIN_TABLE.RESULT_BIT = 'CC' ) BBB ON ( AAA.ACCNT_NO = BBB.ACCNT_NO AND AAA.REQ_DAY = BBB.REQ_DAY AND AAA.REQ_PAY_NO = BBB.REQ_PAY_NO AND AAA.DEL_FG = 'N' )
WHEN MATCHED THEN UPDATE SET AAA.CC_RESULT_KEY = BBB.RANK ;
END ;

mapper.insert:
BEGIN INSERT /* ReqCustMap.insertReqWdrwResult */ INTO TB_MEMBER_WDRW_RESULT ( ACCNT_NO, REQ_DAY, REQ_NO, PAY_KIND, RESULT_KEY, RESULT_CD, RESULT_MSG, PAY_MTHD, AUTH_DT, AUTH_CD, ETC, TID, ICHAE_CD, REG_DM, REG_MAN ) VALUES ( #{accnt_no},
#{req_day},
TO_CHAR(SYSDATE,'YYYYMMDD'),
#{pay_no}, #{pay_kind}, #{result_key}, #{result_cd}, #{result_msg}, #{pay_mthd}, #{auth_dt}, #{auth_cd}, #{etc}, #{result_key}, #{ichae_cd}, SYSDATE, #{emple_no} );
UPDATE TB_MEMBER_WDRW_REQ SET RESULT_KEY = #{result_key} , REQ_DAY = TO_CHAR(SYSDATE,'YYYYMMDD') , UPD_DM = SYSDATE , UPD_MAN = #{emple_no} , RESULT_KIND = #{pay_kind}
, DEL_FG = 'C'
, DEL_FG = 'F'
WHERE 1=1 AND PAY_MTHD = '06' AND DEL_FG = 'N' AND ACCNT_NO = #{accnt_no} AND REQ_DAY = #{req_day} AND REQ_PAY_NO = #{pay_no};
UPDATE TB_MEMBER_WDRW_REQ SET DEL_FG = 'Y' , UPD_DM = SYSDATE , UPD_MAN = #{emple_no} WHERE 1=1 AND ACCNT_NO = #{accnt_no} AND DEL_FG = 'N';
END;

mapper.select:
SELECT /* ReqCustMap.getWdrwResultCount */ COUNT(*) AS WDRW_CNT FROM ( SELECT ACCNT_NO /* 회원번호 */ ,REQ_DAY /* 청구/납입 일자 */ ,REQ_NO /* 청구/납입회차 */ ,PAY_KIND /* 01:파일, 02:실시간, 03:자유,04:기타 */ ,RESULT_KEY /* 결과키(TID, CMS결과키) */ ,RESULT_CD /* 결과코드 */ ,RESULT_MSG /* 결과메세지 */ ,PAY_MTHD /* 납입방법(CMS/카드) */ ,AUTH_DT /* 승인일자 */ ,AUTH_CD /* 승인번호 */ ,ICHAE_CD /* 결과 이체 CD (카드사, 은행) */ ,ETC /* 결과이슈사항(통장기재내역등) */ ,AUTH_YN /* 승인여부(Y:유승인,N:무승인) */ ,INI_YN FROM LF_DMUSER.TB_MEMBER_WDRW_RESULT WHERE 1=1 AND PAY_MTHD = #{pay_mthd} AND PAY_KIND = #{pay_kind} AND AUTH_YN = #{auth_yn} AND REQ_DAY = #{req_day}
AND INI_YN = 'Y'
AND NVL(INI_YN, 'N') != 'Y'
) A WHERE 1=1

SELECT COUNT(*) AS ICNT FROM ( SELECT /* ReqCustMap.getWdrwReqListCount */ ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) PAGE_INDX, /* PAGE INDX */ A.* FROM ( SELECT ACCNT_NO /* 회원번호 */ ,PROD_CD /* 상품코드 */ ,PAY_MTHD /* 산출구분(CMS:04, CARD:06) */ ,REQ_BIT /* 산출상세구분(01:정기, 02:미출금, 03:임의 ,04:연체, 05:임시, 06:고객센터) */ ,STAT /* 상태 */ ,KSTBIT /* 회원상태(01:대기, 02:가입, 03:해약, 04:행사, 09:만기) */ ,TRUE_CNT /* 실납입회수 */ ,REQ_PAY_NO /* 산출청구회차 */ ,PAY_BIT /* 산출비트 */ ,ICHAE_NO /* 이체번호(계좌 혹은 BID) */ ,ICHAE_CD /* 이체코드(은행, 카드사) */ ,REQ_DAY /* 청구일 */ ,BANK_FILIN_BRKDN /* 통장기재내역 */ ,PAY_AMT /* 청구금액 */ ,REG_MAN /* 등록자 */ ,ICHAE_DT /* 정기건 및 연체건 이체일 */ ,REQ_PAY_GUNSU /* 산출청구건수 */ ,UPD_DM /* 수정일 */ ,UPD_MAN /* 수정자 */ ,DEL_FG /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패)) */ ,BIRTH /* 생년월일(CMS용) */ ,BILL_GUBUN /* 36회 카드사 전달 구분값 */ ,RESULT_KEY /* 카드/CMS결과KEY(실시간포함) */ ,RESULT_KIND /* 01:파일, 02:실시간, 03:자유,04:기타 */ ,MONTH_CNT /* 당월회차 */ ,'REQ' AS REQ_CNCL_BIT
,TO_CHAR(REG_DM, 'YYYYMMDD') AS REG_DM /* 등록일 */ FROM TB_MEMBER_WDRW_REQ_2021
,TO_CHAR(REQ_REG_DM, 'YYYYMMDD') AS REG_DM /* 등록일 */ FROM VIEW_MEMBER_WDRW_REQ
,TO_CHAR(REG_DM, 'YYYYMMDD') AS REG_DM /* 등록일 */ FROM TB_MEMBER_WDRW_REQ
WHERE 1=1 AND DEL_FG != 'Y'
AND REQ_DAY BETWEEN #{req_day_stt} AND #{req_day_end}
AND PAY_MTHD = #{pay_mthd}
AND AUTH_YN = #{auth_yn}
AND REQ_BIT = #{req_bit}
AND RESULT_KIND = #{result_kind}
AND DEL_FG IN ('N', 'R')
AND DEL_FG = #{del_fg}
AND ACCNT_NO = #{accnt_no}
UNION ALL
SELECT CNCL.ACCNT_NO /* 회원번호 */ ,REQ.PROD_CD /* 상품코드 */ ,CNCL.PAY_MTHD /* 산출구분(CMS:04, CARD:06) */ ,REQ.REQ_BIT /* 산출상세구분(01:정기, 02:미출금, 03:임의 ,04:연체, 05:임시, 06:고객센터) */ ,REQ.STAT /* 상태 */ ,REQ.KSTBIT /* 회원상태(01:대기, 02:가입, 03:해약, 04:행사, 09:만기) */ ,REQ.TRUE_CNT /* 실납입회수 */ ,CNCL.REQ_PAY_NO /* 산출청구회차 */ ,REQ.PAY_BIT /* 산출비트 */ ,REQ.ICHAE_NO /* 이체번호(계좌 혹은 BID) */ ,REQ.ICHAE_CD /* 이체코드(은행, 카드사) */ ,CNCL.REQ_DAY /* 청구일 */ ,REQ.BANK_FILIN_BRKDN /* 통장기재내역 */ ,CNCL.PAY_AMT /* 청구금액 */ ,CNCL.REG_MAN /* 등록자 */ ,REQ.ICHAE_DT /* 정기건 및 연체건 이체일 */ ,REQ.REQ_PAY_GUNSU /* 산출청구건수 */ ,REQ.UPD_DM /* 수정일 */ ,REQ.UPD_MAN /* 수정자 */ ,CNCL.DEL_FG /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패)) */ ,REQ.BIRTH /* 생년월일(CMS용) */ ,REQ.BILL_GUBUN /* 36회 카드사 전달 구분값 */ ,CNCL.RESULT_KEY /* 카드/CMS결과KEY(실시간포함) */ ,REQ.RESULT_KIND /* 01:파일, 02:실시간, 03:자유,04:기타 */ ,REQ.MONTH_CNT /* 당월회차 */ ,'CNCL' AS REQ_CNCL_BIT ,TO_CHAR(CNCL.REG_DM, 'YYYYMMDD') AS REG_DM /* 등록일 */ FROM LF_DMUSER.TB_MEMBER_REQ_NAUTH_CNCL CNCL INNER JOIN
LF_DMUSER.TB_MEMBER_WDRW_REQ_2021 REQ ON CNCL.ACCNT_NO = REQ.ACCNT_NO AND CNCL.RESULT_KEY = REQ.RESULT_KEY AND CNCL.REQ_PAY_NO = REQ.REQ_PAY_NO
LF_DMUSER.VIEW_MEMBER_WDRW_REQ REQ ON CNCL.ACCNT_NO = REQ.ACCNT_NO AND CNCL.RESULT_KEY = REQ.RESULT_KEY AND CNCL.REQ_PAY_NO = REQ.REQ_PAY_NO
LF_DMUSER.TB_MEMBER_WDRW_REQ REQ ON CNCL.ACCNT_NO = REQ.ACCNT_NO AND CNCL.RESULT_KEY = REQ.RESULT_KEY AND CNCL.REQ_PAY_NO = REQ.REQ_PAY_NO
WHERE 1=1 AND CNCL.DEL_FG != 'Y'
AND CNCL.REQ_DAY BETWEEN #{req_day_stt} AND #{req_day_end}
AND CNCL.PAY_MTHD = #{pay_mthd}
AND REQ.REQ_BIT = #{req_bit}
AND REQ.RESULT_KIND = #{result_kind}
AND CNCL.DEL_FG IN ('N', 'R')
AND CNCL.DEL_FG = #{del_fg}
AND CNCL.ACCNT_NO = #{accnt_no}
) A WHERE 1=1
AND A.REQ_CNCL_BIT = #{req_cncl_bit}
) A WHERE 1=1

SELECT * FROM ( SELECT /* ReqCustMap.getPayWdrwReqList */ ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) PAGE_INDX, /* PAGE INDX */ A.* FROM ( SELECT ACCNT_NO /* 회원번호 */ ,PROD_CD /* 상품코드 */ ,PAY_MTHD /* 산출구분(CMS:04, CARD:06) */ ,REQ_BIT /* 산출상세구분(01:정기, 02:미출금, 03:임의 ,04:연체, 05:임시, 06:고객센터) */ ,STAT /* 상태 */ ,KSTBIT /* 회원상태(01:대기, 02:가입, 03:해약, 04:행사, 09:만기) */ ,TRUE_CNT /* 실납입회수 */ ,REQ_PAY_NO /* 산출청구회차 */ ,PAY_BIT /* 산출비트 */ ,ICHAE_NO /* 이체번호(계좌 혹은 BID) */ ,ICHAE_CD /* 이체코드(은행, 카드사) */ ,REQ_DAY /* 청구일 */ ,BANK_FILIN_BRKDN /* 통장기재내역 */ ,PAY_AMT /* 청구금액 */ ,REG_MAN /* 등록자 */ ,ICHAE_DT /* 정기건 및 연체건 이체일 */ ,REQ_PAY_GUNSU /* 산출청구건수 */ ,UPD_DM /* 수정일 */ ,UPD_MAN /* 수정자 */ ,DEL_FG /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패)) */ ,BIRTH /* 생년월일(CMS용) */ ,BILL_GUBUN /* 36회 카드사 전달 구분값 */ ,RESULT_KEY /* 카드/CMS결과KEY(실시간포함) */ ,RESULT_KIND /* 01:파일, 02:실시간, 03:자유,04:기타 */ ,MONTH_CNT /* 당월회차 */ ,'REQ' AS REQ_CNCL_BIT
,TO_CHAR(REG_DM, 'YYYYMMDD') AS REG_DM /* 등록일 */ FROM LF_DMUSER.TB_MEMBER_WDRW_REQ_2021
,TO_CHAR(REQ_REG_DM, 'YYYYMMDD') AS REG_DM /* 등록일 */ FROM LF_DMUSER.VIEW_MEMBER_WDRW_REQ
,TO_CHAR(REG_DM, 'YYYYMMDD') AS REG_DM /* 등록일 */ FROM LF_DMUSER.TB_MEMBER_WDRW_REQ
WHERE 1=1 AND DEL_FG != 'Y'
AND REQ_DAY BETWEEN #{req_day_stt} AND #{req_day_end}
AND PAY_MTHD = #{pay_mthd}
AND AUTH_YN = #{auth_yn}
AND REQ_BIT = #{req_bit}
AND RESULT_KIND = #{result_kind}
AND DEL_FG IN ('N', 'R')
AND DEL_FG = #{del_fg}
AND ACCNT_NO = #{accnt_no}
UNION ALL
SELECT CNCL.ACCNT_NO /* 회원번호 */ ,REQ.PROD_CD /* 상품코드 */ ,CNCL.PAY_MTHD /* 산출구분(CMS:04, CARD:06) */ ,REQ.REQ_BIT /* 산출상세구분(01:정기, 02:미출금, 03:임의 ,04:연체, 05:임시, 06:고객센터) */ ,REQ.STAT /* 상태 */ ,REQ.KSTBIT /* 회원상태(01:대기, 02:가입, 03:해약, 04:행사, 09:만기) */ ,REQ.TRUE_CNT /* 실납입회수 */ ,CNCL.REQ_PAY_NO /* 산출청구회차 */ ,REQ.PAY_BIT /* 산출비트 */ ,REQ.ICHAE_NO /* 이체번호(계좌 혹은 BID) */ ,REQ.ICHAE_CD /* 이체코드(은행, 카드사) */ ,CNCL.REQ_DAY /* 청구일 */ ,REQ.BANK_FILIN_BRKDN /* 통장기재내역 */ ,CNCL.PAY_AMT /* 청구금액 */ ,CNCL.REG_MAN /* 등록자 */ ,REQ.ICHAE_DT /* 정기건 및 연체건 이체일 */ ,REQ.REQ_PAY_GUNSU /* 산출청구건수 */ ,REQ.UPD_DM /* 수정일 */ ,REQ.UPD_MAN /* 수정자 */ ,CNCL.DEL_FG /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패)) */ ,REQ.BIRTH /* 생년월일(CMS용) */ ,REQ.BILL_GUBUN /* 36회 카드사 전달 구분값 */ ,CNCL.RESULT_KEY /* 카드/CMS결과KEY(실시간포함) */ ,REQ.RESULT_KIND /* 01:파일, 02:실시간, 03:자유,04:기타 */ ,REQ.MONTH_CNT /* 당월회차 */ ,'CNCL' AS REQ_CNCL_BIT ,TO_CHAR(CNCL.REG_DM, 'YYYYMMDD') AS REG_DM /* 등록일 */ FROM LF_DMUSER.TB_MEMBER_REQ_NAUTH_CNCL CNCL INNER JOIN
LF_DMUSER.TB_MEMBER_WDRW_REQ_2021 REQ ON CNCL.ACCNT_NO = REQ.ACCNT_NO AND CNCL.RESULT_KEY = REQ.RESULT_KEY AND CNCL.REQ_PAY_NO = REQ.REQ_PAY_NO
LF_DMUSER.VIEW_MEMBER_WDRW_REQ REQ ON CNCL.ACCNT_NO = REQ.ACCNT_NO AND CNCL.RESULT_KEY = REQ.RESULT_KEY AND CNCL.REQ_PAY_NO = REQ.REQ_PAY_NO
LF_DMUSER.TB_MEMBER_WDRW_REQ REQ ON CNCL.ACCNT_NO = REQ.ACCNT_NO AND CNCL.RESULT_KEY = REQ.RESULT_KEY AND CNCL.REQ_PAY_NO = REQ.REQ_PAY_NO
WHERE 1=1 AND CNCL.DEL_FG != 'Y'
AND CNCL.REQ_DAY BETWEEN #{req_day_stt} AND #{req_day_end}
AND CNCL.PAY_MTHD = #{pay_mthd}
AND REQ.REQ_BIT = #{req_bit}
AND REQ.RESULT_KIND = #{result_kind}
AND CNCL.DEL_FG IN ('N', 'R')
AND CNCL.DEL_FG = #{del_fg}
AND CNCL.ACCNT_NO = #{accnt_no}
) A WHERE 1=1
AND A.REQ_CNCL_BIT = #{req_cncl_bit}
) A WHERE 1=1
AND PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT /* ReqCustMap.getCardNauthRecvFileExist */
       COUNT(*) AS ICNT
  FROM LF_DMUSER.TB_CARDNAUTH_RECV_FILE_LIST
 WHERE 1=1
   AND REQ_MONTH = #{req_month}
