SELECT /* ReqCustMap.getCntSpecialAcntList */ COUNT(*) FROM TB_MEMBER_EXT_SPECIAL MES INNER JOIN MEM_PROD_ACCNT MPA ON MES.ACCNT_NO = MPA.ACCNT_NO AND MPA.DEL_FG = 'N' INNER JOIN MEMBER MBR ON MPA.MEM_NO = MBR.MEM_NO AND MBR.DEL_FG = 'N' WHERE 1=1
AND MES.DEL_FG = #{del_fg}
AND MES.ACCNT_NO = #{accnt_no}
AND MES.SPECIAL_BIT = #{special_bit}

SELECT /* ReqCustMap.getSpecialAcntList */ Z.* FROM (SELECT X.*,
ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) PAGE_INDX
FROM ( SELECT MES.ACCNT_NO ,REPLACE(MBR.MEM_NM,CHR(0),'') AS MEM_NM ,REPLACE(MBR.CELL,CHR(0),'') AS CELL ,MPA.KSTBIT ,MES.REQ_PAY_GUNSU ,MES.SPECIAL_BIT ,MBID.PROD_CD ,MBID.TRUE_CNT ,MBID.REL_CNT ,MBID.ADD_CNT ,CM.ICHAE_DT ,REPLACE(TO_CHAR(MES.REG_DM, 'YYYY-MM-DD HH24:MI:SS'),CHR(0),'') AS REG_DM ,REPLACE(( SELECT EMPLE_NM FROM EMPLOYEE WHERE EMPLE_NO = MES.REG_MAN ),CHR(0),'') AS REG_MAN ,MBID.MONTH_COUNT - MBID.TRUE_CNT AS NOR_YEON_CHE ,CASE WHEN MBID.MONTH_COUNT > MBID.TOT_REL_CNT THEN MBID.TOT_REL_CNT - MBID.REL_CNT WHEN MBID.MONTH_COUNT <= MBID.TOT_REL_CNT THEN MBID.MONTH_COUNT - MBID.REL_CNT ELSE -999 END AS REL_YEON_CHE ,CASE WHEN MBID.MONTH_COUNT > MBID.TOT_ADD_CNT THEN MBID.TOT_ADD_CNT - MBID.ADD_CNT WHEN MBID.MONTH_COUNT <= MBID.TOT_ADD_CNT THEN MBID.MONTH_COUNT - MBID.ADD_CNT ELSE -999 END AS ADD_YEON_CHE ,MBID.TRUE_DAY ,MBID.REL_DAY ,MBID.ADD_DAY ,REPLACE(MES.SPECIAL_ETC,CHR(0),'') AS SPECIAL_ETC ,FN_COM_NM('01', MPA.PAY_MTHD) AS PAY_MTHD ,REPLACE(TO_CHAR(MES.UPD_DM, 'YYYY-MM-DD HH24:MI:SS'),CHR(0),'') AS UPD_DM ,REPLACE(( SELECT EMPLE_NM FROM EMPLOYEE WHERE EMPLE_NO = MES.UPD_MAN ),CHR(0),'') AS UPD_MAN FROM TB_MEMBER_EXT_SPECIAL MES INNER JOIN MEM_PROD_ACCNT MPA ON MES.ACCNT_NO = MPA.ACCNT_NO AND MPA.DEL_FG = 'N' INNER JOIN MEMBER MBR ON MPA.MEM_NO = MBR.MEM_NO AND MBR.DEL_FG = 'N' LEFT OUTER JOIN TB_MEMBER_BASIC_INFO_DAY MBID ON MES.ACCNT_NO = MBID.ACCNT_NO LEFT OUTER JOIN ( SELECT ACCNT_NO, '06' AS PAY_MTHD, PAY_DT AS ICHAE_DT FROM CARD_MEM CARD WHERE CARD.DEL_FG = 'N'
UNION
SELECT ACCNT_NO, '04' AS PAY_MTHD, ICHAE_DT FROM CMS_MEM CMS WHERE CMS.DEL_FG = 'N' ) CM ON MBID.ACCNT_NO = CM.ACCNT_NO AND MBID.PAY_MTHD = CM.PAY_MTHD WHERE 1=1
AND MES.DEL_FG = #{del_fg}
AND MES.ACCNT_NO = #{accnt_no}
AND MES.SPECIAL_BIT = #{special_bit}
) X ) Z
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

