mapper.select:
    	/* ReqCustMap.getReqRltmCardLogCount */
    	SELECT         
    		COUNT(*) AS ICNT       
		FROM LF_DMUSER.TB_MEMBER_WDRW_REQ REQ INNER JOIN
            PRODUCT PD ON REQ.PROD_CD = PD.PROD_CD
            LEFT OUTER JOIN LF_DMUSER.TB_MEMBER_WDRW_RESULT RST
                         ON REQ.ACCNT_NO = RST.ACCNT_NO
                        AND REQ.PAY_MTHD = RST.PAY_MTHD
                        AND REQ.REQ_DAY = RST.REQ_DAY
                        AND REQ.RESULT_KEY = RST.RESULT_KEY
            LEFT OUTER JOIN LF_DMUSER.TB_MEMBER_REQ_REFUND MRR
                         ON MRR.ACCNT_NO = REQ.ACCNT_NO
                        AND MRR.PAY_MTHD = REQ.PAY_MTHD
                        AND MRR.DEL_FG = 'N'
                        AND MRR.REQ_DAY = REQ.REQ_DAY
                      --AND MRR.RESULT_KEY = REQ.RESULT_KEY
                        AND NVL(MRR.RESULT_KEY,'JL32') = NVL(REQ.RESULT_KEY,'JL32') /* JL32 : PGWEB 서버 통신 중 에러 */
        WHERE 1=1

            AND REQ.ACCNT_NO = #{accnt_no}

        AND REQ.PAY_MTHD = #{pay_mthd}
        AND REQ.DEL_FG IN ('C', 'F', 'X')

            AND REQ.DEL_FG = #{del_fg}


            AND REQ.REQ_DAY BETWEEN #{stt_req_dt} AND #{end_req_dt}


            AND REQ.RESULT_KIND = #{result_kind}


            AND REQ.REQ_BIT = #{req_bit}


         	AND REQ.AUTH_YN = #{auth_yn}

SELECT /* ReqCustMap.getReqRltmCardLogList */ * FROM (SELECT ROW_NUMBER() OVER(ORDER BY REQ_DAY DESC, REG_DM DESC, REQ_PAY_NO DESC, ACCNT_NO) PAGE_INDX,
X.* FROM ( SELECT REQ.ACCNT_NO, REQ.MEM_NO, PD.PROD_NM, REQ_BIT, REQ.TRUE_CNT, /* 실납입횟수 */ REQ.REQ_PAY_NO, /* 청구회차 */ '조회' AS MAX_REQ_PAY_NO, TO_CHAR(RST.REG_DM, 'YYYYMMDDHH24MISS') AS REG_DM, /* 결과입력시간*/ '' AS MAX_REG_DM, /* (TEMP) 회원번호 기준 MAX 결과입력시간 */ REQ.PAY_BIT, REQ.ICHAE_NO, REQ.REQ_DAY, PAY_AMT, REQ.RESULT_KEY, RST.TID, RST.PAY_KIND AS RESULT_KIND,
RST.RESULT_CD, CASE WHEN REQ.PAY_MTHD = '04' THEN FN_COM_NM('15', RESULT_CD) WHEN REQ.PAY_MTHD = '06' AND NVL(REQ.AUTH_YN,'Y') = 'N' THEN FN_COM_NM('0222', RESULT_CD) ELSE RST.RESULT_MSG END AS RESULT_MSG, AUTH_DT, AUTH_CD, (SELECT COUNT(*) FROM TB_MEMBER_REQ_NAUTH_CNCL WHERE ACCNT_NO = REQ.ACCNT_NO AND REQ_PAY_NO = REQ.REQ_PAY_NO AND DEL_FG IN ('P','N','R')) AS CNCL_CNT, CASE WHEN REQ.PAY_MTHD = '04' THEN FN_COM_NM('63', NVL(RST.ICHAE_CD,REQ.ICHAE_CD)) WHEN REQ.PAY_MTHD = '06' AND RST.PAY_KIND = '01' AND NVL(REQ.INI_YN, 'N') != 'Y' THEN RST.ICHAE_CD WHEN REQ.PAY_MTHD = '06' AND RST.PAY_KIND != '01' AND NVL(REQ.INI_YN, 'N') != 'Y' THEN FN_COM_NM('0120', NVL(RST.ICHAE_CD,REQ.ICHAE_CD)) WHEN REQ.PAY_MTHD = '06' AND RST.PAY_KIND = '01' AND NVL(REQ.INI_YN, 'N') = 'Y' THEN FN_COM_NM('0196', NVL(RST.ICHAE_CD,REQ.ICHAE_CD)) WHEN REQ.PAY_MTHD = '06' AND RST.PAY_KIND != '01' AND NVL(REQ.INI_YN, 'N') = 'Y' THEN FN_COM_NM('0196', NVL(RST.ICHAE_CD,REQ.ICHAE_CD)) ELSE '' END AS ICHAE_CD,
MRR.REG_DM AS REFUND_REG_DT, /* 환불등록일 */
TO_CHAR(MRR.CANCEL_DT, 'YYYYMMDDHH24MISS') AS CANCEL_DT, /* 실제취소일*/ RST.ETC, REQ.PAY_MTHD, /* 납입방법 (04:CMS, 06:카드) */ REQ.DEL_FG, CASE WHEN LENGTH(RST.REG_MAN) = 10 THEN ( SELECT EMPLE_NM FROM EMPLOYEE WHERE EMPLE_NO = RST.REG_MAN ) ELSE RST.REG_MAN END AS REG_MAN, /* 결과등록자 사원명으로 표시 */ REQ.AUTH_YN, REQ.FRANCHISEE_NO, REQ.INI_YN FROM LF_DMUSER.TB_MEMBER_WDRW_REQ REQ INNER JOIN PRODUCT PD ON REQ.PROD_CD = PD.PROD_CD LEFT OUTER JOIN LF_DMUSER.TB_MEMBER_WDRW_RESULT RST ON REQ.ACCNT_NO = RST.ACCNT_NO AND REQ.PAY_MTHD = RST.PAY_MTHD AND REQ.REQ_DAY = RST.REQ_DAY AND REQ.RESULT_KEY = RST.RESULT_KEY LEFT OUTER JOIN LF_DMUSER.TB_MEMBER_REQ_REFUND MRR ON MRR.ACCNT_NO = REQ.ACCNT_NO AND MRR.PAY_MTHD = REQ.PAY_MTHD AND MRR.DEL_FG = 'N' AND MRR.REQ_DAY = REQ.REQ_DAY --AND MRR.RESULT_KEY = REQ.RESULT_KEY AND NVL(MRR.RESULT_KEY,'JL32') = NVL(REQ.RESULT_KEY,'JL32') /* JL32 : PGWEB 서버 통신 중 에러 */ WHERE 1=1
AND REQ.ACCNT_NO = #{accnt_no}
AND REQ.PAY_MTHD = #{pay_mthd} AND REQ.DEL_FG IN ('C', 'F', 'X')
AND REQ.AUTH_YN = #{auth_yn}
AND REQ.DEL_FG = #{del_fg}
AND REQ.REQ_DAY BETWEEN #{stt_req_dt} AND #{end_req_dt}
AND REQ.RESULT_KIND = #{result_kind}
AND REQ.REQ_BIT = #{req_bit}
) X WHERE 1=1 ) Z
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT /* ReqCustMap.getDlwCustAcntList */ MPA.ACCNT_NO, MB.MEM_NO, MB.MEM_NM, FN_ACCNT_STAT(MPA.ACCNT_NO) AS ACCNT_STAT, NVL(PD.PROD_LONGTERM_YN,'N') AS PROD_LONGTERM_YN, NVL(TLM.LTI_PROD_PAY,'N') AS LTI_PROD_PAY, NVL(LTI_USE_YN,NVL(PD.PROD_LONGTERM_YN,'N')) AS LTI_USE_YN FROM MEM_PROD_ACCNT MPA INNER JOIN MEMBER MB ON MPA.MEM_NO = MB.MEM_NO AND MB.DEL_FG = 'N' INNER JOIN PRODUCT PD ON MPA.PROD_CD = PD.PROD_CD LEFT OUTER JOIN TB_LTINSTALL_MNG TLM ON MPA.ACCNT_NO = TLM.ACCNT_NO AND TLM.DEL_FG = 'N' WHERE 1=1 AND MPA.DEL_FG = 'N'
AND MB.MEM_NM LIKE #{srch_vl} || '%'
AND MPA.ACCNT_NO = #{srch_vl}
AND MB.MEM_NO = #{srch_vl}

