	                UPDATE TB_MEMBER_WDRW_REQ_2021
			        SET DEL_FG = 'X', 
			            UPD_MAN =  #{emple_no},
			            UPD_DM = SYSDATE
			        WHERE 1=1
			        AND PAY_MTHD = '06'
			        AND DEL_FG = 'C'
			        AND ACCNT_NO = #{accnt_no} 
			        AND REQ_DAY = #{req_day}
			        AND RESULT_KEY = #{result_key};


	                UPDATE TB_MEMBER_WDRW_REQ
			        SET DEL_FG = 'X', 
			            UPD_MAN =  #{emple_no},
			            UPD_DM = SYSDATE
			        WHERE 1=1
			        AND PAY_MTHD = '06'
			        AND DEL_FG = 'C'
			        AND ACCNT_NO = #{accnt_no} 
			        AND REQ_DAY = #{req_day}
			        AND RESULT_KEY = #{result_key};





		        UPDATE LF_DMUSER.HP_CALL
		        SET STAT = '15',
		            UPD_MAN =  #{emple_no}, 
		            UPD_DM = SYSDATE
		        WHERE 1=1
		        AND ACCNT_NO = #{accnt_no} ;

		        UPDATE LF_DMUSER.TB_LTINSTALL_MNG
		        SET LTI_PROD_PAY = 'N',
		        	DEL_FG = 'Y',
		            UPD_MAN =  #{emple_no},
		            UPD_DM = SYSDATE
		        WHERE 1=1
		        AND ACCNT_NO = #{accnt_no} 
		        AND DEL_FG = 'N'
		        ;



        	COMMIT;

        END ;


