BEGIN /* ReqCustMap.saveTransInicisCardCancel */ INSERT INTO TB_MEMBER_REQ_REFUND ( ACCNT_NO , PAY_MTHD , REQ_DAY , RESULT_KEY , REFUND_LOC /* 환불 경로(01:결과파일환불,02:해약등록,03:청약철회등록,04:행사등록) */ , ICHAE_NO /* 은행이체번호(CMS) */ , ICHAE_CD /* 은행이체코드(CMS) */ , DEPR /* 예금주(CMS) */ , REFUND_DT /* 환불일자 */ , REFUND_ETC /* 비고 */ , DEL_FG , REG_DM , REG_MAN , REFUND_REASON /* 환불사유(0001:환불,0002:입력오류,0003:기타,0004:매입취소) */ , CANCEL_DT , REQ_PAY_NO /* 청구회차 */ ) VALUES ( #{accnt_no} , #{pay_mthd} , #{req_day} , #{result_key} , '01' , '' /* ICHAE_NO */ , '' /* ICHAE_CD */ , '' /* DEPR */ , #{pay_cancel_day} /* REFUND_DT */ , #{pay_cancel_note} , 'N' , SYSDATE , #{emple_no} , #{pay_cancel_cd} , SYSDATE , #{req_pay_no} );
UPDATE TB_MEMBER_WDRW_REQ_2021 SET DEL_FG = 'X', UPD_MAN = #{emple_no}, UPD_DM = SYSDATE WHERE 1=1 AND PAY_MTHD = '06' AND DEL_FG = 'C' AND REQ_DAY = #{req_day} AND RESULT_KEY = #{result_key};
UPDATE TB_MEMBER_WDRW_REQ SET DEL_FG = 'X', UPD_MAN = #{emple_no}, UPD_DM = SYSDATE WHERE 1=1 AND PAY_MTHD = '06' AND DEL_FG = 'C' AND REQ_DAY = #{req_day} AND RESULT_KEY = #{result_key};
END;

