AND DEL_FG != 'Y'
AND DEL_FG = #{del_fg}
AND REG_DM BETWEEN TO_DATE(#{refund_dt_from} || '000000', 'YYYYMMDDHH24MISS') AND TO_DATE(#{refund_dt_to} || '235959', 'YYYYMMDDHH24MISS')
AND REQ_DAY BETWEEN #{refund_dt_from} AND #{refund_dt_to}
) MAIN_TBL WHERE 1=1

