   AND REQ_PAY_NO  = #{req_pay_no}

BEGIN /* ReqCustMap.updateMemberMangiDataList */ UPDATE TB_MEMBER_REQ_REFUND SET CANCEL_DT = SYSDATE , REFUND_DT = SYSDATE WHERE 1=1 AND PAY_MTHD = '06' AND (ACCNT_NO, RESULT_KEY, REQ_PAY_NO) IN (SELECT ACCNT_NO, RESULT_KEY, REQ_PAY_NO FROM TB_MEMBER_REQ_NAUTH_CNCL WHERE 1=1 AND DEL_FG = 'C');
MERGE INTO TB_MEMBER_WDRW_REQ_DAMO AAA USING ( SELECT ACCNT_NO, PRE_REQ_DAY, RESULT_KEY FROM TB_MEMBER_REQ_NAUTH_CNCL WHERE 1=1 AND DEL_FG = 'C' ) BBB ON (AAA.ACCNT_NO = BBB.ACCNT_NO AND AAA.REQ_DAY = BBB.PRE_REQ_DAY AND AAA.RESULT_KEY = BBB.RESULT_KEY) WHEN MATCHED THEN UPDATE SET AAA.DEL_FG = 'X', AAA.UPD_MAN = #{emple_no}, AAA.UPD_DM = SYSDATE ;
UPDATE LF_DMUSER.TB_MEMBER_REQ_NAUTH_CNCL SET DEL_FG = 'X' WHERE 1=1 AND DEL_FG = 'C';
UPDATE LF_DMUSER.TB_MEMBER_REQ_NAUTH_CNCL SET DEL_FG = 'P' WHERE 1=1 AND DEL_FG = 'F'; END;

mapper.select:
SELECT /* ReqCustMap.getMemberRefundListCount */ COUNT(*) AS ICNT FROM (
SELECT *
FROM ( SELECT MRR.ACCNT_NO /* 회원번호 */ ,MBR.MEM_NO /* 고유번호 */ ,MBR.MEM_NM /* 회원명 */ ,MRR.PAY_MTHD /* 납입방법(04:CMS, 06:Card) */ ,CASE WHEN MRR.CANCEL_DT IS NOT NULL THEN CASE WHEN MWR.RESULT_KIND = '01' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > TO_CHAR(TO_DATE(AUTH_DT), 'YYMMDD') THEN '후취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'N' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > SUBSTR(AUTH_DT, 1, 6) THEN '후취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'Y' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > SUBSTR(AUTH_DT, 3, 8) THEN '후취소' WHEN MWR.RESULT_KIND = '01' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = TO_CHAR(TO_DATE(AUTH_DT), 'YYMMDD') THEN '취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'N' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = SUBSTR(AUTH_DT, 1, 6) THEN '취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'Y' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = SUBSTR(AUTH_DT, 3, 8) THEN '취소' ELSE '' END ELSE '' END AS CANCEL_GB ,MWR.PAY_BIT /* PAYBIT */ ,MRR.REQ_DAY /* 청구일자 */ ,MWR.PAY_AMT /* 청구금액 */ ,MWR.KSTBIT /* 회원상태(01:대기, 02:가입, 03:해약, 04:행사, 09:만기) */ ,MRR.RESULT_KEY /* 결과키 */ ,MRR.REFUND_LOC /* 환불 경로(01:결과파일환불,02:해약등록,03:청약철회등록,04:행사등록) */ ,MRR.ICHAE_NO /* 은행이체번호(CMS) */ ,MRR.ICHAE_CD /* 은행이체코드(CMS) */ ,MRR.DEPR /* 예금주(CMS) */ ,MRR.REFUND_DT /* 환불일자 */ ,MRR.REG_DM /* 등록일자 */ ,MRR.REG_MAN /* 등록자ID */ ,MWR.DEL_FG /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패) X:입금취소) */ ,MWR.REQ_PAY_NO /* 산출청구회차 */ ,MWS.RESULT_CD /* 결과코드 */ ,MWS.RESULT_MSG /* 결과메세지 */
,TO_CHAR(MRR.CANCEL_DT, 'YYYYMMDDHH24MISS') AS CANCEL_DT /* 실시간 취소 일자 */ ,MWS.TID /* 결과키(TID, CMS결과키) */ ,AUTH_DT /* 승인일자 */ ,MWS.AUTH_CD /* 승인번호 */ ,MRR.REFUND_REASON /* 환불사유(환불사유코드) */ ,MRR.REFUND_ETC /* 비고 */ ,MRR.AUTH_YN ,(SELECT DEL_FG FROM TB_MEMBER_REQ_NAUTH_CNCL CNCL WHERE 1=1 AND CNCL.ACCNT_NO = MRR.ACCNT_NO AND CNCL.REQ_PAY_NO = MRR.REQ_PAY_NO AND CNCL.DEL_FG != 'Y') AS CNCL_DEL_FG FROM TB_MEMBER_REQ_REFUND MRR /* 고객환불정보 */ LEFT OUTER JOIN TB_MEMBER_WDRW_REQ MWR /* 고객청구요청정보 */ ON MRR.ACCNT_NO = MWR.ACCNT_NO AND MRR.PAY_MTHD = MWR.PAY_MTHD AND MRR.REQ_DAY = MWR.REQ_DAY AND MRR.REQ_PAY_NO = MWR.REQ_PAY_NO AND MRR.RESULT_KEY = MWR.RESULT_KEY LEFT OUTER JOIN TB_MEMBER_WDRW_RESULT MWS /* 고객청구결과정보 */ ON MWR.ACCNT_NO = MWS.ACCNT_NO AND MWR.RESULT_KEY = MWS.RESULT_KEY AND MWR.REQ_DAY = MWS.REQ_DAY INNER JOIN MEM_PROD_ACCNT MPA /* 고객가입상품정보 */ ON MPA.ACCNT_NO = MRR.ACCNT_NO AND MPA.DEL_FG = 'N' INNER JOIN MEMBER MBR /* 고객기본정보 */ ON MBR.MEM_NO = MPA.MEM_NO AND MBR.DEL_FG = 'N' LEFT OUTER JOIN PRODUCT_NO_DATA PND /* 상품회차별정보 */ ON MWR.PROD_CD = PND.PROD_CD AND MWR.REQ_PAY_NO = PND.NO WHERE 1=1
AND TO_CHAR(MRR.REG_DM,'YYYYMMDD') BETWEEN #{reg_dm_stt} AND #{reg_dm_end}
AND TO_CHAR(MRR.CANCEL_DT,'YYYYMMDD') BETWEEN #{reg_dm_stt} AND #{reg_dm_end}
AND MRR.ACCNT_NO = #{accnt_no}
AND MBR.MEM_NM = #{mem_nm}
AND MRR.REFUND_LOC = #{refund_loc}
AND MWR.DEL_FG = #{req_stat} /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패) X:입금취소) */
AND MRR.REQ_DAY = #{req_day} /* 청구일자 */
AND MRR.PAY_MTHD = #{pay_mthd} /* 납입방법(04:CMS, 06:Card) */
AND MRR.AUTH_YN = #{auth_yn}
AND MWR.REQ_DAY >= '20210101' AND NVL(TO_CHAR(MRR.CANCEL_DT,'YYYYMMDD'),'99999999') = '99999999'
AND MRR.DEL_FG = 'N' ) MAIN_TBL ) WHERE 1=1
AND CANCEL_GB = #{cancel_gb}

SELECT /* ReqCustMap.getMemberRefundList */ * FROM
( SELECT ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) PAGE_INDX /* PAGE INDX */ , MAIN_TBL.* FROM ( SELECT MRR.ACCNT_NO /* 회원번호 */ ,MWR.PROD_CD ,(SELECT MAX(PD.PROD_NM) FROM PRODUCT PD WHERE 1=1 AND PD.PROD_CD = MWR.PROD_CD) AS PROD_NM ,MWR.ICHAE_NO AS BID ,MBR.MEM_NO /* 고유번호 */ ,MBR.MEM_NM /* 회원명 */ ,MRR.PAY_MTHD /* 납입방법(04:CMS, 06:Card) */ ,CASE WHEN MRR.CANCEL_DT IS NOT NULL THEN CASE WHEN MWR.RESULT_KIND = '01' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > TO_CHAR(TO_DATE(AUTH_DT), 'YYMMDD') THEN '후취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'N' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > SUBSTR(AUTH_DT, 1, 6) THEN '후취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'Y' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > SUBSTR(AUTH_DT, 3, 8) THEN '후취소' WHEN MWR.RESULT_KIND = '01' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = TO_CHAR(TO_DATE(AUTH_DT), 'YYMMDD') THEN '취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'N' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = SUBSTR(AUTH_DT, 1, 6) THEN '취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'Y' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = SUBSTR(AUTH_DT, 3, 8) THEN '취소' ELSE '' END ELSE '' END AS CANCEL_GB ,MWR.PAY_BIT /* PAYBIT */ ,MRR.REQ_DAY /* 청구일자 */ ,MWR.PAY_AMT /* 청구금액 */ ,(CASE WHEN SUBSTR(MWR.PAY_BIT,1,1) != 'X' THEN PND.AMT ELSE 0 END) AS TRUE_AMT /* 상조금 */ ,(CASE WHEN SUBSTR(MWR.PAY_BIT,2,1) != 'X' THEN PND.REL_AMT ELSE 0 END) AS REL_AMT /* 결합금 */ ,(CASE WHEN SUBSTR(MWR.PAY_BIT,3,1) != 'X' THEN PND.ADD_AMT ELSE 0 END) AS ADD_AMT /* 추가금 */ ,MWR.KSTBIT /* 회원상태(01:대기, 02:가입, 03:해약, 04:행사, 09:만기) */ ,MRR.RESULT_KEY /* 결과키 */ ,MWR.RESULT_KIND /* 결과유형 (01:파일, 02:실시간, 03:자유,04:기타) */ ,MRR.REFUND_LOC /* 환불 경로(01:결과파일환불,02:해약등록,03:청약철회등록,04:행사등록) */
,CASE WHEN MWR.PAY_MTHD = '04' THEN CASE WHEN NVL(MRR.ICHAE_NO, 'N') = 'N' THEN MWR.ICHAE_NO ELSE MRR.ICHAE_NO END ELSE '' END AS ICHAE_NO ,CASE WHEN MWR.PAY_MTHD = '04' THEN CASE WHEN NVL(MRR.ICHAE_CD, 'N') = 'N' THEN MWR.ICHAE_CD ELSE MRR.ICHAE_CD END ELSE '' END AS ICHAE_CD ,CASE WHEN MWR.PAY_MTHD = '06' AND NVL(MWR.INI_YN, 'N') != 'Y' THEN LF_DMUSER.FN_COM_NM('0120', MWR.ICHAE_CD) WHEN MWR.PAY_MTHD = '06' AND NVL(MWR.INI_YN, 'N') = 'Y' THEN LF_DMUSER.FN_COM_NM('0196', MWR.ICHAE_CD) ELSE '' END AS CARD_CD ,MRR.DEPR /* 예금주(CMS) */ ,MRR.REFUND_DT /* 환불일자 */ ,MRR.REG_DM /* 등록일자 */ ,MRR.REG_MAN /* 등록자ID */ ,MWR.DEL_FG /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패) X:입금취소) */ ,MWR.REQ_PAY_NO /* 산출청구회차 */ ,MWS.RESULT_CD /* 결과코드 */ ,MWS.RESULT_MSG /* 결과메세지 */
,TO_CHAR(MRR.CANCEL_DT, 'YYYYMMDDHH24MISS') AS CANCEL_DT /* 실시간 취소 일자 */ ,MWS.TID /* 결과키(TID, CMS결과키) */ ,AUTH_DT /* 승인일자 */ ,MWS.AUTH_CD /* 승인번호 */ ,MRR.REFUND_REASON /* 환불사유(환불사유코드) */ ,MRR.REFUND_ETC /* 비고 */ ,MRR.AUTH_YN ,MWR.FRANCHISEE_NO ,MWR.INI_YN ,(SELECT DEL_FG FROM TB_MEMBER_REQ_NAUTH_CNCL CNCL WHERE 1=1 AND CNCL.ACCNT_NO = MRR.ACCNT_NO AND CNCL.REQ_PAY_NO = MRR.REQ_PAY_NO AND CNCL.DEL_FG != 'Y') AS CNCL_DEL_FG FROM TB_MEMBER_REQ_REFUND MRR /* 고객환불정보 */ LEFT OUTER JOIN TB_MEMBER_WDRW_REQ MWR /* 고객청구요청정보 */ ON MRR.ACCNT_NO = MWR.ACCNT_NO AND MRR.PAY_MTHD = MWR.PAY_MTHD AND MRR.REQ_DAY = MWR.REQ_DAY AND MRR.REQ_PAY_NO = MWR.REQ_PAY_NO AND MRR.RESULT_KEY = MWR.RESULT_KEY LEFT OUTER JOIN TB_MEMBER_WDRW_RESULT MWS /* 고객청구결과정보 */ ON MWR.ACCNT_NO = MWS.ACCNT_NO AND MWR.RESULT_KEY = MWS.RESULT_KEY AND MWR.REQ_DAY = MWS.REQ_DAY INNER JOIN MEM_PROD_ACCNT MPA /* 고객가입상품정보 */ ON MPA.ACCNT_NO = MRR.ACCNT_NO AND MPA.DEL_FG = 'N' INNER JOIN MEMBER MBR /* 고객기본정보 */ ON MBR.MEM_NO = MPA.MEM_NO AND MBR.DEL_FG = 'N' LEFT OUTER JOIN PRODUCT_NO_DATA PND /* 상품회차별정보 */ ON MWR.PROD_CD = PND.PROD_CD AND MWR.REQ_PAY_NO = PND.NO WHERE 1=1
AND TO_CHAR(MRR.REG_DM,'YYYYMMDD') BETWEEN #{reg_dm_stt} AND #{reg_dm_end}
AND TO_CHAR(MRR.CANCEL_DT,'YYYYMMDD') BETWEEN #{reg_dm_stt} AND #{reg_dm_end}
AND MRR.ACCNT_NO = #{accnt_no}
AND MBR.MEM_NM = #{mem_nm}
AND MRR.REFUND_LOC = #{refund_loc}
AND MWR.DEL_FG = #{req_stat} /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패) X:입금취소) */
AND MRR.REQ_DAY = #{req_day}
AND MRR.PAY_MTHD = #{pay_mthd}
AND MRR.AUTH_YN = #{auth_yn}
AND MWR.REQ_DAY >= '20210101' AND NVL(TO_CHAR(MRR.CANCEL_DT,'YYYYMMDD'),'99999999') = '99999999'
AND MRR.DEL_FG = 'N' ) MAIN_TBL
WHERE 1=1
AND CANCEL_GB = #{cancel_gb}
)
WHERE 1=1

