SELECT /*ReqCustMap.getDlwAccntBaseInfo_popup*/ Z.* FROM ( SELECT A.*, ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) AS PAGE_INDX FROM ( SELECT ACCNT_NO, TBL3.MEM_NO, MB.MEM_NM, TBL3.PROD_CD, PAY_MTHD, STAT, KSTBIT FROM ( SELECT TBL2.MEM_NO, TBL2.ACCNT_NO, TBL2.PROD_CD, PAY_MTHD, STAT, KSTBIT FROM ( SELECT MBID.MEM_NO, MBID.ACCNT_NO, MBID.PROD_CD, MBID.PAY_MTHD, MBID.KSTBIT, (SELECT STAT FROM HP_CALL WHERE ACCNT_NO = MBID.ACCNT_NO) AS STAT FROM ( SELECT MPA.MEM_NO, MPA.ACCNT_NO, MPA.PROD_CD, MPA.PAY_MTHD, MPA.KSTBIT FROM MEM_PROD_ACCNT MPA WHERE 1=1 AND MPA.KSTBIT NOT IN ('03','04') AND MPA.DEL_FG = 'N'
UNION
SELECT MPA.MEM_NO, MPA.ACCNT_NO, MPA.PROD_CD, MPA.PAY_MTHD, MPA.KSTBIT FROM MEM_PROD_ACCNT MPA LEFT OUTER JOIN RESCISSION RS ON MPA.ACCNT_NO = RS.ACCNT_NO AND RS.DEL_FG = 'N' LEFT OUTER JOIN EVENT EV ON MPA.ACCNT_NO = EV.ACCNT_NO AND EV.DEL_FG = 'N' INNER JOIN PRODUCT PD ON MPA.PROD_CD = PD.PROD_CD WHERE 1=1 AND MPA.DEL_FG = 'N' --AND PD.SECTION_T = '0002' AND MPA.KSTBIT IN ('03','04')
) MBID INNER JOIN PRODUCT PD ON MBID.PROD_CD = PD.PROD_CD LEFT OUTER JOIN ( SELECT ACCNT_NO, '06' AS PAY_MTHD FROM CARD_MEM CARD WHERE CARD.DEL_FG = 'N'
UNION
SELECT ACCNT_NO, '04' AS PAY_MTHD FROM CMS_MEM CMS WHERE CMS.DEL_FG = 'N'
) CM ON MBID.ACCNT_NO = CM.ACCNT_NO AND MBID.PAY_MTHD = CM.PAY_MTHD ) TBL2 ) TBL3 INNER JOIN MEMBER MB ON TBL3.MEM_NO = MB.MEM_NO AND MB.DEL_FG = 'N' WHERE 1=1
AND MB.MEM_NM LIKE #{srch_vl} || '%'
AND TBL3.ACCNT_NO = #{srch_vl}
AND MB.MEM_NO = #{srch_vl}
) A
) Z
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT /*ReqCustMap.getDlwWdrwAcntInfo*/ * FROM ( SELECT TWR.ACCNT_NO , TWR.REQ_PAY_NO , TWR.REQ_DAY , TWR.ICHAE_CD , TWR.PAY_AMT , TWR.DEL_FG , CASE WHEN PAY_MTHD = '07' THEN '01' --가상계좌 WHEN DEL_FG = 'N' THEN '02' --산출중 WHEN DEL_FG = 'R' THEN '03' -- 청구중 ELSE '03' END AS REQ_TEMP FROM TB_MEMBER_WDRW_REQ TWR WHERE 1=1
AND TWR.ACCNT_NO = #{accnt_no}
AND TWR.DEL_FG IN ('N', 'R') ) ORDER BY REQ_PAY_NO ASC

SELECT /*ReqCustMap.getVirtualExtList*/ TBL.ACCNT_NO, TBL.MEM_NO, TBL.PROD_CD, PAY_MTHD, STAT, TBL.KSTBIT, TRUE_CNT, (TRUE_CNT + REQ_PAY_GUNSU) AS REQ_PAY_NO, -- 청구 회차 1 AS REQ_GUNSU, REQ_PAY_BIT, CASE WHEN REQ_PAY_BIT = 'NXX' THEN PND.AMT WHEN REQ_PAY_BIT = 'NRX' THEN PND.AMT + PND.REL_AMT WHEN REQ_PAY_BIT = 'XRX' THEN PND.REL_AMT WHEN REQ_PAY_BIT = 'XRA' THEN PND.REL_AMT + PND.ADD_AMT WHEN REQ_PAY_BIT = 'XXA' THEN PND.ADD_AMT ELSE PND.AMT + PND.REL_AMT + PND.ADD_AMT END AS PAY_AMT, CASE WHEN SUBSTR(REQ_PAY_BIT,0,1) = 'N' THEN MONTH_CNT WHEN SUBSTR(REQ_PAY_BIT,0,1) = 'X' AND TOT_REL_CNT <= MONTH_CNT THEN TOT_REL_CNT WHEN SUBSTR(REQ_PAY_BIT,0,1) = 'X' AND TOT_REL_CNT > MONTH_CNT THEN MONTH_CNT ELSE MONTH_CNT END AS MONTH_CNT FROM ( SELECT MBID.MEM_NO, MBID.ACCNT_NO, MBID.PROD_CD, MBID.PAY_MTHD, STAT, KSTBIT, CASE WHEN KSTBIT IN ('03','04') THEN REL_CNT ELSE TRUE_CNT END AS TRUE_CNT, CASE WHEN KSTBIT = '01' THEN 1 ELSE MONTH_CNT END AS MONTH_CNT, B.NUM AS REQ_PAY_GUNSU, (TRUE_CNT + B.NUM) AS REQ_PAY_NO, -- 청구 회차 CASE WHEN PROD_PAY_BIT = 'NXX' THEN CASE WHEN TRUE_CNT + B.NUM <= (TOT_TRUE_CNT - NEW_CHAN_GUNSU) THEN 'NXX' ELSE 'FUL' END WHEN PROD_PAY_BIT = 'NRX' THEN CASE WHEN KSTBIT IN ('03','04') AND REL_CNT + B.NUM <= TOT_REL_CNT THEN 'XRX' WHEN KSTBIT IN ('03','04') AND REL_CNT + B.NUM > TOT_REL_CNT THEN 'FUX' WHEN TRUE_CNT + B.NUM <= (TOT_TRUE_CNT - NEW_CHAN_GUNSU) AND REL_CNT + B.NUM <= TOT_REL_CNT THEN 'NRX' WHEN TRUE_CNT + B.NUM <= (TOT_TRUE_CNT - NEW_CHAN_GUNSU) AND REL_CNT + B.NUM > TOT_REL_CNT THEN 'NXX' WHEN TRUE_CNT + B.NUM > (TOT_TRUE_CNT - NEW_CHAN_GUNSU) AND REL_CNT + B.NUM <= TOT_REL_CNT THEN 'XRX' WHEN TRUE_CNT + B.NUM > (TOT_TRUE_CNT - NEW_CHAN_GUNSU) AND REL_CNT + B.NUM > TOT_REL_CNT THEN 'FUL' ELSE 'ERR' END WHEN PROD_PAY_BIT = 'NRA' THEN CASE WHEN KSTBIT IN ('03','04') AND REL_CNT + B.NUM <= TOT_REL_CNT AND ADD_CNT + B.NUM <= TOT_REL_CNT THEN 'XRA' WHEN KSTBIT IN ('03','04') AND REL_CNT + B.NUM > TOT_REL_CNT AND ADD_CNT + B.NUM <= TOT_REL_CNT THEN 'XXA' WHEN KSTBIT IN ('03','04') AND REL_CNT + B.NUM > TOT_REL_CNT AND ADD_CNT + B.NUM > TOT_REL_CNT THEN 'FUX' WHEN TRUE_CNT + B.NUM <= (TOT_TRUE_CNT - NEW_CHAN_GUNSU) AND REL_CNT + B.NUM <= TOT_REL_CNT AND ADD_CNT + B.NUM <= TOT_REL_CNT THEN 'NRA' WHEN TRUE_CNT + B.NUM <= (TOT_TRUE_CNT - NEW_CHAN_GUNSU) AND REL_CNT + B.NUM > TOT_REL_CNT AND ADD_CNT + B.NUM > TOT_REL_CNT THEN 'NXX' WHEN TRUE_CNT + B.NUM > (TOT_TRUE_CNT - NEW_CHAN_GUNSU) AND REL_CNT + B.NUM <= TOT_REL_CNT AND ADD_CNT + B.NUM <= TOT_REL_CNT THEN 'XRA' WHEN TRUE_CNT + B.NUM > (TOT_TRUE_CNT - NEW_CHAN_GUNSU) AND REL_CNT + B.NUM > TOT_REL_CNT AND ADD_CNT + B.NUM <= TOT_REL_CNT THEN 'XXA' WHEN TRUE_CNT + B.NUM > (TOT_TRUE_CNT - NEW_CHAN_GUNSU) AND REL_CNT + B.NUM > TOT_REL_CNT AND ADD_CNT + B.NUM > TOT_REL_CNT THEN 'FUL' ELSE 'ERR' END ELSE 'ERR' END AS REQ_PAY_BIT, TOT_TRUE_CNT, TOT_REL_CNT, TOT_ADD_CNT, REL_CNT, ADD_CNT, NEW_CHAN_GUNSU FROM ( SELECT MPA.ACCNT_NO, MB.MEM_NO, MPA.JOIN_DT, MPA.PROD_CD, KSTBIT, (SELECT STAT FROM HP_CALL WHERE ACCNT_NO = MPA.ACCNT_NO) AS STAT, PAY_MTHD, NEW_CHAN_GUNSU, ( SELECT CASE WHEN SUM(REL_AMT) > 0 AND SUM(ADD_AMT) > 0 THEN 'NRA' WHEN SUM(REL_AMT) > 0 AND SUM(ADD_AMT) = 0 THEN 'NRX' ELSE 'NXX' END FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD ) AS PROD_PAY_BIT,
/******************************************필수항목*************************************************/ (TRUNC(MONTHS_BETWEEN(LAST_DAY(SYSDATE),MPA.JOIN_DT)+1)) AS MONTH_CNT, NVL((SELECT COUNT(NO) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'),0) AS TRUE_CNT, NVL((SELECT COUNT(NO) FROM PAY_MNG_DTL WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'),0) AS REL_CNT, NVL((SELECT COUNT(NO) FROM PAY_MNG_DTL1 WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N'),0) AS ADD_CNT, (SELECT COUNT(NO) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND AMT > 0) AS TOT_TRUE_CNT, (SELECT COUNT(NO) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND REL_AMT > 0) AS TOT_REL_CNT, (SELECT COUNT(NO) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND ADD_AMT > 0) AS TOT_ADD_CNT
FROM MEM_PROD_ACCNT_DAMO MPA INNER JOIN MEMBER_DAMO MB ON MB.MEM_NO = MPA.MEM_NO AND MB.DEL_FG = 'N' INNER JOIN PRODUCT PD ON MPA.PROD_CD = PD.PROD_CD WHERE MPA.DEL_FG = 'N' AND MPA.ACCNT_NO = #{accnt_no} ) MBID INNER JOIN ( SELECT LEVEL NUM FROM DUAL CONNECT BY LEVEL <= 1000 ) B ON B.NUM BETWEEN 1 AND #{inv_gunsu} ) TBL INNER JOIN PRODUCT_NO_DATA PND ON PND.PROD_CD = TBL.PROD_CD AND PND.NO = (TRUE_CNT + REQ_PAY_GUNSU)

