WHERE 1=1 AND RST.DEPOSIT_DT BETWEEN REPLACE(#{stt_dt}, '/', '') AND REPLACE(#{end_dt}, '/', '') AND NVL(INI_YN,'N') = 'Y' AND PAY_MTHD = '07'
