	             , UPD_MAN = #{emple_no}

	             , STAT = '02'
	             , TID = #{tid}


	             , STAT = '05'
	             , TID = #{tid}


	             , STAT = '03'

	         WHERE SEQ = #{seq} ;


			        UPDATE TB_MEMBER_WDRW_REQ
			        SET DEL_FG = 'Y',
			        	UPD_MAN = #{emple_no},
			        	UPD_DM = SYSDATE
			        WHERE RESULT_KEY = #{seq};

		END;


        BEGIN
			UPDATE /* ReqCustMap.updateVirtualExtDelete */
