mapper.select:
SELECT /* ReqCustMap.getCreateBillKeyList */ MAIN_TBL.* FROM ( SELECT
ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) AS PAGE_INDX,
ROW_NUMBER() OVER(ORDER BY SUB_TBL.ACCNT_NO ASC) AS PAGE_INDX,
SUB_TBL.* FROM ( SELECT /* 제휴카드 X */ ACCNT_NO, CARD_NO, EXPIRE_DATE, CARD_BIRTH, 'NOR' AS AFFIL_YN FROM ( SELECT MPA.ACCNT_NO, CM.CARD_NO, CM.EXPIRE_DATE, NVL(CM.CARD_BIRTH,SUBSTR(MB.BRTH_MON_DAY,3,6)) AS CARD_BIRTH, (SELECT STAT FROM HP_CALL WHERE ACCNT_NO = MPA.ACCNT_NO) AS STAT FROM MEM_PROD_ACCNT MPA INNER JOIN CARD_MEM CM ON MPA.ACCNT_NO = CM.ACCNT_NO AND CM.DEL_FG = 'N' INNER JOIN MEMBER MB ON MPA.MEM_NO = MB.MEM_NO AND MB.DEL_FG = 'N' WHERE MPA.DEL_FG = 'N' AND MPA.PAY_MTHD = '06' AND KSTBIT = '02' AND SUBSTR(BID,0,13) = 'BIKYdmlife001' AND CM.EXPIRE_DATE > '2103' AND NVL(CM.INI_YN,'N') = 'N' AND NVL(CM.INI_BID,'N') = 'N' ) WHERE STAT IN ('01','011')
UNION ALL
SELECT /* 제휴카드 O */ ACCNT_NO, CARD_NO, EXPIRE_DATE, CARD_BIRTH, 'SIN' AS AFFIL_YN FROM ( SELECT MPA.ACCNT_NO, PD.PROD_NM, MPA.REL_CNT, CM.CARD_CD, CM.CARD_NO, CM.EXPIRE_DATE, CM.BID, MB.BRTH_MON_DAY, NVL(CM.CARD_BIRTH,SUBSTR(MB.BRTH_MON_DAY,3,6)) AS CARD_BIRTH, --CM.INI_BID, (SELECT COUNT(*) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD AND REL_AMT > 0) AS REL_TOT, (SELECT STAT FROM HP_CALL WHERE ACCNT_NO = MPA.ACCNT_NO) AS STAT FROM TB_MEMBER_BASIC_INFO_DAY MPA INNER JOIN CARD_MEM CM ON MPA.ACCNT_NO = CM.ACCNT_NO AND CM.DEL_FG = 'N' INNER JOIN MEMBER MB ON MPA.MEM_NO = MB.MEM_NO AND MB.DEL_FG = 'N' INNER JOIN PRODUCT PD ON MPA.PROD_CD = PD.PROD_CD INNER JOIN CARD_MID_LIST CML ON PD.MID = CML.MID WHERE 1=1 AND MPA.PAY_MTHD = '06' AND KSTBIT = '02' AND SUBSTR(BID,0,13) != 'BIKYdmlife001' AND CM.EXPIRE_DATE > '2103' AND NVL(CM.INI_YN,'N') = 'N' AND NVL(CM.INI_BID,'N') = 'N' AND NVL(CML.FRANCHISEE_NO,'N') = 'N' --무승인 제외 AND MPA.PROD_CD IN (SELECT PROD_CD FROM TB_CARD_BILL_GUBUN) ) WHERE REL_TOT = REL_CNT ) SUB_TBL WHERE 1=1
AND SUB_TBL.ACCNT_NO = #{accnt_no}
) MAIN_TBL WHERE 1=1
AND PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

    	/* ReqCustMap.getReqRltmCardLogOldCount */
    	SELECT         
    		COUNT(*) AS ICNT       
		FROM LF_DMUSER.TB_MEMBER_WDRW_REQ_2021 REQ INNER JOIN
                PRODUCT PD ON REQ.PROD_CD = PD.PROD_CD
	            LEFT OUTER JOIN LF_DMUSER.TB_MEMBER_WDRW_RESULT_2021 RST
	                         ON REQ.ACCNT_NO = RST.ACCNT_NO
	                        AND REQ.PAY_MTHD = RST.PAY_MTHD
	                        AND REQ.REQ_DAY = RST.REQ_DAY
	                        AND REQ.RESULT_KEY = RST.RESULT_KEY
        LEFT OUTER JOIN LF_DMUSER.TB_MEMBER_REQ_REFUND MRR
                     ON MRR.ACCNT_NO = REQ.ACCNT_NO
                    AND MRR.PAY_MTHD = REQ.PAY_MTHD
                    AND MRR.DEL_FG = 'N'
                    AND MRR.REQ_DAY = REQ.REQ_DAY
                  --AND MRR.RESULT_KEY = REQ.RESULT_KEY
                    AND NVL(MRR.RESULT_KEY,'JL32') = NVL(REQ.RESULT_KEY,'JL32') /* JL32 : PGWEB 서버 통신 중 에러 */
        WHERE 1=1

            AND REQ.ACCNT_NO = #{accnt_no}

        AND REQ.PAY_MTHD = #{pay_mthd}
        AND REQ.DEL_FG IN ('C', 'F', 'X')

            AND REQ.DEL_FG = #{del_fg}


            AND REQ.REQ_DAY BETWEEN #{stt_req_dt} AND #{end_req_dt}


            AND REQ.RESULT_KIND = #{result_kind}


            AND REQ.REQ_BIT = #{req_bit}


         	AND REQ.AUTH_YN = #{auth_yn}

SELECT /* ReqCustMap.getReqRltmCardLogOldList */ * FROM (SELECT ROW_NUMBER() OVER(ORDER BY REQ_DAY DESC, REG_DM DESC, REQ_PAY_NO DESC, ACCNT_NO) PAGE_INDX,
X.* FROM ( SELECT /*+ use_hash(RST) */ REQ.ACCNT_NO, REQ.MEM_NO, PD.PROD_NM, REQ_BIT, REQ.TRUE_CNT, /* 실납입횟수 */ REQ.REQ_PAY_NO, /* 청구회차 */ '조회' AS MAX_REQ_PAY_NO, TO_CHAR(RST.REG_DM, 'YYYYMMDDHH24MISS') AS REG_DM, /* 결과입력시간*/ '' AS MAX_REG_DM, /* (TEMP) 회원번호 기준 MAX 결과입력시간 */ REQ.PAY_BIT, REQ.ICHAE_NO, REQ.REQ_DAY, PAY_AMT, REQ.RESULT_KEY, RST.TID, RST.PAY_KIND AS RESULT_KIND,
RST.RESULT_CD, CASE WHEN REQ.PAY_MTHD = '04' THEN FN_COM_NM('15', RESULT_CD) WHEN REQ.PAY_MTHD = '06' AND NVL(REQ.AUTH_YN,'Y') = 'N' THEN FN_COM_NM('0222', RESULT_CD) ELSE RST.RESULT_MSG END AS RESULT_MSG, AUTH_DT, AUTH_CD, (SELECT COUNT(*) FROM TB_MEMBER_REQ_NAUTH_CNCL WHERE ACCNT_NO = REQ.ACCNT_NO AND REQ_PAY_NO = REQ.REQ_PAY_NO AND DEL_FG IN ('P','N','R')) AS CNCL_CNT, CASE WHEN REQ.PAY_MTHD = '04' THEN FN_COM_NM('63', NVL(RST.ICHAE_CD,REQ.ICHAE_CD)) WHEN REQ.PAY_MTHD = '06' AND RST.PAY_KIND = '01' AND NVL(REQ.INI_YN, 'N') != 'Y' THEN RST.ICHAE_CD WHEN REQ.PAY_MTHD = '06' AND RST.PAY_KIND != '01' AND NVL(REQ.INI_YN, 'N') != 'Y' THEN FN_COM_NM('0120', NVL(RST.ICHAE_CD,REQ.ICHAE_CD)) WHEN REQ.PAY_MTHD = '06' AND RST.PAY_KIND = '01' AND NVL(REQ.INI_YN, 'N') = 'Y' THEN FN_COM_NM('0196', NVL(RST.ICHAE_CD,REQ.ICHAE_CD)) WHEN REQ.PAY_MTHD = '06' AND RST.PAY_KIND != '01' AND NVL(REQ.INI_YN, 'N') = 'Y' THEN FN_COM_NM('0196', NVL(RST.ICHAE_CD,REQ.ICHAE_CD)) ELSE '' END AS ICHAE_CD,
MRR.REG_DM AS REFUND_REG_DT, /* 환불등록일 */
TO_CHAR(MRR.CANCEL_DT, 'YYYYMMDDHH24MISS') AS CANCEL_DT, /* 실제취소일*/ RST.ETC, REQ.PAY_MTHD, /* 납입방법 (04:CMS, 06:카드) */ REQ.DEL_FG, CASE WHEN LENGTH(RST.REG_MAN) = 10 THEN ( SELECT EMPLE_NM FROM EMPLOYEE WHERE EMPLE_NO = RST.REG_MAN ) ELSE RST.REG_MAN END AS REG_MAN, /* 결과등록자 사원명으로 표시 */ REQ.AUTH_YN, REQ.FRANCHISEE_NO, REQ.INI_YN FROM LF_DMUSER.TB_MEMBER_WDRW_REQ_2021 REQ INNER JOIN PRODUCT PD ON REQ.PROD_CD = PD.PROD_CD LEFT OUTER JOIN LF_DMUSER.TB_MEMBER_WDRW_RESULT_2021 RST ON REQ.ACCNT_NO = RST.ACCNT_NO AND REQ.PAY_MTHD = RST.PAY_MTHD AND REQ.REQ_DAY = RST.REQ_DAY AND REQ.RESULT_KEY = RST.RESULT_KEY LEFT OUTER JOIN LF_DMUSER.TB_MEMBER_REQ_REFUND MRR ON MRR.ACCNT_NO = REQ.ACCNT_NO AND MRR.PAY_MTHD = REQ.PAY_MTHD AND MRR.DEL_FG = 'N' AND MRR.REQ_DAY = REQ.REQ_DAY --AND MRR.RESULT_KEY = REQ.RESULT_KEY AND NVL(MRR.RESULT_KEY,'JL32') = NVL(REQ.RESULT_KEY,'JL32') /* JL32 : PGWEB 서버 통신 중 에러 */ WHERE 1=1
AND REQ.ACCNT_NO = #{accnt_no}
AND REQ.PAY_MTHD = #{pay_mthd} AND REQ.DEL_FG IN ('C', 'F', 'X')
AND REQ.AUTH_YN = #{auth_yn}
AND REQ.DEL_FG = #{del_fg}
AND REQ.REQ_DAY BETWEEN #{stt_req_dt} AND #{end_req_dt}
AND REQ.RESULT_KIND = #{result_kind}
AND REQ.REQ_BIT = #{req_bit}
) X WHERE 1=1 ) Z
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

    	/* ReqCustMap.getReqRltmCardLogAccntNoCount */
    	SELECT         
    		COUNT(*) AS ICNT       
		FROM LF_DMUSER.VIEW_MEMBER_WDRW_REQ REQ INNER JOIN
                PRODUCT PD ON REQ.PROD_CD = PD.PROD_CD
        LEFT OUTER JOIN LF_DMUSER.TB_MEMBER_REQ_REFUND MRR
                     ON MRR.ACCNT_NO = REQ.ACCNT_NO
                    AND MRR.PAY_MTHD = REQ.PAY_MTHD
                    AND MRR.DEL_FG = 'N'
                    AND MRR.REQ_DAY = REQ.REQ_DAY
                  --AND MRR.RESULT_KEY = REQ.RESULT_KEY
                    AND NVL(MRR.RESULT_KEY,'JL32') = NVL(REQ.RESULT_KEY,'JL32') /* JL32 : PGWEB 서버 통신 중 에러 */
        WHERE 1=1
        AND REQ.ACCNT_NO = #{accnt_no}
        AND REQ.PAY_MTHD = #{pay_mthd}
        AND REQ.DEL_FG IN ('C', 'F', 'X')

            AND REQ.DEL_FG = #{del_fg}


            AND REQ.REQ_DAY BETWEEN #{stt_req_dt} AND #{end_req_dt}


            AND REQ.RESULT_KIND = #{result_kind}


            AND REQ.REQ_BIT = #{req_bit}


         	AND REQ.AUTH_YN = #{auth_yn}

SELECT /* ReqCustMap.getReqRltmCardLogAccntNoList */ * FROM (SELECT ROW_NUMBER() OVER(ORDER BY REQ_DAY DESC, REG_DM DESC, REQ_PAY_NO DESC, ACCNT_NO) PAGE_INDX,
X.* FROM ( SELECT REQ.ACCNT_NO, REQ.MEM_NO, PD.PROD_NM, REQ.REQ_BIT, REQ.TRUE_CNT, /* 실납입횟수 */ REQ.REQ_PAY_NO, /* 청구회차 */ '조회' AS MAX_REQ_PAY_NO, REQ.REG_DM, '' AS MAX_REG_DM, /* (TEMP) 회원번호 기준 MAX 결과입력시간 */ REQ.PAY_BIT, REQ.ICHAE_NO, REQ.REQ_DAY, REQ.PAY_AMT, REQ.RESULT_KEY, REQ.TID, REQ.RESULT_KIND,
REQ.RESULT_CD, CASE WHEN REQ.PAY_MTHD = '04' THEN FN_COM_NM('15', REQ.RESULT_CD) WHEN REQ.PAY_MTHD = '06' AND NVL(REQ.AUTH_YN,'Y') = 'N' THEN FN_COM_NM('0222', REQ.RESULT_CD) ELSE REQ.RESULT_MSG END AS RESULT_MSG, REQ.AUTH_DT, REQ.AUTH_CD, (SELECT COUNT(*) FROM TB_MEMBER_REQ_NAUTH_CNCL WHERE ACCNT_NO = REQ.ACCNT_NO AND REQ_PAY_NO = REQ.REQ_PAY_NO AND DEL_FG IN ('P','N','R')) AS CNCL_CNT, CASE WHEN REQ.PAY_MTHD = '04' THEN FN_COM_NM('63', NVL(REQ.ICHAE_CD,REQ.ICHAE_CD)) WHEN REQ.PAY_MTHD = '06' AND REQ.RESULT_KIND = '01' AND NVL(REQ.INI_YN, 'N') != 'Y' THEN REQ.ICHAE_CD WHEN REQ.PAY_MTHD = '06' AND REQ.RESULT_KIND != '01' AND NVL(REQ.INI_YN, 'N') != 'Y' THEN FN_COM_NM('0120', REQ.ICHAE_CD) WHEN REQ.PAY_MTHD = '06' AND REQ.RESULT_KIND = '01' AND NVL(REQ.INI_YN, 'N') = 'Y' THEN FN_COM_NM('0196', REQ.ICHAE_CD) WHEN REQ.PAY_MTHD = '06' AND REQ.RESULT_KIND != '01' AND NVL(REQ.INI_YN, 'N') = 'Y' THEN FN_COM_NM('0196', REQ.ICHAE_CD) ELSE '' END AS ICHAE_CD,
MRR.REG_DM AS REFUND_REG_DT, /* 환불등록일 */ TO_CHAR(MRR.CANCEL_DT, 'YYYYMMDDHH24MISS') AS CANCEL_DT, /* 실제취소일*/ REQ.ETC, REQ.PAY_MTHD, /* 납입방법 (04:CMS, 06:카드) */ REQ.DEL_FG, CASE WHEN LENGTH(REQ.REG_MAN) = 10 THEN ( SELECT EMPLE_NM FROM EMPLOYEE WHERE EMPLE_NO = REQ.REG_MAN ) ELSE REQ.REG_MAN END AS REG_MAN, /* 결과등록자 사원명으로 표시 */ REQ.AUTH_YN, REQ.FRANCHISEE_NO, REQ.INI_YN FROM LF_DMUSER.VIEW_MEMBER_WDRW_REQ REQ INNER JOIN PRODUCT PD ON REQ.PROD_CD = PD.PROD_CD LEFT OUTER JOIN LF_DMUSER.TB_MEMBER_REQ_REFUND MRR ON MRR.ACCNT_NO = REQ.ACCNT_NO AND MRR.PAY_MTHD = REQ.PAY_MTHD AND MRR.DEL_FG = 'N' AND MRR.REQ_DAY = REQ.REQ_DAY --AND MRR.RESULT_KEY = REQ.RESULT_KEY AND NVL(MRR.RESULT_KEY,'JL32') = NVL(REQ.RESULT_KEY,'JL32') /* JL32 : PGWEB 서버 통신 중 에러 */ WHERE 1=1 AND REQ.ACCNT_NO = #{accnt_no} AND REQ.PAY_MTHD = #{pay_mthd} AND REQ.DEL_FG IN ('C', 'F', 'X')
AND REQ.AUTH_YN = #{auth_yn}
AND REQ.DEL_FG = #{del_fg}
AND REQ.RESULT_KIND = #{result_kind}
AND REQ.REQ_BIT = #{req_bit}
) X WHERE 1=1 ) Z
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

SELECT /* ReqCustMap.getMemberRefundListOldCount */ COUNT(*) AS ICNT FROM (
SELECT *
FROM ( SELECT MRR.ACCNT_NO /* 회원번호 */ ,MBR.MEM_NO /* 고유번호 */ ,MBR.MEM_NM /* 회원명 */ ,MRR.PAY_MTHD /* 납입방법(04:CMS, 06:Card) */ ,CASE WHEN MRR.CANCEL_DT IS NOT NULL THEN CASE WHEN MWR.RESULT_KIND = '01' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > TO_CHAR(TO_DATE(AUTH_DT), 'YYMMDD') THEN '후취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'N' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > SUBSTR(AUTH_DT, 1, 6) THEN '후취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'Y' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > SUBSTR(AUTH_DT, 3, 8) THEN '후취소' WHEN MWR.RESULT_KIND = '01' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = TO_CHAR(TO_DATE(AUTH_DT), 'YYMMDD') THEN '취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'N' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = SUBSTR(AUTH_DT, 1, 6) THEN '취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'Y' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = SUBSTR(AUTH_DT, 3, 8) THEN '취소' ELSE '' END ELSE '' END AS CANCEL_GB ,MWR.PAY_BIT /* PAYBIT */ ,MRR.REQ_DAY /* 청구일자 */ ,MWR.PAY_AMT /* 청구금액 */ ,MWR.KSTBIT /* 회원상태(01:대기, 02:가입, 03:해약, 04:행사, 09:만기) */ ,MRR.RESULT_KEY /* 결과키 */ ,MRR.REFUND_LOC /* 환불 경로(01:결과파일환불,02:해약등록,03:청약철회등록,04:행사등록) */ ,MRR.ICHAE_NO /* 은행이체번호(CMS) */ ,MRR.ICHAE_CD /* 은행이체코드(CMS) */ ,MRR.DEPR /* 예금주(CMS) */ ,MRR.REFUND_DT /* 환불일자 */ ,MRR.REG_DM /* 등록일자 */ ,MRR.REG_MAN /* 등록자ID */ ,MWR.DEL_FG /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패) X:입금취소) */ ,MWR.REQ_PAY_NO /* 산출청구회차 */ ,MWS.RESULT_CD /* 결과코드 */ ,MWS.RESULT_MSG /* 결과메세지 */
,TO_CHAR(MRR.CANCEL_DT, 'YYYYMMDDHH24MISS') AS CANCEL_DT /* 실시간 취소 일자 */ ,MWS.TID /* 결과키(TID, CMS결과키) */ ,AUTH_DT /* 승인일자 */ ,MWS.AUTH_CD /* 승인번호 */ ,MRR.REFUND_REASON /* 환불사유(환불사유코드) */ ,MRR.REFUND_ETC /* 비고 */ ,MRR.AUTH_YN ,(SELECT DEL_FG FROM TB_MEMBER_REQ_NAUTH_CNCL CNCL WHERE 1=1 AND CNCL.ACCNT_NO = MRR.ACCNT_NO AND CNCL.REQ_PAY_NO = MRR.REQ_PAY_NO AND CNCL.DEL_FG != 'Y') AS CNCL_DEL_FG FROM TB_MEMBER_REQ_REFUND MRR /* 고객환불정보 */
LEFT OUTER JOIN TB_MEMBER_WDRW_REQ_2021 MWR /* 고객청구요청정보 */ ON MRR.ACCNT_NO = MWR.ACCNT_NO AND MRR.PAY_MTHD = MWR.PAY_MTHD AND MRR.REQ_DAY = MWR.REQ_DAY AND MRR.REQ_PAY_NO = MWR.REQ_PAY_NO AND MRR.RESULT_KEY = MWR.RESULT_KEY LEFT OUTER JOIN TB_MEMBER_WDRW_RESULT_2021 MWS /* 고객청구결과정보 */ ON MWR.ACCNT_NO = MWS.ACCNT_NO AND MWR.RESULT_KEY = MWS.RESULT_KEY AND MWR.REQ_DAY = MWS.REQ_DAY
INNER JOIN MEM_PROD_ACCNT MPA /* 고객가입상품정보 */ ON MPA.ACCNT_NO = MRR.ACCNT_NO AND MPA.DEL_FG = 'N' INNER JOIN MEMBER MBR /* 고객기본정보 */ ON MBR.MEM_NO = MPA.MEM_NO AND MBR.DEL_FG = 'N' LEFT OUTER JOIN PRODUCT_NO_DATA PND /* 상품회차별정보 */ ON MWR.PROD_CD = PND.PROD_CD AND MWR.REQ_PAY_NO = PND.NO WHERE 1=1
AND TO_CHAR(MRR.REG_DM,'YYYYMMDD') BETWEEN #{reg_dm_stt} AND #{reg_dm_end}
AND TO_CHAR(MRR.CANCEL_DT,'YYYYMMDD') BETWEEN #{reg_dm_stt} AND #{reg_dm_end}
AND MRR.ACCNT_NO = #{accnt_no}
AND MBR.MEM_NM = #{mem_nm}
AND MRR.REFUND_LOC = #{refund_loc}
AND MWR.DEL_FG = #{req_stat} /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패) X:입금취소) */
AND MRR.REQ_DAY = #{req_day} /* 청구일자 */
AND MRR.PAY_MTHD = #{pay_mthd} /* 납입방법(04:CMS, 06:Card) */
AND MRR.AUTH_YN = #{auth_yn}
AND MWR.REQ_DAY >= '20210101' AND NVL(TO_CHAR(MRR.CANCEL_DT,'YYYYMMDD'),'99999999') = '99999999'
AND MRR.DEL_FG = 'N' ) MAIN_TBL ) WHERE 1=1
AND CANCEL_GB = #{cancel_gb}

SELECT /* ReqCustMap.getMemberRefundOldList */ * FROM
( SELECT ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) PAGE_INDX /* PAGE INDX */ , MAIN_TBL.* FROM ( SELECT MRR.ACCNT_NO /* 회원번호 */ ,MWR.PROD_CD ,(SELECT MAX(PD.PROD_NM) FROM PRODUCT PD WHERE 1=1 AND PD.PROD_CD = MWR.PROD_CD) AS PROD_NM ,MWR.ICHAE_NO AS BID ,MBR.MEM_NO /* 고유번호 */ ,MBR.MEM_NM /* 회원명 */ ,MRR.PAY_MTHD /* 납입방법(04:CMS, 06:Card) */ ,CASE WHEN MRR.CANCEL_DT IS NOT NULL THEN CASE WHEN MWR.RESULT_KIND = '01' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > TO_CHAR(TO_DATE(AUTH_DT), 'YYMMDD') THEN '후취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'N' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > SUBSTR(AUTH_DT, 1, 6) THEN '후취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'Y' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > SUBSTR(AUTH_DT, 3, 8) THEN '후취소' WHEN MWR.RESULT_KIND = '01' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = TO_CHAR(TO_DATE(AUTH_DT), 'YYMMDD') THEN '취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'N' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = SUBSTR(AUTH_DT, 1, 6) THEN '취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'Y' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = SUBSTR(AUTH_DT, 3, 8) THEN '취소' ELSE '' END ELSE '' END AS CANCEL_GB ,MWR.PAY_BIT /* PAYBIT */ ,MRR.REQ_DAY /* 청구일자 */ ,MWR.PAY_AMT /* 청구금액 */ ,(CASE WHEN SUBSTR(MWR.PAY_BIT,1,1) != 'X' THEN PND.AMT ELSE 0 END) AS TRUE_AMT /* 상조금 */ ,(CASE WHEN SUBSTR(MWR.PAY_BIT,2,1) != 'X' THEN PND.REL_AMT ELSE 0 END) AS REL_AMT /* 결합금 */ ,(CASE WHEN SUBSTR(MWR.PAY_BIT,3,1) != 'X' THEN PND.ADD_AMT ELSE 0 END) AS ADD_AMT /* 추가금 */ ,MWR.KSTBIT /* 회원상태(01:대기, 02:가입, 03:해약, 04:행사, 09:만기) */ ,MRR.RESULT_KEY /* 결과키 */ ,MWR.RESULT_KIND /* 결과유형 (01:파일, 02:실시간, 03:자유,04:기타) */ ,MRR.REFUND_LOC /* 환불 경로(01:결과파일환불,02:해약등록,03:청약철회등록,04:행사등록) */
,CASE WHEN MWR.PAY_MTHD = '04' THEN CASE WHEN NVL(MRR.ICHAE_NO, 'N') = 'N' THEN MWR.ICHAE_NO ELSE MRR.ICHAE_NO END ELSE '' END AS ICHAE_NO ,CASE WHEN MWR.PAY_MTHD = '04' THEN CASE WHEN NVL(MRR.ICHAE_CD, 'N') = 'N' THEN MWR.ICHAE_CD ELSE MRR.ICHAE_CD END ELSE '' END AS ICHAE_CD ,CASE WHEN MWR.PAY_MTHD = '06' AND NVL(MWR.INI_YN, 'N') != 'Y' THEN LF_DMUSER.FN_COM_NM('0120', MWR.ICHAE_CD) WHEN MWR.PAY_MTHD = '06' AND NVL(MWR.INI_YN, 'N') = 'Y' THEN LF_DMUSER.FN_COM_NM('0196', MWR.ICHAE_CD) ELSE '' END AS CARD_CD ,MRR.DEPR /* 예금주(CMS) */ ,MRR.REFUND_DT /* 환불일자 */ ,MRR.REG_DM /* 등록일자 */ ,MRR.REG_MAN /* 등록자ID */ ,MWR.DEL_FG /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패) X:입금취소) */ ,MWR.REQ_PAY_NO /* 산출청구회차 */ ,MWS.RESULT_CD /* 결과코드 */ ,MWS.RESULT_MSG /* 결과메세지 */
,TO_CHAR(MRR.CANCEL_DT, 'YYYYMMDDHH24MISS') AS CANCEL_DT /* 실시간 취소 일자 */ ,MWS.TID /* 결과키(TID, CMS결과키) */ ,AUTH_DT /* 승인일자 */ ,MWS.AUTH_CD /* 승인번호 */ ,MRR.REFUND_REASON /* 환불사유(환불사유코드) */ ,MRR.REFUND_ETC /* 비고 */ ,MRR.AUTH_YN ,MWR.FRANCHISEE_NO ,MWR.INI_YN ,(SELECT DEL_FG FROM TB_MEMBER_REQ_NAUTH_CNCL CNCL WHERE 1=1 AND CNCL.ACCNT_NO = MRR.ACCNT_NO AND CNCL.REQ_PAY_NO = MRR.REQ_PAY_NO AND CNCL.DEL_FG != 'Y') AS CNCL_DEL_FG FROM TB_MEMBER_REQ_REFUND MRR /* 고객환불정보 */ LEFT OUTER JOIN TB_MEMBER_WDRW_REQ_2021 MWR /* 고객청구요청정보 */ ON MRR.ACCNT_NO = MWR.ACCNT_NO AND MRR.PAY_MTHD = MWR.PAY_MTHD AND MRR.REQ_DAY = MWR.REQ_DAY AND MRR.REQ_PAY_NO = MWR.REQ_PAY_NO AND MRR.RESULT_KEY = MWR.RESULT_KEY LEFT OUTER JOIN TB_MEMBER_WDRW_RESULT_2021 MWS /* 고객청구결과정보 */ ON MWR.ACCNT_NO = MWS.ACCNT_NO AND MWR.RESULT_KEY = MWS.RESULT_KEY AND MWR.REQ_DAY = MWS.REQ_DAY INNER JOIN MEM_PROD_ACCNT MPA /* 고객가입상품정보 */ ON MPA.ACCNT_NO = MRR.ACCNT_NO AND MPA.DEL_FG = 'N' INNER JOIN MEMBER MBR /* 고객기본정보 */ ON MBR.MEM_NO = MPA.MEM_NO AND MBR.DEL_FG = 'N' LEFT OUTER JOIN PRODUCT_NO_DATA PND /* 상품회차별정보 */ ON MWR.PROD_CD = PND.PROD_CD AND MWR.REQ_PAY_NO = PND.NO WHERE 1=1
AND TO_CHAR(MRR.REG_DM,'YYYYMMDD') BETWEEN #{reg_dm_stt} AND #{reg_dm_end}
AND TO_CHAR(MRR.CANCEL_DT,'YYYYMMDD') BETWEEN #{reg_dm_stt} AND #{reg_dm_end}
AND MRR.ACCNT_NO = #{accnt_no}
AND MBR.MEM_NM = #{mem_nm}
AND MRR.REFUND_LOC = #{refund_loc}
AND MWR.DEL_FG = #{req_stat} /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패) X:입금취소) */
AND MRR.REQ_DAY = #{req_day}
AND MRR.PAY_MTHD = #{pay_mthd}
AND MRR.AUTH_YN = #{auth_yn}
AND MWR.REQ_DAY >= '20210101' AND NVL(TO_CHAR(MRR.CANCEL_DT,'YYYYMMDD'),'99999999') = '99999999'
AND MRR.DEL_FG = 'N' ) MAIN_TBL
WHERE 1=1
AND CANCEL_GB = #{cancel_gb}
)
WHERE 1=1

SELECT /* ReqCustMap.getMemberRefundListAccntNoCount */ COUNT(*) AS ICNT FROM (
SELECT *
FROM ( SELECT MRR.ACCNT_NO /* 회원번호 */ ,MBR.MEM_NO /* 고유번호 */ ,MBR.MEM_NM /* 회원명 */ ,MRR.PAY_MTHD /* 납입방법(04:CMS, 06:Card) */ ,CASE WHEN MRR.CANCEL_DT IS NOT NULL THEN CASE WHEN MWR.RESULT_KIND = '01' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > TO_CHAR(TO_DATE(AUTH_DT), 'YYMMDD') THEN '후취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'N' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > SUBSTR(AUTH_DT, 1, 6) THEN '후취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'Y' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > SUBSTR(AUTH_DT, 3, 8) THEN '후취소' WHEN MWR.RESULT_KIND = '01' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = TO_CHAR(TO_DATE(AUTH_DT), 'YYMMDD') THEN '취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'N' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = SUBSTR(AUTH_DT, 1, 6) THEN '취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'Y' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = SUBSTR(AUTH_DT, 3, 8) THEN '취소' ELSE '' END ELSE '' END AS CANCEL_GB ,MWR.PAY_BIT /* PAYBIT */ ,MRR.REQ_DAY /* 청구일자 */ ,MWR.PAY_AMT /* 청구금액 */ ,MWR.KSTBIT /* 회원상태(01:대기, 02:가입, 03:해약, 04:행사, 09:만기) */ ,MRR.RESULT_KEY /* 결과키 */ ,MRR.REFUND_LOC /* 환불 경로(01:결과파일환불,02:해약등록,03:청약철회등록,04:행사등록) */ ,MRR.ICHAE_NO /* 은행이체번호(CMS) */ ,MRR.ICHAE_CD /* 은행이체코드(CMS) */ ,MRR.DEPR /* 예금주(CMS) */ ,MRR.REFUND_DT /* 환불일자 */ ,MRR.REG_DM /* 등록일자 */ ,MRR.REG_MAN /* 등록자ID */ ,MWR.DEL_FG /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패) X:입금취소) */ ,MWR.REQ_PAY_NO /* 산출청구회차 */ ,MWR.RESULT_CD /* 결과코드 */ ,MWR.RESULT_MSG /* 결과메세지 */
,TO_CHAR(MRR.CANCEL_DT, 'YYYYMMDDHH24MISS') AS CANCEL_DT /* 실시간 취소 일자 */ ,MWR.TID /* 결과키(TID, CMS결과키) */ ,AUTH_DT /* 승인일자 */ ,MWR.AUTH_CD /* 승인번호 */ ,MRR.REFUND_REASON /* 환불사유(환불사유코드) */ ,MRR.REFUND_ETC /* 비고 */ ,MRR.AUTH_YN ,(SELECT DEL_FG FROM TB_MEMBER_REQ_NAUTH_CNCL CNCL WHERE 1=1 AND CNCL.ACCNT_NO = MRR.ACCNT_NO AND CNCL.REQ_PAY_NO = MRR.REQ_PAY_NO AND CNCL.DEL_FG != 'Y') AS CNCL_DEL_FG FROM TB_MEMBER_REQ_REFUND MRR /* 고객환불정보 */
LEFT OUTER JOIN VIEW_MEMBER_WDRW_REQ MWR /* 고객청구요청정보 */ ON MRR.ACCNT_NO = MWR.ACCNT_NO AND MRR.PAY_MTHD = MWR.PAY_MTHD AND MRR.REQ_DAY = MWR.REQ_DAY AND MRR.REQ_PAY_NO = MWR.REQ_PAY_NO AND MRR.RESULT_KEY = MWR.RESULT_KEY
INNER JOIN MEM_PROD_ACCNT MPA /* 고객가입상품정보 */ ON MPA.ACCNT_NO = MRR.ACCNT_NO AND MPA.DEL_FG = 'N' INNER JOIN MEMBER MBR /* 고객기본정보 */ ON MBR.MEM_NO = MPA.MEM_NO AND MBR.DEL_FG = 'N' LEFT OUTER JOIN PRODUCT_NO_DATA PND /* 상품회차별정보 */ ON MWR.PROD_CD = PND.PROD_CD AND MWR.REQ_PAY_NO = PND.NO WHERE 1=1 AND MRR.ACCNT_NO = #{accnt_no}
AND MBR.MEM_NM = #{mem_nm}
AND MRR.REFUND_LOC = #{refund_loc}
AND MWR.DEL_FG = #{req_stat} /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패) X:입금취소) */
AND MRR.REQ_DAY = #{req_day} /* 청구일자 */
AND MRR.PAY_MTHD = #{pay_mthd} /* 납입방법(04:CMS, 06:Card) */
AND MRR.AUTH_YN = #{auth_yn}
AND MWR.REQ_DAY >= '20210101' AND NVL(TO_CHAR(MRR.CANCEL_DT,'YYYYMMDD'),'99999999') = '99999999'
AND MRR.DEL_FG = 'N' ) MAIN_TBL ) WHERE 1=1
AND CANCEL_GB = #{cancel_gb}

SELECT /* ReqCustMap.getMemberRefundAccntNoList */ * FROM
( SELECT ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) PAGE_INDX /* PAGE INDX */ , MAIN_TBL.* FROM ( SELECT MRR.ACCNT_NO /* 회원번호 */ ,MWR.PROD_CD ,(SELECT MAX(PD.PROD_NM) FROM PRODUCT PD WHERE 1=1 AND PD.PROD_CD = MWR.PROD_CD) AS PROD_NM ,MWR.ICHAE_NO AS BID ,MBR.MEM_NO /* 고유번호 */ ,MBR.MEM_NM /* 회원명 */ ,MRR.PAY_MTHD /* 납입방법(04:CMS, 06:Card) */ ,CASE WHEN MRR.CANCEL_DT IS NOT NULL THEN CASE WHEN MWR.RESULT_KIND = '01' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > TO_CHAR(TO_DATE(AUTH_DT), 'YYMMDD') THEN '후취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'N' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > SUBSTR(AUTH_DT, 1, 6) THEN '후취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'Y' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') > SUBSTR(AUTH_DT, 3, 8) THEN '후취소' WHEN MWR.RESULT_KIND = '01' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = TO_CHAR(TO_DATE(AUTH_DT), 'YYMMDD') THEN '취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'N' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = SUBSTR(AUTH_DT, 1, 6) THEN '취소' WHEN MWR.RESULT_KIND != '01' AND NVL(MWR.INI_YN,'N') = 'Y' AND TO_CHAR(MRR.CANCEL_DT, 'YYMMDD') = SUBSTR(AUTH_DT, 3, 8) THEN '취소' ELSE '' END ELSE '' END AS CANCEL_GB ,MWR.PAY_BIT /* PAYBIT */ ,MRR.REQ_DAY /* 청구일자 */ ,MWR.PAY_AMT /* 청구금액 */ ,(CASE WHEN SUBSTR(MWR.PAY_BIT,1,1) != 'X' THEN PND.AMT ELSE 0 END) AS TRUE_AMT /* 상조금 */ ,(CASE WHEN SUBSTR(MWR.PAY_BIT,2,1) != 'X' THEN PND.REL_AMT ELSE 0 END) AS REL_AMT /* 결합금 */ ,(CASE WHEN SUBSTR(MWR.PAY_BIT,3,1) != 'X' THEN PND.ADD_AMT ELSE 0 END) AS ADD_AMT /* 추가금 */ ,MWR.KSTBIT /* 회원상태(01:대기, 02:가입, 03:해약, 04:행사, 09:만기) */ ,MRR.RESULT_KEY /* 결과키 */ ,MWR.RESULT_KIND /* 결과유형 (01:파일, 02:실시간, 03:자유,04:기타) */ ,MRR.REFUND_LOC /* 환불 경로(01:결과파일환불,02:해약등록,03:청약철회등록,04:행사등록) */
,CASE WHEN MWR.PAY_MTHD = '04' THEN CASE WHEN NVL(MRR.ICHAE_NO, 'N') = 'N' THEN MWR.ICHAE_NO ELSE MRR.ICHAE_NO END ELSE '' END AS ICHAE_NO ,CASE WHEN MWR.PAY_MTHD = '04' THEN CASE WHEN NVL(MRR.ICHAE_CD, 'N') = 'N' THEN MWR.ICHAE_CD ELSE MRR.ICHAE_CD END ELSE '' END AS ICHAE_CD ,CASE WHEN MWR.PAY_MTHD = '06' AND NVL(MWR.INI_YN, 'N') != 'Y' THEN LF_DMUSER.FN_COM_NM('0120', MWR.ICHAE_CD) WHEN MWR.PAY_MTHD = '06' AND NVL(MWR.INI_YN, 'N') = 'Y' THEN LF_DMUSER.FN_COM_NM('0196', MWR.ICHAE_CD) ELSE '' END AS CARD_CD ,MRR.DEPR /* 예금주(CMS) */ ,MRR.REFUND_DT /* 환불일자 */ ,MRR.REG_DM /* 등록일자 */ ,MRR.REG_MAN /* 등록자ID */ ,MWR.DEL_FG /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패) X:입금취소) */ ,MWR.REQ_PAY_NO /* 산출청구회차 */ ,MWR.RESULT_CD /* 결과코드 */ ,MWR.RESULT_MSG /* 결과메세지 */
,TO_CHAR(MRR.CANCEL_DT, 'YYYYMMDDHH24MISS') AS CANCEL_DT /* 실시간 취소 일자 */ ,MWR.TID /* 결과키(TID, CMS결과키) */ ,AUTH_DT /* 승인일자 */ ,MWR.AUTH_CD /* 승인번호 */ ,MRR.REFUND_REASON /* 환불사유(환불사유코드) */ ,MRR.REFUND_ETC /* 비고 */ ,MRR.AUTH_YN ,MWR.FRANCHISEE_NO ,MWR.INI_YN ,(SELECT DEL_FG FROM TB_MEMBER_REQ_NAUTH_CNCL CNCL WHERE 1=1 AND CNCL.ACCNT_NO = MRR.ACCNT_NO AND CNCL.REQ_PAY_NO = MRR.REQ_PAY_NO AND CNCL.DEL_FG != 'Y') AS CNCL_DEL_FG FROM TB_MEMBER_REQ_REFUND MRR /* 고객환불정보 */ LEFT OUTER JOIN VIEW_MEMBER_WDRW_REQ MWR /* 고객청구요청정보 */ ON MRR.ACCNT_NO = MWR.ACCNT_NO AND MRR.PAY_MTHD = MWR.PAY_MTHD AND MRR.REQ_DAY = MWR.REQ_DAY AND MRR.REQ_PAY_NO = MWR.REQ_PAY_NO AND MRR.RESULT_KEY = MWR.RESULT_KEY INNER JOIN MEM_PROD_ACCNT MPA /* 고객가입상품정보 */ ON MPA.ACCNT_NO = MRR.ACCNT_NO AND MPA.DEL_FG = 'N' INNER JOIN MEMBER MBR /* 고객기본정보 */ ON MBR.MEM_NO = MPA.MEM_NO AND MBR.DEL_FG = 'N' LEFT OUTER JOIN PRODUCT_NO_DATA PND /* 상품회차별정보 */ ON MWR.PROD_CD = PND.PROD_CD AND MWR.REQ_PAY_NO = PND.NO WHERE 1=1 AND MRR.ACCNT_NO = #{accnt_no}
AND MBR.MEM_NM = #{mem_nm}
AND MRR.REFUND_LOC = #{refund_loc}
AND MWR.DEL_FG = #{req_stat} /* 청구여부(N:청구전 Y:삭제 R:청구 C:완료(변환/입금) F:완료(실패) X:입금취소) */
AND MRR.REQ_DAY = #{req_day}
AND MRR.PAY_MTHD = #{pay_mthd}
AND MRR.AUTH_YN = #{auth_yn}
AND MWR.REQ_DAY >= '20210101' AND NVL(TO_CHAR(MRR.CANCEL_DT,'YYYYMMDD'),'99999999') = '99999999'
AND MRR.DEL_FG = 'N' ) MAIN_TBL
WHERE 1=1
AND CANCEL_GB = #{cancel_gb}
)
WHERE 1=1

SELECT /* ReqCustMap.getCustExceptionCount */
       COUNT(*)
  FROM LF_DMUSER.TB_BANKRUPTCY_MNG TBM INNER JOIN
  MEM_PROD_ACCNT MPA ON TBM.ACCNT_NO = MPA.ACCNT_NO AND MPA.DEL_FG = 'N' INNER JOIN
  MEMBER MB ON MPA.MEM_NO = MB.MEM_NO AND MB.DEL_FG = 'N' INNER JOIN
  PRODUCT PD ON MPA.PROD_CD = PD.PROD_CD 
WHERE 1=1 

AND TBM.ACCNT_NO = #{accnt_no}


       AND TBM.MEM_NM LIKE '%' || #{mem_nm} || '%'


AND TBM.BANKR_GBN = #{bankr_gbn}


AND TBM.DEL_FG = #{del_fg}




         AND  TBM.RECORD_DT >= #{stt_dt}
         AND  TBM.RECORD_DT <= #{end_dt}





         AND  TBM.CLOSE_DT >= #{stt_dt}
         AND  TBM.CLOSE_DT <= #{end_dt}

SELECT /* ReqCustMap.getCustExceptionList */ Z.* FROM (SELECT X.*,
ROW_NUMBER() OVER(ORDER BY ${orderBy} ${orderDirection}) PAGE_INDX
FROM ( SELECT SEQ, ACCNT_NO, KSTBIT, MEM_NM, PROD_CD, PROD_NM, PAY_TOT_AMT, REL_TOT_AMT, ADD_TOT_AMT, NVL((SELECT SUM(AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = TBL.PROD_CD AND NO <= TRUE_CNT),0) AS TRUE_AMT, NVL((SELECT SUM(REL_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = TBL.PROD_CD AND NO <= REL_CNT),0) AS REL_AMT, NVL((SELECT SUM(ADD_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = TBL.PROD_CD AND NO <= ADD_CNT),0) AS ADD_AMT, BANKR_GBN, CASE WHEN BANKR_GBN = 'B' THEN '파산' WHEN BANKR_GBN = 'R' THEN '회생' ELSE BANKR_GBN END AS BANKR_GBN_NM, JURISDICTION, CASE_NO_1, CASE_NO_2, RECORD_DT, CASE_CONTENT, CASE_NO_3, CASE_NO_4, CLOSE_DT, RESN_PROC_YN, REPAY_AMT, ( NVL((SELECT SUM(REL_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = TBL.PROD_CD AND NO <= REL_CNT),0) + NVL((SELECT SUM(ADD_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = TBL.PROD_CD AND NO <= ADD_CNT),0) + NVL((SELECT SUM(AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = TBL.PROD_CD AND NO <= TRUE_CNT),0) + REPAY_AMT ) - (REL_TOT_AMT + ADD_TOT_AMT) AS DIFF_AMT, REG_DM, REG_MAN FROM ( SELECT TBM.SEQ, TBM.ACCNT_NO, CASE WHEN MPA.KSTBIT = '01' THEN '대기' WHEN MPA.KSTBIT = '02' THEN '가입' WHEN MPA.KSTBIT = '03' THEN '해약' WHEN MPA.KSTBIT = '04' THEN '행사' WHEN MPA.KSTBIT = '09' THEN '만기' ELSE MPA.KSTBIT END KSTBIT, TBM.BANKR_GBN, TBM.JURISDICTION, TBM.CASE_NO_1, TBM.CASE_NO_2, TBM.RECORD_DT, TBM.CLOSE_DT, TBM.CASE_CONTENT, TBM.CASE_NO_3, TBM.CASE_NO_4, TBM.MEM_NM, MPA.PROD_CD, PD.PROD_NM, NVL(RS.RESN_PROC_YN,'N') AS RESN_PROC_YN, (SELECT SUM(AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD) AS PAY_TOT_AMT, (SELECT SUM(REL_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD) AS REL_TOT_AMT, (SELECT SUM(ADD_AMT) FROM PRODUCT_NO_DATA WHERE PROD_CD = MPA.PROD_CD) AS ADD_TOT_AMT, CASE WHEN KSTBIT = '03' AND NVL(RS.RESN_PROC_YN,'N') = 'Y' THEN 0 ELSE (SELECT COUNT(*) FROM PAY_MNG WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N') END AS TRUE_CNT, (SELECT COUNT(*) FROM PAY_MNG_DTL WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N') AS REL_CNT, (SELECT COUNT(*) FROM PAY_MNG_DTL1 WHERE ACCNT_NO = MPA.ACCNT_NO AND DEL_FG = 'N') AS ADD_CNT, NVL(( SELECT SUM(NVL(AMT * (END_NO - STT_NO + 1),0)) FROM LF_DMUSER.TB_BANKRUPTCY_SETUP WHERE MST_SEQ = TBM.SEQ ),0) AS REPAY_AMT, TO_CHAR(TBM.REG_DM, 'YYYYMMDD') AS REG_DM, (SELECT USER_NM FROM PS_WILLVI.TB_USER WHERE USER_ID = TBM.REG_MAN) AS REG_MAN FROM LF_DMUSER.TB_BANKRUPTCY_MNG TBM INNER JOIN MEM_PROD_ACCNT MPA ON TBM.ACCNT_NO = MPA.ACCNT_NO AND MPA.DEL_FG = 'N' INNER JOIN MEMBER MB ON MPA.MEM_NO = MB.MEM_NO AND MB.DEL_FG = 'N' INNER JOIN PRODUCT PD ON MPA.PROD_CD = PD.PROD_CD LEFT OUTER JOIN RESCISSION RS ON MPA.ACCNT_NO = RS.ACCNT_NO AND RS.DEL_FG = 'N' WHERE 1=1
AND TBM.ACCNT_NO = #{accnt_no}
AND TBM.MEM_NM LIKE '%' || #{mem_nm} || '%'
AND TBM.BANKR_GBN = #{bankr_gbn}
AND TBM.DEL_FG = #{del_fg}
AND TBM.RECORD_DT >= #{stt_dt} AND TBM.RECORD_DT <= #{end_dt}
AND TBM.CLOSE_DT >= #{stt_dt} AND TBM.CLOSE_DT <= #{end_dt}
) TBL ) X ) Z
WHERE PAGE_INDX >= #{startLine} AND PAGE_INDX < #{endLine}

mapper.update:
UPDATE TB_BANKRUPTCY_MNG
   SET BANKR_GBN = #{bankr_gbn}
     , JURISDICTION = #{jurisdiction}
     , CASE_NO_1 = #{case_no_1}
     , CASE_NO_2 = #{case_no_2}
     , RECORD_DT = #{record_dt}
     , CASE_CONTENT = #{case_content}
     , CASE_NO_3 = #{case_no_3}
     , CASE_NO_4 = #{case_no_4}
     , MEM_NM = #{mem_nm}
     , CLOSE_DT = #{close_dt}
 WHERE 1=1
   AND SEQ = ${seq} 
   AND DEL_FG = 'N'


		/* ReqCustMap.updateBankRup */
        UPDATE TB_BANKRUPTCY_SETUP         
           SET  STT_NO=#{stt_no}, END_NO=#{end_no}, AMT=#{amt}, REG_MAN=#{reg_man}, REG_DM=SYSDATE
        WHERE 1=1
           AND SEQ = ${seq}
           AND DEL_FG = 'N'

		/* ReqCustMap.deleteBankRup */
        UPDATE TB_BANKRUPTCY_SETUP
           SET DEL_FG = 'Y',REG_MAN=#{reg_man}, REG_DM=SYSDATE
         WHERE 1=1
           AND SEQ = ${seq}
           AND DEL_FG = 'N'

mapper.insert:
BEGIN
INSERT INTO TB_MEMBER_WDRW_REQ ( ACCNT_NO, MEM_NO, PROD_CD, PAY_MTHD, REQ_BIT, STAT, KSTBIT, REQ_PAY_NO, ICHAE_NO, ICHAE_CD, REQ_DAY, PAY_AMT, REG_DM, REG_MAN, DEL_FG, RESULT_KEY, RESULT_KIND, INI_YN ) VALUES ( #{accnt_no}, #{mem_no}, #{prod_cd}, '06', '03', #{stat}, #{kstbit}, 999, SUBSTR(#{card_no},0,4) || '-****-****[' || #{exp_year} || #{exp_mon} , #{card_cd}, TO_CHAR(SYSDATE,'YYYYMMDD'), #{pay_amt}, SYSDATE, #{emple_no}, #{del_fg}, #{result_key}, #{pay_kind}, 'Y' );
INSERT /* ReqCustMap.insertNewTypeFreeCardResult */ INTO TB_MEMBER_WDRW_RESULT ( ACCNT_NO, REQ_DAY, REQ_NO, PAY_KIND, RESULT_KEY, RESULT_CD, RESULT_MSG, PAY_MTHD, AUTH_DT, AUTH_CD, ICHAE_CD, ICHAE_NO, ETC, REG_DM, REG_MAN, TID ) VALUES ( #{accnt_no}, TO_CHAR(SYSDATE,'YYYYMMDD'), #{pay_no}, #{pay_kind}, #{result_key}, #{result_cd}, #{result_msg}, #{pay_mthd}, #{auth_dt}, #{auth_cd}, #{card_cd}, SUBSTR(#{card_no},0,4) || '-****-****[' || #{exp_year} || #{exp_mon} , #{etc}, SYSDATE, #{emple_no}, #{result_key} ); END ;
